--
-- Table structure for table `pest_libraries`
--

CREATE TABLE `pest_libraries` (
  `id` bigint(20) UNSIGNED NOT NULL,
  `name_ar` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL,
  `name_en` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL,
  `image` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `meta_description_ar` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `image_alt` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `description_ar` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `description_en` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `sort` tinyint(1) NOT NULL,
  `is_active` tinyint(1) NOT NULL,
  `home_order` int(11) NOT NULL DEFAULT '0',
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  `slug_ar` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `slug_en` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `meta_title_ar` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `sub_pest` int(11) DEFAULT NULL,
  `keywords_ar` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `keywords_en` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `meta_title_en` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `meta_description_en` text COLLATE utf8mb4_unicode_ci NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Dumping data for table `pest_libraries`
--

INSERT INTO `pest_libraries` (`id`, `name_ar`, `name_en`, `image`, `meta_description_ar`, `image_alt`, `description_ar`, `description_en`, `sort`, `is_active`, `home_order`, `created_at`, `updated_at`, `slug_ar`, `slug_en`, `meta_title_ar`, `sub_pest`, `keywords_ar`, `keywords_en`, `meta_title_en`, `meta_description_en`) VALUES
(5, 'بق الفراش', 'Bed bugs', 'uploads/pest_libraries/1588918542b232a9e9485291ef05ef5af49a6721f4image.png', 'بق الفراش من أشهر الحشرات التي تتغذى على دم البشر، ولذلك فهي دائمة التواجد في المنازل، ولا يُعرف عنها أنها تنقل الأمراض، والاسم العلمي لحشرة بق الفراش هو  Cimex lectularius وفيما يلي سوف نتعرف على المزيد من المعلومات الهامة عن حشرة بق الفراش وعلى الرغم من أن البشر هو المضيف المفضل لبق الفراش، فهي تتغذى على العديد من الحيوانات ذوات الدم الحار بما في ذلك الفئران والكلاب والقطط والدواجن والطيور الأخرى. مثل الخفافيش.', 'Bed bugs', '<p dir=\"rtl\" style=\"text-align:justify\">بق الفراش من أشهر الحشرات التي تتغذى على دم البشر، ولذلك فهي دائمة التواجد في المنازل، ولا يُعرف عنها أنها تنقل الأمراض، والاسم العلمي لحشرة بق الفراش هو&nbsp; Cimex lectularius وفيما يلي سوف نتعرف على المزيد من المعلومات الهامة عن حشرة بق الفراش وعلى الرغم من أن البشر هو المضيف المفضل لبق الفراش، فهي تتغذى على العديد من الحيوانات ذوات الدم الحار بما في ذلك الفئران والكلاب والقطط والدواجن والطيور الأخرى. مثل الخفافيش.</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">&nbsp;ولا يوجد أي دليل علمي يٌظهر أن بق الفراش ينقل الأمراض، ولكن شاغلنا الرئيسي هو حول تأثير إصابتهم للمنازل على البشر وأسلوب حياتهم، فحشرة بق الفراش تدخل في جلد المضيف وتمتص الدماء، من خلال حقن اللعاب الذي تنتجه في الجلد والذي ينتج عنه رد فعل تحسسي غالبًا ما يسبب التورم، والحكة ، والتهيج الذي قد يستمر لمدة أسبوع أو أكثر، وقد يكون للإصابة الكبيرة بالبق رائحة قوية ملحوظة.</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\"><img alt=\"شكل حشرة بق الفراش\" src=\"https://orkidapest.com/orkida-test/uploads/contents/-بق-الفراش-1.jpg\" style=\"height:465px; width:800px\" /></p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>ما هي حشرة&nbsp; بق الفراش</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">بق الفراش حشرات ليلية وهي تخرج من أماكن الاختباء لتناول وجبة الدم، كما أنها حشرات انتهازية أيضا حيث يمكنها الحصول على وجبة دم خلال النهار، وخاصة في المناطق الموبوءة بشدة إذا كانت الفرصة متاحة لها، وتتطلب عملية التغذية عادة من 5 إلى 10 دقائق وبعد الانتهاء تنتقل إلى أماكن معزولة وتختبئ لمدة 5-10 أيام وتقوم بهضم وجبتها وتضع البيض، ولا يمكن للبق أن يطير أو يقفز، بل يقوم بالزحف لمسافات طويلة، وهو ينقل بين البشر والمنازل من خلال الأمتعة والغسيل ووسائل المواصلات، والأكياس الفارغة والأثاث المستعمل الذي يقوم البعض بشرائه، كما أن بق الفراش يمكن أن يشارك الحيوانات في منازلهم.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>المظهر الخارجي لحشرة بق الفراش</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">حشرة البق البالغة لها لون بني محمر وشكل بيضاوي ولا تملك أجنحة، ويصل طولها &nbsp; من 1/4 إلى 3/8 بوصة، وبعد الحصول على وجبتها من الدم ينتفخ جسمها ويصبح لها لون أحمر، أما البيض يكون صغير جدًا ويصل لحوالي 1/32 وتوجد في مجموعات من 10 إلى 50 حشرة، وتكون الحوريات عديمة اللون عندما تفقس لأول مرة وتشبه في الشكل الحشرات البالغة لكنها تكون أصغر منها، وفي منازلنا يمكننا العثور على جميع مراحل البق.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>دورة حياة حشرة بق الفراش</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">بعد التزاوج، تضع الإناث البيض البيضاوي والأبيض اللون والذي يصل طوله 1/16 في الشقوق، ويمكن أن تضع 200 إلى 250 بيضة في حياتها، ويفقس البيض في غضون 6 إلى 10 أيام، وتسعى الحوريات الناشئة حديثًا إلى الحصول على وجبة دم، وتغير الحوريات غير الناضجة جلدها حوالي خمس مرات من خلال التخلص من هيكلها الخارجي وذلك قبل بلوغها سن البلوغ، وقد يكون هناك ثلاثة أجيال أو أكثر في السنة ويتم العثور على جميع الأعمار في حالة الإصابة، وفي ظل الظروف العادية يعيش بق الفراش البالغ لحوالي 2 إلى 4 أشهر</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">ويحتاج بق الفراش إلى الحصول على الطعام مرة واحدة على الأقل قبل كل مرحلة، وعلى الرغم من أنها يمكن أن تتغذى مرة واحدة يوميًا يمكن أن تعيش الحوريات الصغيرة بدون وجبة دم لعدة أيام تصل إلى عدة أشهر، كما يمكن أن تعيش الحوريات والحشرات البالغة الأكبر سناً لفترة أطول بدون وجبة دم تصل إلى عام في ظل الظروف القاسية.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>علامات حشرة بق الفراش</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يمكنك أن تتوصل بخبير للمساعدة في تحديد نوع الحشرة المشتبه فيها، كما يمكنك أن تتعرف على حشرة بق الفراش البالغة من خلال المظهر الخارجي للحشرة والذي تحدثنا عنه منذ قليل، كما يمكنك البحث عن علامات بق الفراش في المنزل من خلال البحث عنها في أماكن الراحة والنوم، وذلك حول المراتب وإطارات السرير، والطيات والأثاث مثل المكاتب والكراسي ووراء ورق الحائط والساعات وفي شقوق الأرضيات الخشبية وتحت حواف السجاد، كما يتم العثور على بق الفراش في المدارس ومرافق البيع والمباني والمكاتب والمكتبات وغيرها من الأماكن العامة، ويمكن لأصحاب المنازل العثور على حشرات البق الحية، وكذلك بقايا الجلود التي تتركها حينما تنتقل من مرحلة لأخرى، كما يمكن العثور على بقع الدم على الملاءات والأغطية وكذلك البراز، ويسبب البق رائحة مميزة للغاية خاصة في حالة الإصابات الشديدة والتواجد في أعداد كبيرة.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>اماكن تواجد حشرة بق الفراش</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تتنقل حشرة بق الفراش من مكان لآخر وهي تختبئ في الحقائب والصناديق والأحذية لتكون بالقرب من مصدر الغذاء، وهي حشرات ليلية ماهرة في الاختباء، حيث يمكنها أن تختبئ خلف الألواح وفي الشقوق ومناطق الأسرة والوسادات والأثاث المجاور للفراش وفي أماكن النوم، ويمكنها أيضًا الإختفاء في لوحات المفاتيح الكهربائية أو إطارات الصور أو ورق الحائط أو في أي مكان تقريبًا داخل المنزل أو السيارة.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>اضرار حشرة بق الفراش على البشر</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يقوم بق الفراش بقرص البشر وهم نائمون في جميع أنحاء الجسم، وعادة تستهدف المناطق المكشوفة، وتختلف ردود الفعل التحسسية من شخص إلى آخر، فبعض الناس قد لا يعرفون حتى أنهم تعرضوا للعض، بينما يصاب البعض الآخر بتورمات مؤلمة وشعور قوي بالحكة، ولذلك إذا كنت مسافراً في مكان ما تحقق من حواف المرتبة قبل النوم للبحث عن نقاط الدم، وانظر أسفل حافة لوحة الرأس، ويمكنك البحث باستخدام مصباح فلاش، حيث يميل البق إلى الاختباء في المناطق غير المزعجة ولذلك لا تضع أبدًا حقائب على الأرض أو تحت السرير، وإذا وجدت البق في منزلك، فقم بفحص شامل للمنزل للتخلص من البق، ومن المهم معرفة بأكبر قدر ممكن من المعلومات عن الحشرة.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>كيفية التخلص من حشرة بق الفراش</strong></h2>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align:justify\">التخلص من الفوضى في المنزل قدر المستطاع حتى لا يجد البقاء أماكن للاختباء.</li>\r\n	<li style=\"text-align:justify\">سد الفجوات والشقوق التي يختبئ بها البق.</li>\r\n	<li style=\"text-align:justify\">المعالجة الحرارية للعناصر المصابة بالبق وذلك من خلال تعريضها لأشعة الشمس القوية أو المجفف الحراري.</li>\r\n	<li style=\"text-align:justify\">وضع العناصر المصابة في المبرد لقتلها.</li>\r\n	<li style=\"text-align:justify\">عند السفر، لا تقم بفك حقيبتك في غرفة الفندق ولا تستخدم خزانة ملابس الفندق، بل اترك ملابسك في حقيبة السفر الخاصة بك حتى تكون مستعدًا لارتدائها وقم بتخزين حقيبة السفر الخاصة بك في أعلى الخزانة قدر الإمكان.</li>\r\n	<li style=\"text-align:justify\">تفقد غرفتك بعناية قبل إحضار أمتعتك إلى الداخل وإذا رأيت أي علامات على بق الفراش، فاطلب غرفة جديدة.</li>\r\n	<li style=\"text-align:justify\">عند العودة إلى المنزل، اغسل جميع ملابسك فورًا، سواء ارتدتها أم لا، وجففها على نار عالية فالحرارة سوف تقتل بق الفراش</li>\r\n	<li style=\"text-align:justify\">لا تقم بشراء المراتب والاثاث المستخدم من الخارج.</li>\r\n</ul>', '<p dir=\"ltr\" style=\"text-align:justify\">Bed bugs are among the most common insects in the world, that feed on human blood. Therefore, they always present in human houses, they are not known that they may transmit diseases to humans, bed bug scientific name is has the scientific called&quot; Cimex lectularius</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">The bed bug insects move from one place to another while hiding in bags, boxes and shoes to be near from the source of food which is human blood, and they are nocturnal insects, very clever at hiding, where they can hide behind the boards and in the cracks, family areas, pillows, furniture adjacent to the bed and in the sleeping places, and they can also hide in the Electrical keyboards, photo frames, wallpaper, or almost anywhere in your home, office or car.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>BED BUGS</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">During night, bed bugs come out from their hiding places to eat their daily blood meal, bed bugs also opportunistic insects where they can get a blood meal during the daylight, especially in severely affected areas if the opportunity is available to them, and the feeding process usually requires from 5 to 10 minutes, after completion feeding, they come back to their isolated places, they can hide for 5-10 days, digests their meal, and lay eggs.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Bed Bug Appearance</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">The adult bug bugs have a reddish-brown color and they are oval in shape, they also don&#39;t have wings. their length ranges from 1/4 to 3/8 inches. After getting their meal from human blood, their body begins to swells and becomes red in color, while the eggs are very small and they reach about 1/32 inches, they always found in Groups from 10 up to 50 insects, and nymphs are colorless. when female bed bug hatches, they resemble adult bugs, but they are smaller than them, and in our home infestation we can find all stages of bugs.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>life cycle of bedbugs</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">After mating, females lay an oval and white eggs, their lenght is&nbsp; about 1/16 mm, they hide them in cracks, female bed bug can lay 200 to 250 eggs during their lifetime, and eggs hatch within 6 to 10 days, the newly emerging nymphs seek a blood meal, and nymphs change Mature skin about five times by getting rid of its outer structure, before reaching puberty, There may be three generations or more per year,all ages are found in the event of injury, and under normal circumstances adult bed bugs live for about 2 to 4 months</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Bed bugs need to get food at least once in each stage, and although they can be fed once a day, small nymphs can live without a blood meal for several days up to several months, nymphs and older adults can live for a while The longest without a blood meal up to a year under hard conditions.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>How to recognize bed bugs?</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">You can contact an expert to help you to determine the type of the suspected insect. You can also learn about the adult bed bugs through their external appearance bug that we mentioned a while ago. You can also search for signs of bed bugs at your home by searching for them in places of rest and sleep, you can search in mattresses and bed frames, folds and furniture such as beds and chairs, behind wallpaper and clocks, in cracks of wooden floors and under the edges of carpets. Bed bugs are also found in schools, offices, buildings, libraries and other public places, homeowners can find bed bugs itself, their outer layers that they leave when transforming from one stage to another, you can also find blood signs on sheets and covers as well as&nbsp; their dotted stools, bed bugs also have a very distinctive odor especially in the case of severe infestations.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Places where bed bugs hide</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Bed bugs move from one place to another while hiding in bags, boxes and shoes to be near the source of the food, they are night creatures and can hide in cracks, where they can hide behind the boards, family areas, pillows, furniture adjacent to the bed and in the sleeping places, and they can also hide in the keyboards Electrical, photo frames, wallpaper, or almost anywhere in your home or car.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Dangers Of Bed Bugs On Humans.</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Bed bugs can bite human beings while sleeping all over the body, usually targeting open areas, they cause allergic reactions to human, it differs from one person to another, some people may not even know that they have been bitten, while others develop a painful swelling and a strong feeling of itching sensation, so if you are traveling Somewhere check the edges of the mattress and bed, looking for blood points, and look below the edge of the headboard, you can search using a flashlight, as bugs tend to hide in unseen areas, remember also not to put bags on the floor or under the bed, and if you find bugs in Your home, do a complete home examination to get rid of bedbugs, and it is important Knowing as much information as possible about the insect.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>How to get rid of bed bugs</strong></h2>\r\n\r\n<ul>\r\n	<li dir=\"ltr\" style=\"text-align:justify\">Get rid of clutter at home as much as possible so, the bed bugs can not find places to hide.</li>\r\n	<li dir=\"ltr\" style=\"text-align:justify\">&bull; Fill gaps and cracks in your house.</li>\r\n	<li dir=\"ltr\" style=\"text-align:justify\">Use Heat treatment to get rid of bed bugs by exposing them to strong sunlight or a heat dryer.</li>\r\n	<li dir=\"ltr\" style=\"text-align:justify\">&bull; Place the infected items in a coolant to kill them.</li>\r\n	<li dir=\"ltr\" style=\"text-align:justify\">&bull; When traveling, do not disassemble your bag in the hotel room and do not use the hotel&#39;s wardrobe, but leave your clothes in your travel bag until you are ready to wear it and store your travel bag as high as possible.</li>\r\n	<li dir=\"ltr\" style=\"text-align:justify\">&bull; Check your room carefully before bringing your luggage indoors and if you see any signs of bed bugs, request a new room.</li>\r\n	<li dir=\"ltr\" style=\"text-align:justify\">&bull; When returning home, wash all your clothes immediately, whether you wear them or not, and dry them over a high heat as heat will kill bed bugs</li>\r\n	<li dir=\"ltr\" style=\"text-align:justify\">Do not purchase mattresses and used furniture from abroad</li>\r\n	<li dir=\"rtl\" style=\"text-align:justify\">&nbsp;</li>\r\n</ul>', 3, 1, 0, '2020-03-04 01:47:03', '2020-05-08 06:03:21', 'بق-الفراش', 'Bed-bugs', 'حشرة بق الفراش : معلومات وحقائق وكيفية التخلص من حشرة بق الفراش', NULL, 'حشرة بق الفراش', 'Bed bugs', 'Bed bugs | Facts & Identification, Control & Prevention', 'Bed bugs are among the most famous insects that feed on human blood, and therefore they are always present in homes, and they are not known to transmit diseases. The scientific name of bed bugs is Cimex lectularius. Below we will learn more important information about bed bugs and although Humans are the favorite host of bed bugs, they feed on many warm-blooded animals including mice, dogs, cats, poultry, and other birds. Like a bat.'),
(6, 'السمك الفضي', 'Silverfish', 'uploads/pest_libraries/1588919797dae2b99a792d5603de3b13b009479ed6image.png', 'السمك الفضي أو ال silver fish من الحشرات الصغيرة الشائعة حول العالم، والتي يكون من الصعب جدًا العثور عليها أو السيطرة عليها، فهي حشرة ليلية، مما يعني أنه من غير المحتمل أن تراها أثناء النهار، كما أن حجمها الصغير يجعلها بارعة في الاختباء، وغالبا ما يكون أصحاب المنازل الذين يعانون من غزو السمك الفضي بحاجة للمساعدة المهنية، فهي تميل إلى إخفاء وجودها عن البشر مما يعني أن أي ضرر تسببه الحشرة قد لا يلاحظه أحد أيضًا بشكل سريع، وتقوم حشرة السمك الفضي بتناول أي شيء يحتوي على النشا أو السكريات بما في ذلك الورق والصور وورق الجدران وورق الكتاب, ', 'السمك الفضي', '<p dir=\"rtl\" style=\"text-align:justify\">السمك الفضي أو ال silver fish من الحشرات الصغيرة الشائعة حول العالم، والتي يكون من الصعب جدًا العثور عليها أو السيطرة عليها، فهي حشرة ليلية، مما يعني أنه من غير المحتمل أن تراها أثناء النهار، كما أن حجمها الصغير يجعلها بارعة في الاختباء، وغالبا ما يكون أصحاب المنازل الذين يعانون من غزو السمك الفضي بحاجة للمساعدة المهنية، فهي تميل إلى إخفاء وجودها عن البشر مما يعني أن أي ضرر تسببه الحشرة قد لا يلاحظه أحد أيضًا بشكل سريع، وتقوم حشرة السمك الفضي بتناول أي شيء يحتوي على النشا أو السكريات بما في ذلك الورق والصور وورق الجدران وورق الكتاب,&nbsp;</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">وتتطلب الأسماك الفضية مستويات عالية من الرطوبة في الأماكن التي تعيش بها ولذلك على الأرجح تجد طريقها إلى منزلك بحثًا عن الرطوبة والطعام، وهي عادة ما تغزو الطوابق السفلية والحمامات والمطابخ وغيرها من المناطق ذات الرطوبة العالية، وسوف نتعرف على شكل الحشرة ودورة حياتها وعلامات الإصابة بها وكيفية التخلص منها من خلال هذا المقال.</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\"><img alt=\"\" src=\"https://orkidapest.com/orkida-test/uploads/contents/-الفضي-.jpg\" style=\"height:459px; width:800px\" /></p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>حشرة السمك الفضي</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تحصل حشرة السمك على اسماه بسبب لون الحشرة الفضي ومظهرها المعدني وشكلها وحركاتها الشبيهة بالسمك، وهي تٌعرف أيضًا باسم &quot;الشعيرات الخيطية&quot; بسبب الزوائد الثلاثة الطويلة الشبيهة بالشعر الخشن أو الذيل في الطرف الخلفي من جسمها، وهي من الحشرات الشائعة في المنازل خاصة في الأماكن المظلمة والرطبة والبعيدة عن متناول البشر, ولا شك أنها مثل جميع الحشرات تسبب الكثير من الإزعاج لأصحاب المنازل ولهذا السبب يبحث من يعاني من الإصابة بها عن طرق فعالة للتخلص منها.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>&nbsp;المظهر الخارجي للسمك الفضي</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يكون للسمك الفضي أجسام مسطحة يتراوح طولها بين 1/2 و 1 بوصة، كما أن لديها 3 شعيرات تبرز من الجزء الخلفي من البطن، وهي لا تملك أجنحة، لكنها قادرة على الجري بسرعة كبيرة، حيث يبلغ طول جسم حشرة السمك الفضي البالغة حوالي &frac12;-3/4 &quot;(12-19 مم) وهذا لا يشمل طول الذيل، وهي تملك جسم مسطح غالبًا ما يتم مقارنة شكله بالدمعة أو السمك فهو مستدقً من الرأس إلى الخلف، وعادة ما يكون لها لون فضي اللون أو لون المسدس المعدني وله هوائيات طويلة تشبه الخيوط. بالإضافة إلى ذلك، لديها عيون مركبة صغيرة ومفصولة وتبدو الأسماك الفضية غير الناضجة مشابهة للحشرات البالغة باستثناء الحجم وتظهر قشورها مع الجيل الثالث أو الرابع.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>دورة حياة حشرة السمك الفضي</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">على عكس معظم الحشرات، فإن السمك الفضي يمر بثلاث مراحل فقط خلال دورة حياتها، حيث يمكن أن تضع الأنثى البيض في أي وقت من العام، وعادة ما تستغرق من ثلاثة إلى أربعة أشهر حتى تنمو من البيضة حتى تصل في النهاية&nbsp; إلى مرحلة البلوغ.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>الأطعمة التي يتناولها السمك الفضي</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">على الرغم من أن السمك الفضي يمكن أن يعيش في الواقع عدة أشهر دون أن يأكل أي شيء على الإطلاق ، إلا أنه يأكل أشياء كثيرة مثل الكربوهيدرات والسكريات والنشويات، وهم يحبون أيضًا السليلوز الموجود في المنتجات الخشبية، ولهذا السبب يأكلون الكتب والصور القديمة والسجاد والملابس والجلود والستائر والأغطية والبطانيات والمنسوجات والمستندات واللصق على ورق الحائط، ومن المعروف أيضًا أنها تتناول الشامبو والقشرة والحشرات الميتة والسكر والقهوة والمعكرونة والدقيق والحبوب وغيرها من الأطعمة النشوية، ويمكن أن تتناول قشرة الرأس وخيوط فرشاة الشعر.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>أماكن اختباء السمك الفضي</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يمكن للسمك الفضي البقاء على قيد الحياة في العديد من المناخات، لكنها تفضل العيش داخل المناطق المظلمة والرطبة مثل غرف الغسيل والأقبية والمطابخ والحمامات. وهي تنجذب بشكل خاص إلى المناطق التي تحتوي على أقمشة رطبة، أما خارج المنازل فيمكن العثور عليها داخل العناصر المخزنة أو داخل الصناديق أو في المرائب أو داخل الحظائر وغيرها من المباني الخارجية.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>اضرار حشرة السمك الفضي&nbsp;</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\"><span style=\"font-size:13px\">لا يُعرف عن حشرة السمك الفضية تسببها في نقل الأمراض إلى منازل البشر، غير أنها تتسبب في تلف الكثير من العناصر في المنزل مثل الكتب الورق والمستندات والمجلات، وبذلك يمكن أن تكون مصدر إزعاج لأنها تلحق الضرر بالعناصر التي تغزوها وتتغذى عليها.</span></p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>كيف تدخل حشرة السمك الفضي ؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">&nbsp;إذا كنت ترى السمك الفضي داخل منزلك، فمن المحتمل أنه نشأ من التربة المحيطة بمنزلك، فإذا كان الجو جافًا للغاية في الخارج، فسوف تبحث السمكة الفضية عن بيئة أكثر رطوبة، وإذا كانت لديك فجوات وشقو فمن المحتمل أن تدخل المنزل من خلالها، وعلى الرغم من أنهم يفضلون البيئات الرطبة، لا يمكنهم العيش إذا كان هناك الكثير من الماء في التربة.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>ما هي علامات غزو السمك الفضي للمنزل؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يمكن رؤية ثقوب أو شقوق أو علامات حفر على طول الأسطح التي يتغذى عليها حشرة السمك الفضي في حالة الإصابة، وفي الحالات الخطيرة، يمكن رؤية علامات صغيرة من البراز على الأسطح وهي عبارة عن حبيبات صغيرة تشبه الفلفل الأسود، كما يمكن رؤية السمك الفضي في السقف أو الأرضيات في الأماكن الرطبة.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>كيفية التخلص من حشرة السمك الفضي؟</strong></h2>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align:justify\">فحص مناطق الموائل المفضلة بدقة وفي حالة وجود المواد الغذائية المناسبة للسمك الفضل فلا بد من التخلص منها مثل الكتب القديمة والورق وغيرها من العناصر.</li>\r\n	<li style=\"text-align:justify\">إذا كانت إصابة منزلك موضعية فيجب تنظيف الأماكن التي ترى بها السمك الفضي جيدا وتطهيرها.</li>\r\n	<li style=\"text-align:justify\">&nbsp;إذا كانت الإصابة واسعة الانتشار في الخارج فيجب نقل أو إزالة أي شيء مخزّن على السطح الخارجي للمنزل أو بالقرب منه نظرًا لأن السمك الفضي يمكنه بسهولة تسلق الجدران والعثور على مدخل حول إطارات النوافذ والأبواب وأنابيب المرافق وفتحات التهوية.</li>\r\n	<li style=\"text-align:justify\">ينبغي تنظيف جميع الأسطح المؤدية للمنزل وسد الفجوات والشقوق التي تدخل منها.</li>\r\n	<li style=\"text-align:justify\">الحصول على مزيل الرطوبة لمنزلك، وإصلاح الأنابيب والصرف الصحي المتسرب وإصلاح أي خشب متعفن.</li>\r\n	<li style=\"text-align:justify\">&nbsp;لا تحتفظ بالكتب والمجلات القديمة في المناطق التي توجد فيها حشرة الأسماك الفضية مثل الطوابق السفلية والسندرات والجراجات.</li>\r\n	<li style=\"text-align:justify\">&nbsp;الاحتفاظ بالمواد الغذائية مثل الدقيق والسكر في حاويات محكمة الإغلاق.</li>\r\n	<li style=\"text-align:justify\">في حالة الاشتباه في وجود إصابة بالسمك الفضي فمن الأفضل استدعاء خبير من شركة مكافحة الحشرات بجدة لتفتيش المنزل بشكل صحيح والتوصية بطريقة العلاج المناسبة</li>\r\n</ul>', '<p dir=\"rtl\" style=\"text-align:left\">Silver fish is a very common insect around the world, it is very difficult to find or control, it is a nocturnal insect, which means that it is not likely to be seen during the day, and their small size makes them clever at hiding, so often Homeowners suffering from the invasion of silver fish are in need for professional help, as they tend to hide their presence from humans which means that any damage caused by this insect may not be noticed quickly either, silver fish insects eat anything containing starch or sugars including Paper, photo, wallpaper, book paper,</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:left\">Silver fish requires high levels of humidity in the places where it lives and therefore most they most likely can find their way to your home to search for moisture and food, they usually invade the lower floors, bathrooms, kitchens and other areas of high humidity, and we will learn about the shape of silverfish, its life cycle, signs of infection and how we can Get rid of it through this article.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:left\"><strong>What is a silver&nbsp;fish insect</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:left\">silver fish gets its name because of its silver color, it&#39;s metallic appearance, it has the shape and movements of a fish, it is also known as &quot;filamentous filaments&quot; due to the three long appendages that similar to coarse hair or tail at the back end of its body, is a common insect in home especially in dark and humid places. It lives away from the reach of humans, there is no doubt that they, like all insects, cause a lot of inconvenience to homeowners, and for this reason, those who suffer from infection are looking for effective ways to get rid of them.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:left\"><strong>&nbsp;silverfish appearance&nbsp;</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:left\">Silverfish have flat bodies between 1/2 and 1 inches in length. They also have 3 hairs protruding from the back of the abdomen, and they do not have wings, but they are able to run very quickly, as the length of the body of the silver fish is about &frac12;- 3/4 &quot;(12-19 mm) This does not include the length of the tail. It has a flat body whose shape is often compared to a tear or a fish. It is drawn from the head to the back, usually it has a silver color or a metal gun color, and has long antennae, separated compound eyes and immature silver fish look similar to adult insects except for size.&nbsp;</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:left\"><strong>Silverfish insect life cycle</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:left\">Unlike most insects, silverfish go through only three stages during their life cycle, as a female can lay eggs at any time of the year, and it usually takes three to four months for them to grow from an egg until they eventually reach adulthood.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:left\"><strong>Food sources for silverfish</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:left\">Although silverfish can actually live for several months without eating anything at all, it eats many things like carbohydrates, sugars and starches, and they also love cellulose found in wood products, which is why they eat books, old photos, carpets, clothes, leather, and curtains Sheets, blankets, blankets, textiles, documents, and wallpaper paste are also known to eat shampoo, dandruff, dead insects, sugar, coffee, pasta, flour, grains and other starchy foods, and can eat dandruff and hair brush strings.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:left\"><strong>Where does silverfish hide?</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:left\">Silverfish can survive in many climates, but it prefers to live in dark and humid areas such as wash rooms, basements, kitchens, and bathrooms. They are particularly attracted to areas that contain wet fabrics, either outside the homes they can be found inside stored items, inside boxes, garages, or inside barns and other outdoor buildings.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:left\"><strong>Damage caused by silver fish&nbsp;</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:left\">It is not known about the silverfish that it may cause or transmit diseases to human, except that it causes damage to many elements in the home such as books, documents, and magazines, and thus can be a nuisance because it harms the elements that they invade and feed on.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:left\"><strong>How does silver fish enter the house?</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:left\">&nbsp;If you see silver fish in your home, it probably originated from the soil surrounding your home, and if the weather is very dry outside, the silver fish will search for a more humid environment, and if you have gaps and fissures you will likely enter the house through them, though They prefer moist environments, they cannot live if there is a lot of water in the soil.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:left\"><strong>What are the signs of silverfish invasion?</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:left\">Perforations, cracks, or pits can be seen along the surfaces on which the silverfish insects feed in the event of injury, and in severe cases, small signs of stools can be seen on the surfaces which are small granules that resemble black pepper, as the silver fish can be seen on the ceiling or Flooring in wet places.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:left\"><strong>How to get rid of silverfish insect?</strong></h2>\r\n\r\n<ul dir=\"ltr\">\r\n	<li>&bull; Examine the areas that silverfish prefer carefully and in the case of presence of food suitable for them, it must be dispose suchlike old books, paper and other elements.</li>\r\n	<li>&bull; If the injury to your home is localized, the places where you see the silver fish must be well cleaned and disinfected.</li>\r\n	<li>&bull; If the injury is widespread outside, anything stored on or near the exterior of the house should be moved or removed because silverfish can easily climb over the walls and find an entrance around window frames, doors, utility tubes and ventilation openings.</li>\r\n	<li>&bull; All surfaces leading to the house should be cleaned and the gaps and cracks in them closed.</li>\r\n	<li>&bull; Get a dehumidifier for your home, repair pipelines, leaked sewage and fix any moldy wood.</li>\r\n	<li>&bull;&nbsp; Do not keep old books and magazines in areas where the insect has silver fish, such as basements and garages.</li>\r\n	<li>Keep nutrients like flour and sugar in tight containers.</li>\r\n	<li>&bull; In case of a suspected silver mouth infection</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>', 6, 1, 0, '2020-03-05 18:38:38', '2020-05-08 06:03:40', 'السمك-الفضي', 'silver-fish', 'حشرة السمك الفضي : معلومات وحقائق وكيفية التخلص من السمكة الفضية', NULL, 'السمك الفضي', 'Silverfish', 'Silverfish | Facts & Identification, Control & Prevention', 'Silver fish is a very common insect around the world, it is very difficult to find or control, it is a nocturnal insect, which means that it is not likely to be seen during the day, and their small size makes them clever at hiding, so often Homeowners suffering from the invasion of silver fish are in need for professional help, as they tend to hide their presence from humans which means that any damage caused by this insect may not be noticed quickly either, silver fish insects eat anything containing starch or sugars including Paper, photo, wallpaper, book paper,'),
(7, 'النمل الابيض', 'Termite', 'uploads/pest_libraries/1589097363Termite.jpg', 'هل لديك مشاكل مع النمل الأبيض ( Termites )  في منزلك؟ فقد لا يعلم الكثير من مالكي المنازل أن هذه الحشرات تتربص بأساسات المنزل لأن النمل الأبيض يعيش في الغالب تحت الأرض وفي جدران منزلك، ويٌسمى النمل الأبيض بالحشرات المدمرة الصامتة لأنه يختبئ في أماكن صعب العثور عليها، فهي تعيش سراً في الطابق السفلي أو الأسقف العلوية دون ظهور أي علامات فورية على حدوث أضرار للمنازل،', 'Termite', '<p dir=\"rtl\" style=\"text-align:justify\">هل لديك مشاكل مع النمل الأبيض ( Termites )&nbsp; في منزلك؟ فقد لا يعلم الكثير من مالكي المنازل أن هذه الحشرات تتربص بأساسات المنزل لأن النمل الأبيض يعيش في الغالب تحت الأرض وفي جدران منزلك، ويٌسمى النمل الأبيض بالحشرات المدمرة الصامتة لأنه يختبئ في أماكن صعب العثور عليها، فهي تعيش سراً في الطابق السفلي أو الأسقف العلوية دون ظهور أي علامات فورية على حدوث أضرار للمنازل،</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">ويزدهر النمل الأبيض في مناخات مختلفة ويأكل أنواعًا مختلفة من الطعام وفقاً لنوعه، حيث تتطلب جميع أنواع النمل الأبيض أربعة أشياء للبقاء على قيد الحياة وهي الطعام والرطوبة والمأوى ودرجة الحرارة المثلى، ولسوء الحظ، يمكن لجميع المنازل أن توفر هذه الظروف المثالية مما يسبب الإصابة بالنمل الأبيض، ونظرًا لأنه يصعب تشخيص الإصابة، فإن النمل الأبيض يتسبب في أضرار تصل إلى 5 مليارات دولار كل عام، وإذا كنت تتساءل عن كيفية التعرف على النمل الأبيض أو كيفية حماية منزلك من هؤلاء الغزاة فقد يكون عليك قراءة هذا المقال.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>المظهر الخارجي للنمل الأبيض</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">حشرة النمل الابيض الاسم العلمي ( Isoptera ) يتراوح طول النمل الأبيض عادة ما بين 1/4 و 1/2 من بوصة طويلة، وتملك الحشرة أجسام لينة مع هوائيات مستقيمة، وتكون الملكات هي الأكبر حجماً، ويمكنهم الوصول إلى أكثر من بوصة واحدة طولاً، ويتراوح لون النمل الأبيض من الأبيض إلى البني الفاتح، وغالبًا ما يظهر لو النمل الأبيض العامل بدرجات أخف، ويكون النمل الأبيض الطائر أغمق ويمكنه التكاثر ويكون له زوجان من الأجنحة البارزة.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>مستعمرة النمل الأبيض</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يعيش النمل الأبيض في مستعمرات يقوم بتطويرها بشكل مستمر، وهي تتشابه بشكل كبير مع مستعمرات النمل والنحل، وهناك تسلسل هرمي صارم بين الحشرات وهم العمال، والجنود، والذكور، ويوجد في قلب المستعمرة ملكة النمل الأبيض المسؤولة عن إنتاج البيض والتي يمكن أن تضع أكثر من ألف بيضة في يوم واحد، وهناك أنواع من النمل الأبيض في أفريقيا تضع بيضة كل 2-3 ثواني، وبهذا المعدل قد يصل إلى 30-35 ألف بيضة في اليوم، ويصل حجم الملكة إلى 10 سم، وهو ما يعادل عدة أضعاف لحجم النمل الأبيض العادي، ولهذا لا يمكنها التحرك بنفسها، وإذا لزم الأمر يتم نقلها إلى موقع جديد من قبل عشرات النمل الأبيض العامل</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>لماذا يتم اكتشاف الإصابة بالنمل الأبيض في الربيع عادةً؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">عادةً ما تظهر أعداد كبيرة من النمل الأبيض المجنح والمعروفة باسم &quot;السرب&quot; داخل المنازل في فصل الربيع، ويقوم سرب النمل الأبيض بالتفرق لبدء مستعمرات جديدة، وبمجرد ظهور النمل الأبيض المجنح من المستعمرة يطير في الهواء بسبب ارتفاع درجات الحرارة وهطول الأمطار، ثم تسقط الأسراب على الأرض، وتتخلى عن أجنحتها، وتتزاوج مع رفيقة، وتحاول أن تبدأ مستعمرات جديدة في التربة، وتكون أسراب النمل الأبيض الناشئة داخل المنزل غير قادرة على إتلاف الأخشاب وتعيش فقط لمدة يوم تقريبًا، ويمكن التخلص باستخدام مكنسة كهربائية فور العثور عليها.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>كيف أعرف إذا كان منزلي مصابًا بالنمل الأبيض؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">اكتشاف النمل الأبيض المجنح في الداخل يشير دائمًا إلى وجود إصابة بالنمل الأبيض، ونظرًا لأنه ينجذب نحو الضوء، فغالبًا ما يتم رؤيتهم حول النوافذ والأبواب، وغالبًا ما يخلط الناس بين النمل الأبيض المجنح والنمل العادي الذي يوجد في نفس الوقت من العام، فالنمل الأبيض له هوائيات مستقيمة وخصر سميك موحد وأجنحة متساوية في الحجم، وعلى العكس من ذلك يمتلك النمل هوائيات، وخصور ضيقة، وأجنحة أمامية تكون أطول من الأجنحة الخلفية</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>أماكن تواجد النمل الأبيض</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يكون رؤية أسراب النمل الأبيض في الخارج بالقرب من جذوع الأشجار، والخشب، وما إلى ذلك في الفناء وقد لا يعني ذلك أن المنزل مصاب بالنمل الأبيض، ومن ناحية أخرى، إذا ظهرت أسراب النمل الأبيض بجوار المنازل والشرفات مما يعني أن هناك فرصة كبيرة لأن ينتشر المنزل أيضًا.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>علامات الإصابة بالنمل الأبيض؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">&nbsp;من أهم علامات الإصابة بالنمل الأبيض هي رؤية أسراب النمل الأبيض في أوقات التكاثر, كما يمكن رؤية أنابيب الطين الترابية الممتدة فوق جدران الأساس، والأرصفة والألواح والعتبات والأرضيات الخشبية وما إلى ذلك وعادة ما تكون أنابيب الطين بقطر القلم الرصاص، ولكن في بعض الأحيان قد تكون أكثر سمكًا، ويقوم النمل الأبيض ببناء الأنابيب للمأوى لأنها تنتقل بين المستعمرات تحت الأرض والهياكل، وللمساعدة في تحديد ما إذا كا منزلك مصابا أم لا، يمكنك تكسير الأنابيب وفحص وجود النمل الأبيض فإذا كان الأنبوب فارغ، فهذا لا يعني بالضرورة أن الإصابة غير نشطة لأنه في كثير من الأحيان يمكن أن يتخلي عن أجزاء من الأنبوب أثناء البحث عن مكان آخر في الهيكل، ولذلك يمكنك فحص الخشب والتحقيق من وجود النمل باستخدام مفك مسطح الرأس أو أي أداة مماثلة.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>الأضرار التي يسببها النمل الأبيض</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يتسبب النمل الأبيض في أضرار تٌقدر بمليارات الدولارات كل عام، فهي تتغذى بشكل أساسي على الخشب، ولكنها تلحق الضرر أيضًا بالورق، والكتب، والأثاث، وحتى بطانات حمامات السباحة وهياكل المنازل والأساسات، ويمكن أن يصيب النمل الأبيض الأشجار الحية والشجيرات، وفي حين أن النمل الأبيض قد يصيب المباني في أي وقت، إلا أنه قد يستهدف المنازل التي تكون في طور البناء، بسبب الهياكل والأساسات الخشبية التي يتم وضعها.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>كيف يمكنني منع الإصابة بالنمل الأبيض؟</strong></h2>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align:justify\">اجعل الهياكل المنزلية أقل جاذبية للنمل الأبيض أثناء البناء من خلال استخدام أساسً خرساني.</li>\r\n	<li style=\"text-align:justify\">ترك مساحة تهوية بين التربة والخشب.</li>\r\n	<li style=\"text-align:justify\">تغطية الأسطح الخشبية المكشوفة بمانع للتسرب أو بحاجز معدني.</li>\r\n	<li style=\"text-align:justify\">بعد البناء، حافظ على جفاف التربة حول الأساس من خلال الدرجات المناسبة والصرف بما في ذلك صيانة الأنابيب والصرف الصحي.</li>\r\n	<li style=\"text-align:justify\">تقليل الفتحات التي تتيح وصول النمل الأبيض إلى الهيكل من خلال ملء الشقوق في أساس الأسمنت وكذلك حول المكان الذي تمر فيه المرافق عبر الحائط بالإسمنت أو الجص أو السد.</li>\r\n	<li style=\"text-align:justify\">إصلاح التسريبات على الفور والحفاظ على الفتحات خالية من الانسداد، بما في ذلك النباتات.</li>\r\n	<li style=\"text-align:justify\">التأكد من عدم زراعة الأشجار والشجيرات بالقرب من الهيكل وعدم السماح لها بالنمو مقابل الأسطح الخشبية المكشوفة.</li>\r\n	<li style=\"text-align:justify\">منع تراكم أو تخزن الحطب أو الحطام الخشبي بجوار المنزل.</li>\r\n	<li style=\"text-align:justify\">التفتيش الدوري للمساعدة في كشف مستعمرات النمل للتخلص منها.</li>\r\n	<li style=\"text-align:justify\">إصلاح الأثاث التالف بسبب النمل الأبيض أو استبداله في حالة تلفه بالكامل.</li>\r\n</ul>', '<p dir=\"ltr\">Do you have problems with termites in your house? Because Many home owners may not notice that they have problems in the house, because termites often live under the ground and in the walls of your home, termites are called silent destructive insects, because they hide in difficult places to find them, they live secretly in the basement or upper ceilings without appearing Any immediate signs of house damage,</p>\r\n\r\n<p dir=\"ltr\">Termites success to survive in different climates, they eat different types of food according to their type, as all types of termites require four things to survive which are food, humidity, shelter, and the suitable temperature. Unfortunately, human houses can provide these four ideal conditions which cause termite infestation, it is difficult to diagnose an infection, termites cause damage of up to $ 5 billion each year, and if you are wondering how to recognize termites or how to protect your home from these invaders you may have to read this article.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>identification of termites</strong></h2>\r\n\r\n<p dir=\"ltr\">Termites length usually range between 1/4 and 1/2 an inches, it has soft bodies with straight antennas, the queens are the largest, and they can reach more than one inch in length, termite color ranges from white to light brown, workers often appear in lighter degrees of color, flying termites are darker and they are responsible for reproduction, they have two prominent wings.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Termite colony</strong></h2>\r\n\r\n<p dir=\"ltr\">Termites live in colonies that he develops continuously, and they are very similar to the colonies of ants and bees, there is a strict hierarchy between insects and they are workers, soldiers, and males. In the heart of the colony there is a queen of termites&nbsp; which is responsible for the production of eggs, they can lay more than a thousand An egg in one day, also there are types of termites in Africa that lay an egg every 2-3 seconds, so it may reach 30-35 thousand eggs per day, the size of the queen reaches up to 10 cm, which is equivalent to several times the size of the normal termite, That is why she cannot move by herself, and if necessary she is moved to a new location by dozens of ants The white factor</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Why is termite infection usually found in spring?</strong></h2>\r\n\r\n<p dir=\"ltr\">Large numbers of flying termites known as &quot;swarms&quot; usually appear inside homes in the spring, and termite swarms start new colonies, as soon as winged termites appear from the colony they fly into the air due to high temperatures and precipitation, swarms fall onto The land, give up its wings, mate with males,&nbsp; they start new colonies in the soil, the new termite swarms are unable to destroy the woods and live about a day, they could be disposed of with a vacuum cleaner as soon as they are found.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>How do I know if my house has termites?</strong></h2>\r\n\r\n<p dir=\"ltr\">discovery of flying termites always indicates that there is a termite infestation, because they are attracted to light, they are often seen around windows and doors, people, termites have straight antennas, uniform thick waist and wings of equal size, on the contrary ants have antennas, narrow grooves, and front wings that are taller than the posterior wings</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Where are Termites located?</strong></h2>\r\n\r\n<p dir=\"ltr\">Seeing termite swarms outside is near tree trunks, wood, etc. in the yard and this may not mean that the house is infected with termites, on the other hand, if termite swarms appear next to homes and balconies, which means that there is a great chance that the house will spread Also.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Signs of termite infection?</strong></h2>\r\n\r\n<p dir=\"ltr\">&nbsp;One of the most important signs of termite infestation is the seeing of termite swarms at matting times , you can be see dirty mud tubes that extend over the walls of the foundations, sidewalks, panels, sills, wooden floors, etc. Usually the mud tubes are in the diameter of the pencil, but sometimes it may be more Thicker, termites build pipes for shelter as they travel between underground colonies and structures, and to help determine if your house is infected or not, you can break pipes and check for termites if the tube is empty, this does not necessarily mean that the injury is inactive because in many From The times can give up parts of the tube while searching for another place in the temple, so you can examine the wood and investigation of the presence of ants using a flat-head screwdriver or similar tool.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Termite damage</strong></h2>\r\n\r\n<p dir=\"ltr\">Termites cause billions of dollars in damage each year. They are mainly feed on wood, but they also damage paper, books, furniture, and even pool liners, house structures, and foundations. Termites can infect live trees and shrubs, while termites It may strike buildings at any time, but it may target homes that are in the process of being built, due to the structures and wood foundations laid.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>How can I prevent termite infection?</strong></h2>\r\n\r\n<ul dir=\"ltr\">\r\n	<li>Make household structures less attractive to termites while building with a concrete foundation.</li>\r\n	<li>Leave ventilation space between the soil and wood.</li>\r\n	<li>Cover the exposed wooden surfaces with sealant or with a metal barrier.</li>\r\n	<li>After construction, keep the soil dry around the foundation through proper steps and drainage, including pipeline maintenance and drainage.</li>\r\n	<li>Reduce openings that allow termites to reach the structure by filling cracks in the cement foundation as well as around where the facilities pass through the wall with cement, plaster, or a dam.</li>\r\n	<li>Fix leakage and keep openings free from clogging, including plants.</li>\r\n	<li>Ensure that trees and shrubs are not planted near the structure and are not allowed to grow</li>\r\n</ul>', 1, 1, 0, '2020-03-08 14:07:26', '2020-05-09 23:56:03', 'النمل-الابيض', 'Termite', 'حشرة النمل الابيض : معلومات وحقائق وكيفية التخلص من حشرة النمل الأبيض', NULL, 'النمل الابيض', 'Termite', 'Termite | Facts & Identification, Control & Prevention', 'Do you have problems with termites in your house? Because Many home owners may not notice that they have problems in the house, because termites often live under the ground and in the walls of your home, termites are called silent destructive insects, because they hide in difficult places to find them, they live secretly in the basement or upper ceilings without appearing Any immediate signs of house damage,');
INSERT INTO `pest_libraries` (`id`, `name_ar`, `name_en`, `image`, `meta_description_ar`, `image_alt`, `description_ar`, `description_en`, `sort`, `is_active`, `home_order`, `created_at`, `updated_at`, `slug_ar`, `slug_en`, `meta_title_ar`, `sub_pest`, `keywords_ar`, `keywords_en`, `meta_title_en`, `meta_description_en`) VALUES
(8, 'الفئران', 'Mice', 'uploads/pest_libraries/15889195201ca14ce6abdff0668e50cfc503a7176fimage.png', 'تنتمي الفئران إلى فئة الثدييات الصغيرة التي تٌعرف بالقوارض، والتي تتميز بوجود أسنان لا تتوقف عن النمو أبدًا لإبقائها حادة ومدببة ولذلك فهي تقضم الأشياء باستمرار، وهي واحدة من أكثر الآفات شيوعًا حول العالم وعندما تدخل الفئران إلى المنزل فإنها نادراً ما تجلب معها أي شيء جيد، وذلك بسبب نمط المعيشة الخاص بهم والذي يجعلهم يتجولون في أماكن القمامة وحول سلال المهملات، وهذا يجعلهم يقومون بنقل البكتيريا الضارة، ولمكافحة الفئران ستكون بحاجة إلى تحديد علامات الفئران والتعرف على نوعها وكيفية التعامل معها من خلال أبرز النقاط التي سوف نعرضها بعد قليل من خلال هذا المقال.', 'الفئران', '<p dir=\"rtl\">تنتمي الفئران إلى فئة الثدييات الصغيرة التي تٌعرف بالقوارض، والتي تتميز بوجود أسنان لا تتوقف عن النمو أبدًا لإبقائها حادة ومدببة ولذلك فهي تقضم الأشياء باستمرار، وهي واحدة من أكثر الآفات شيوعًا حول العالم وعندما تدخل الفئران إلى المنزل فإنها نادراً ما تجلب معها أي شيء جيد، وذلك بسبب نمط المعيشة الخاص بهم والذي يجعلهم يتجولون في أماكن القمامة وحول سلال المهملات، وهذا يجعلهم يقومون بنقل البكتيريا الضارة، ولمكافحة الفئران ستكون بحاجة إلى تحديد علامات الفئران والتعرف على نوعها وكيفية التعامل معها من خلال أبرز النقاط التي سوف نعرضها بعد قليل من خلال هذا المقال.</p>\r\n\r\n<h2 dir=\"rtl\"><strong>المظهر الخارجي للفئران</strong></h2>\r\n\r\n<p dir=\"rtl\">الفئران كائنات صغيرة جدا، وعموما تصل إلى طول 2 إلى 3 بوصة بالإضافة إلى ذيلها الطويل، ويكون لمعظمها لون رمادي فاتح أو بني أو أسود اللون ولها عيون وآذان كبيرة، ويمكن أن تسبب الفئران لدغة مؤلمة إذا شعرت بأنها محاصرة أو مهددة وعادة ما تكون فضلات الفئران سوداء، وتصبح رمادية وجافة حينما تكون قديمة، وتختلف فضلات الفئران في الحجم، وهذا يتوقف على أنواع الفئران وعمرها&nbsp;وإذا وجدت العديد منها في مكان واحد، وهو أمر محتمل، فهذا يدل على وجود عدة فئران، وإذا كانت الفضلات التي تجدها أصغر من قطعة من الأرز فمن المرجح أن تكون فضلات الصراصير وذلك علامة مميز للتفرقة بينهما، ويمكن للفأر العادي ترك من 70 إلى 150 قطعة براز في يوم واحد، ويكون لها شكل النقانق أو شكل حبوب منع الحمل، حيث تكون رفيعة من الجانبين.</p>\r\n\r\n<h2 dir=\"rtl\"><strong>دورة حياة الفئران</strong></h2>\r\n\r\n<p dir=\"rtl\">تعيش الفئران حياة سريعة للغاية، حيث لا تتوقف الأنثى عن رضاعة صغارهم إلا بعد بضعة أسابيع وينطلقون في بحثهم عن الطعام، وتتكاثر الفئران بنجاح بعد 6 أسابيع فقط من الولادة ، وتكون الفئران الإناث ناضجة جنسيا وقادرة على إنجاب الأطفال، ولأنها ثدييات صغيرة جدًا فلها قائمة طويلة من الحيوانات المفترسة، بدءًا من الطيور إلى الذئاب إلى الثعابين، مما يقلل من متوسط العمر المتوقع لها، وفي المناطق التي لا تتواجد فيها الحيوانات المفترسة مثل المدن، يمكن أن تنفجر الفئران بأعداد كبيرة مما يؤدي إلى ضرر كبير على بيئتها، وتعيش الفئران في المتوسط قرابة العام.</p>\r\n\r\n<h2 dir=\"rtl\"><strong>كيف تدخل الفئران إلى داخل المنازل؟</strong></h2>\r\n\r\n<p dir=\"rtl\">بسبب حجمها الصغير، يمكن للفأر الدخول بسهولة إلى منزلك من خلال الجزء الخلفي من درج الأواني الفضية أو يجد طريقة للدخول إلى خزانتك أو مطبخك للبحث عن الطعام، وقد يتسلل إلى الحمام للبحث عن الرطوبة والماء، وعادة ما يتم العثور على فضلات الفئران عند رفوف التخزين، وعلى طول جدار المرآب أو السقيفة، وفي أماكن العلية، وأسفل أدوات المطبخ ، وخلف الصناديق المخزنة.</p>\r\n\r\n<h2 dir=\"rtl\"><strong>هل الفئران خطيرة على البشر؟</strong></h2>\r\n\r\n<p dir=\"rtl\">من المعروف أن الفئران تنشر العديد من الأمراض المختلفة ، كما أن بعضها يقوم بمضغ الأسلاك ويمكن أن يسبب مخاطر جدية مثل الحريق الكهربائي، وهناك بعض الأمراض التي تنتقل مباشرة عن طريق الفئران وهي مرض السالمونيلا ومتلازمة الرئة الفيروسية، والحمى النزفية مع متلازمة الكلى، حمى الاسا، و داء البريميات، والتهاب المشيمية اللمفاوية والطاعون، حمى الفئران، والتسمم،&nbsp; علاوة على الأضرار الأخرى التي تسببها حيث أنها تكون مدمرة للغاية عندما تغزو منازلنا حيث يمكنها مضغ الأسلاك الكهربائية داخل جدران منازلنا وتشكل خطر الحريق، كما يمكن للقوارض أيضًا مضغ الثقوب عبر الجدران والتسبب في أضرار جسيمة للهياكل، كما تقوم الفئران بتمزيق العزل مما يسمح بفقدان الحرارة وتقوم بمضغ الثقوب في عبوات الطعام وتلوث الطعام أو تدمره. بالإضافة إلى ذلك، فهي تترك البراز والبول في كل مكان يذهبون إليه، مما لا يمثل تهديدًا صحيًا فحسب، بل يخلق أيضًا رائحة كريهة داخل منزلك.</p>\r\n\r\n<h2 dir=\"rtl\"><strong>ما هي علامات الإصابة بالفأر في المنزل؟</strong></h2>\r\n\r\n<ul dir=\"rtl\">\r\n	<li>العلامة الأولى التي من المحتمل أن تلاحظها هي فضلات الفئران والتي يبلغ طولها حوالي 1/4 بوصة، وتكون بالقرب من مصدر الطعام.</li>\r\n	<li>العلامة الرئيسية الأخرى التي يجب البحث عنها هي عش الفأر والذي يقع بشكل عام في منطقة ذات حركة مرور منخفضة وتكون مظلمة معظم الوقت، وتصنع أعشاش الفئران من أي شيء يسهل تمزيقه مثل الصحيفة أو الورق المقوى.</li>\r\n</ul>\r\n\r\n<h2 dir=\"rtl\"><strong>كيف أتخلص من الفئران؟</strong></h2>\r\n\r\n<p dir=\"rtl\">من الصعب للغاية القضاء على الفئران في منزلك، وقد يكون هناك حاجة إلى مساعدة مهنية من شركة مكافحة الحشرات بجدة لمنع القوارض من الدخول إلى منزلك، وحتى اللجوء إلى مساعدة الخبراء يمكنك القيام بما يلي:</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li>سد نقاط الدخول المحتملة للحد من نشاط الفئران.</li>\r\n	<li>&nbsp;التأكد من تخزين جميع الأطعمة بشكل صحيح.&nbsp;</li>\r\n	<li>استخدام حاويات بلاستيكية محكمة الغلق وتنظيف أي انسكابات أو فوضى على الفور لتجنب جذب الفئران إلى منزلك.</li>\r\n	<li>التحقق بعناية من خارج منزلك لمعرفة نقاط الدخول المحتملة للفئران.</li>\r\n	<li>الفحص الدقيق للجدران الخارجية الخاصة بك، والسقف، وجدران الطابق السفلي والبحث عن أي ثقوب أو فجوات أو شقوق أكبر من نصف بوصة حيث يمكن للفئران ضغط جسمها من خلال فتحة بحجم عشرة سنتيمترات ويمكن لبعض الفئران اختراق ثقب بحجم ربع سنتيمير.</li>\r\n	<li>تفقد الأماكن التي يصعب الوصول إليها والنظر عن قرب إلى المكان الذي تمر فيه الأنابيب والمرافق عبر الجدران وحول وحدات تكييف الهواء وتفقد ماسحات الأبواب ونوافذ الطابق السفلي.</li>\r\n	<li>إذا وجدت أي نوافذ مكسورة أو شاشات ممزقة أثناء الفحص، قم بإصلاحها وقم بوضع إطار على عتبة النافذة.</li>\r\n	<li>إذا وجدت فتحات مفتوحة مثل فتحات العادم أو المدخنة فمن المهم تغطيتها بقطعة قماش.</li>\r\n	<li>إذا كان لديك فروع شجرة معلقة فوق سطح منزلك وتقترب منه، فمن المهم تقليمها مرة أخرى، حيث تستخدم الفئران الأشجار للقفز على سطح المنزل والدخول إلى منزلك من خلالها للمنزل.</li>\r\n	<li>إذا رأيت تلفاً في البالوعات أو مصارف الصرف الصحي فلا بد من إصلاحه والتأكد من جفاف الأرضيات من المياه وذلك لقطع مصادر الماء عن الفئران.</li>\r\n	<li>&nbsp;وضع شبكة سلكية في ماسحات التصريف، حيث يمكن للفئران استخدام ماسحات التصريف الخاصة بك للتسلق إلى السقف والوصول إلى المنزل، كما أن وضع شبكة في مجاري المياه السفلية ستمنعهم من إختراق منزلك.</li>\r\n	<li>يجب التخلص من جميع مصادر الطعام الخارجية في الفناء، فإذا كانت لديك شجرة تنتج الفاكهة ومغذيات الطيور وحديقة الخضروات و شجيرات التوت فستحتاج إلى تطبيق شكل ما من أشكال الردع أو الاستبعاد للحفاظ على القوارض بعيدة عن هذه المصادر الغذائية.</li>\r\n	<li>وضع الفخاخ الخارجية فهي مفيدة في مراقبة عدد المرات التي تزور فيها الفئران حديقتك، مع التأكد من الحصول على مصيدة غير ضارة للأطفال أو الحيوانات الأليفة التي تصيب الفئران.</li>\r\n</ul>', '<p dir=\"ltr\">Mice belong to the category of small mammals known as rodents, which are characterized by the presence of teeth which never stop growing and they keep them sharp and pointed, so they can bite things constantly, mice are one of the most common pests around the world, when they enter the house,&nbsp; they rarely bring anything good to it, because of their lifestyle, which causes them to wander around the garbage places and trash, which may transfer harmful bacteria to human, to get rid of mice, you need to identify the signs of infestation and learn about their type and how to deal With them.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Mice appearance and identification</strong></h2>\r\n\r\n<p dir=\"ltr\">Mice are very small creatures, generally up to 2 to 3 inches in length, also they have a long tail, they have light gray color or brown or black according to their type, they also have large eyes and ears, and mice can cause a painful bite if they feel trapped or threatened.</p>\r\n\r\n<p dir=\"ltr\">mice droppings are black, they become gray and dry when they are old, the droppings differ in size, depending on the types of mice and their age, if you find many of them in one place, this indicates the presence of several mice, and if the droppings that You find it smaller than a piece of rice, as cockroach droppings are likely to be a distinctive sign to distinguish between them, mouse can produce 70 to 150 droppings per day, it has the shape of a sausage or contraceptive pills and has a thinner sides.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>mice life cycle</strong></h2>\r\n\r\n<p dir=\"ltr\">Mice live a very fast life, female does not stop feeding their young children until a few weeks later, soon they start their journey to search for food. mice successfully reproduce after only 6 weeks after birth. The female mice are sexually mature and able to reproduce, they are very small mammals and have a long list of predators, , which reduces their life expectancy, in areas where&nbsp; there are no predators such as cities, mice can explode in large numbers leading to great damage to their environment, mice live on average Year.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>How do mice get inside the house?</strong></h2>\r\n\r\n<p dir=\"ltr\">Because of its small size, mice can easily enter your home through the back of the silver tray drawer or find a way to enter your closet or kitchen to search for food, and it may sneak into the bathroom to search for moisture and water, usually mice droppings are found at storage shelves, Along the garage or penthouse wall, in the attic places, under kitchen appliances, and behind stored boxes.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Are mice dangerous to humans?</strong></h2>\r\n\r\n<p dir=\"ltr\">Mice spread many different diseases, they can chew wires and cause serious risks such as electric fire, there are some diseases that are transmitted directly by mice like salmonella and viral lung syndrome, hemorrhagic fever with nephrotic syndrome, fever, Leptospirosis, lymphocytic plague, plague, rat fever, and poisoning, as well as other damages they cause as they are highly destructive when they invade our homes where they can chew electrical wires inside the walls of our homes and pose a fire hazard, and rodents can also chew holes through Walls and causing severe damage to the structures, as the mice tear the insulation, which allows to lose heat and chew holes in food packages and contaminate or destroy food. In addition, it leaves stool and urine everywhere they go, which not only poses a health threat, but also creates an unpleasant odor inside your home.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>signs of mice infection at home</strong></h2>\r\n\r\n<ul dir=\"ltr\">\r\n	<li>The first sign you might notice is mice droppings, you can find them near the food source.</li>\r\n	<li>The other major sign that needs to be searched is the mouse nest, which is generally located in an area with low traffic and is dark most of the time, and rat nests are made of anything that is easy to tear like a newspaper or cardboard.</li>\r\n</ul>\r\n\r\n<h2 dir=\"ltr\"><strong>How to get rid of mice?</strong></h2>\r\n\r\n<p dir=\"ltr\">It is extremely difficult to eliminate mice in your home, and professional help from a pest control company in Jeddah may be needed to prevent rodents from entering your home, and even resort to expert help. You can do the following:</p>\r\n\r\n<ul dir=\"ltr\">\r\n	<li>Block potential entry points to limit rat activity.</li>\r\n	<li>Ensure all foods are stored properly.</li>\r\n	<li>Use plastic containers and clean up any spills or clutter immediately to avoid attracting mice to your home.</li>\r\n	<li>Check carefully outside of your home to see possible entry points for mice.</li>\r\n	<li>Examine closely your external walls, ceiling, and basement walls and look for any holes, gaps, or cracks greater than half an inch where mice can compress their body through a ten-centimeter hole and some mice can penetrate a quarter-centimeter hole.</li>\r\n	<li>Check closely at pipes and facilities through walls and around air conditioning units, and check door wipers and basement windows for closure.</li>\r\n</ul>', 4, 1, 0, '2020-03-10 12:12:23', '2020-05-08 06:03:21', 'الفئران', 'Mice', 'الفئران : معلومات وحقائق وكيفية التخلص من الفئران', NULL, 'الفئران', 'Mice', 'Mice | Facts & Identification, Control & Prevention', 'Mice belong to the category of small mammals known as rodents, which are characterized by the presence of teeth which never stop growing and they keep them sharp and pointed, so they can bite things constantly, mice are one of the most common pests around the world, when they enter the house,  they rarely bring anything good to it, because of their lifestyle, which causes them to wander around the garbage places and trash, which may transfer harmful bacteria to human, to get rid of mice, you need to identify the signs of infestation and learn about their type and how to deal With them.'),
(9, 'البعوض', 'Mosquito', 'uploads/pest_libraries/1588920637e8e8ae0edf89c82f333e87632bb0ad28image.png', 'إذا كانت هناك حشرة واحدة أثبتت أنها مصدر إزعاج للجنس البشري، فهي بالتأكيد البعوضة، حيث  يمكنك سماعهم والشعور بقرصاتهم، ولكن لا يمكنك رؤيتهم، كما أن لديهم القدرة على حمل الأمراض الفتاكة ونقلها إلى البشر، وإذا وجدت نفسك تريد معرفة المزيد عن البعوض فأنت هنا في المكان المناسب، حيث تشمل عائلة البعوض حوالي 41 جنسًا معترفًا بهم، كما تضم ​​حوالي 3500 نوعًا آخر، والكثير من البعوض تكون ناقلات لمسببات الأمراض التي تصيب البشر والحيوانات الأليفة، وتشمل هذه الأمراض المعروفة الملاريا، داء الفيلاريات، التهاب الدماغ، الحمى الصفراء، وحمى الضنك. وهناك العديد من الأمراض الأخرى التي تسببها، كما أن البعوض حشرة مزعجة وقد تسبب الحساسية، أو تتسبب في فقدان كثير للدم عندما تقوم بلدغ البشر.', 'البعوض', '<p dir=\"rtl\" style=\"text-align:justify\">إذا كانت هناك حشرة واحدة أثبتت أنها مصدر إزعاج للجنس البشري، فهي بالتأكيد البعوضة، حيث&nbsp; يمكنك سماعهم والشعور بقرصاتهم، ولكن لا يمكنك رؤيتهم، كما أن لديهم القدرة على حمل الأمراض الفتاكة ونقلها إلى البشر، وإذا وجدت نفسك تريد معرفة المزيد عن البعوض فأنت هنا في المكان المناسب، حيث تشمل عائلة البعوض حوالي 41 جنسًا معترفًا بهم، كما تضم ​​حوالي 3500 نوعًا آخر، والكثير من البعوض تكون ناقلات لمسببات الأمراض التي تصيب البشر والحيوانات الأليفة، وتشمل هذه الأمراض المعروفة الملاريا، داء الفيلاريات، التهاب الدماغ، الحمى الصفراء، وحمى الضنك. وهناك العديد من الأمراض الأخرى التي تسببها، كما أن البعوض حشرة مزعجة وقد تسبب الحساسية، أو تتسبب في فقدان كثير للدم عندما تقوم بلدغ البشر.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>المظهر الخارجي للبعوض</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">البعوض حشرات رفيعة وطويلة الساق وتتميز بوجود جناحان يمكن التعرف عليها بسهولة من خلال خرطومها الطويل ويوجد لها أجزاء فمية تسمح لها بامتصاص السوائل، وتتميز اليرقات عن غيرها من الحشرات المائية بغياب الأرجل، ويكون لها صدر منتفخ أوسع من الرأس والبطن، ويكون لها زوج من الفتحات التنفسية، ويختلف البعوض في اللون اعتمادا على الأنواع فبعضها&nbsp; يكون لونه بني شاحب مع عصابات بيضاء على بطنهم وبعضهم لهم لون أصفر، والبعض له علامات بيضاء ساطعة أسفل أرجلها، والبعض يكون له لون أسود مع خطوط بيضاء في كل من الجسم والساقين.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>دورة حياة البعوض</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يعيش البعوض في البيئات الرطبة التي تحتوي على كميات وفيرة من المياه، فهي ضرورية لتطور اليرقات، حيث أنها تخضع لتحول كامل داخل الماء، وتكون الحشرات البالغة مجنحة وقادرة على الطيران لرحلات واسعة، والمعروف أن الإناث فقط هي التي تتغذى على الدم، حيث تستخدمه لتطوير البيض، لكن كلا الجنسين يتغذيان على سكر النبات، وتتغذى الإناث من معظم الأنواع في المقام الأول على دم الحيوانات البرية أو الدواجن، وهناك عدد قليل منها متخصص في مص دماء البشر، وفي بعض الأنواع تكون التغذية على الدم اختيارية.</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">وتقوم الإناث بوضع البيض على سطح من الماء وتفقس في اليرقات في الماء، وتتغذى اليرقات على الطحالب والحطام العضوي، وقد يكون القليل منها مفترسًا وربما يتغذى على البعوض الآخر، وعلى عكس معظم الحشرات فإن هناك مرحلة خادعة في حياة البعوض والتي يطلق عليها البهلوان، الذي ينشط ويسبح وتتنفس الشرانق عن طريق أنابيب الصدر، وتتزاوج الحشرة البالغة بعد فترة وجيزة وتختلف مدة دورة الحياة اعتمادًا كبيرًا على الأنواع</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>عادات البعوض</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">نظرا لأن البعوض حشرات حساسة، فهي تتواجد في المناطق ذات الهواء الباردً نسبيًا حيث الرطوبة العالية، وتعيش العديد من الأنواع على بعد أمتار قليلة من الأرض، وتعيش يرقات البعوض في البيئات المائية، وتتغذى غالبية اليرقات على الجسيمات العالقة والكائنات الحية الدقيقة على سطح الماء باستخدام فمها، وتحصل يرقات البعوض على الأكسجين عن طريق سطح الماء وبعض الأنواع تحصل على الأكسجين من الأوعية الهوائية للنباتات المائية وهناك بعض الأنواع التي نادرا ما تأتي إلى السطح وتحصل على الأوكسجين المذاب من الماء.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>غذاء البعوض</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يتغذى البعوض بما في ذلك من الذكور والإناث حصرياً على السوائل النباتية، بما في ذلك الرحيق وعسل النحل وعصائر الفاكهة والافرازات النباتية، بينما تتغذى الإناث على دم الحيوانات الحية، وتعتبر الفقاريات ذات الدم الدافئ مصدرًا شائعًا للدم بالنسبة للبعوض وكذلك الحيوانات ذات الدم البارد مثل الثعابين والسلاحف والضفادع والضفادع والحشرات الأخرى، ومن المعروف أنها تتغذى على ديدان الأرض، وهناك بعض الأنواع التي تنشط في الليل أو الشفق بينما تنشط بعض الأنواع خلال ساعات النهار.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>أماكن اختباء البعوض في المنزل</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">الكثير من أصحاب المنازل يشكون من أنهم يمكنهم سماع البعوض ينتشر حول أذنك، ولكن من الصعب العثور عليهم، ولحسن الحظ هناك طريقة للعثور عليهم&nbsp; فقط قم بتشغيل مصباح يدوي عندما تسمع صوت الطنين وضع المصباح مباشرة على الحائط بحيث يسير الضوء في خط مستقيم عبر الحائط، وإذا كانت البعوضة على الحائط، فإن ظلها سيكون ضخمًا، كما يمكنك لف المصباح في جميع الاتجاهات حتى تجد البعوض، ويمكنك أيضًا ترك مصدر ضوء صغير واحد مثل المصباح وانتظر البعوض ليقترب منه حيث أن البعوض يهبط على الحائط بالقرب من مصدر الضوء بحيث يمكنك العثور عليه باستخدام مصباحك الضوئي وقتله.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>الأضرار التي يسببها البعوض للبشر</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">كما ذكرنا من قبل تمتص إناث البعوض الدماء، وهي تزعج البشر والحيوانات الأخرى، وقد تنقل مسببات الأمراض التي تسبب الأمراض للبشر والحيوانات، وتشمل مسببات الأمراض التي تنتقل عن طريق البعوض الفيروسات والديدان الخيطية والديدان الطفيلية والبروتوزوا والملاريا، كما أن البعوض هو السبب غير المباشر للكثير من الوفيات بين البشر، كما أنها تقوم بقرص البشر خاصة من الأجزاء العارية من أجسادهم، وقد تسبب لهم قرصات حمراء وملتهبة، مع شعور قوي بالحكة في كثير من الأحيان، كما أن بعض البشر قد يظهرون أعراض قوية للحساسية الناتجة عن قرصات البعوض، وفي حالة الإصابات الشديدة بالبعوض في بعض المنازل تسبب فقر الدم للصغار نتيجة التغذية المستمرة على دمائهم وفقدهم لكميات كبيرة من المياه.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>القضاء على البعوض</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">نظرًا لأن البعوض عبارة عن حاملات للأمراض المعدية، فإن منعها من التغذية على البشر يعتبر استراتيجية صحية عالمية رئيسية، ويمكن تقليل احتمال انتقال المرض من خلال استخدام طارد البعوض، والملابس الطويلة التي تغطي الذراعين والساقين، ووضع الشاشات على الأبواب والنوافذ المفتوحة، واستخدام شبكات البعوض المعالجة والمعالجة بالمبيدات الحشرية، كما يمكن السيطرة على تجمعات البعوض من خلال القضاء على مصادر المياه الدائمة، والتي توفر مواقع تكاثر مثالية للبعوض،</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">ويمكن وضع طبقة سطحية من الزيت على المياه الراكدة لسد فرص التنفس للحشرات التي توجد على سطح الماء مما يؤدي إلى قتلها، وقد تكون المبيدات الحشرية فعالة لتدمير البعوض البالغ في الداخل، كما أن هناك الكثير من الأساليب الحديثة والمستخدمة في خفض أعداد البعوض وتعطيل انتقال العوامل الممرضة للبشر، بما في ذلك المبيدات الحشرية والتغييرات الجينية، كما يجب التعرف على نوع البعوض وتحديد مواقع التكاثر، والمراقبة الدائمة عن طريق الإزالة أو إدارة مواقع التربية، وتطبيق مبيدات اليرقات، وتطبيق مبيدات الحشرات، وغالبًا ما تقوم شركة مكافحة الحشرات بجدة بدمج برامج المكافحة لاسابقة في برامج شامل للإدارة المتكاملة، علاوة على إدارة عمق الماء والنباتات المائية للتحكم في يرقات البعوض خاصة في أماكن الفيضانات وحقول الأرز.</p>', '<p dir=\"ltr\" style=\"text-align:justify\">If there is one insect that has proven a nuisance to humans, it is definitely a mosquito, you can hear them and feel their pinch, but you cannot see them, they have the ability to carry deadly diseases and transmit them to humans, the mosquito family includes about 41 recognized species, and includes about 3,500 other species, many mosquitoes carry pathogens that affect humans and domestic animals, including malaria, filariasis, encephalitis, yellow fever, and dengue fever. Mosquitoes also may cause allergies, a lot of blood loss when they bite humans.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Mosquitoes identification</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Mosquitoes are thin, has long-legged, has two wings that are easily identifiable, they have mouth parts that allow them to absorb fluids, larvae are distinguished from other water insects by the absence of legs, they have a wider chest than the head and abdomen, a pair of respiratory openings, mosquitoes differ in color depending on the species, some of them are pale brown with white bands on their stomachs, some of them have a yellow color, some have bright white markings below their legs, and some have a black color with white stripes in both the body and legs.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Mosquitoes life cycle</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Mosquitoes live in humid environments that contain abundant amounts of water, they are necessary for the development of larvae, they are subjected to a complete transformation within the water, adults are winged and able to fly for large trips, only females feed on blood, where they use it to develop eggs But both sexes feed on plant sugar, females feed primarily on the blood of wild animals or poultry,&nbsp; there are a few of them that specialize in sucking human blood, and in some species blood nutrition is optional.</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Females lay eggs on a surface of water and hatch in the larvae in the water. larvae feed on algae and organic debris. Few of them may be predators and may feed on other mosquitoes. Unlike most insects, there is a deceptive stage in the life of mosquitoes, which is called the acrobat, which activates The cocoons swim and breathe through the chest tubes, and the adult insect mates soon after and the life cycle duration varies greatly depending on the species</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Mosquitoes habits</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Mosquitoes are sensitive insects, they are found in areas with relatively cold air where high humidity, many species live a few meters from the ground, mosquito larvae live in aquatic environments, most of the larvae feed on suspended particles and microorganisms on the surface of the water using their mouth , Amosquitoes larvae get oxygen through the surface of the water and some species get oxygen from the air vessels of aquatic plants and there are some types that rarely come to the surface and get dissolved oxygen from the water.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Mosquitoes food</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Both males and females feed on plant fluids, including nectar, honey, fruit juices and plant secretions, while females feed on the blood of live animals. Vertebrates with warm blood are a common source of blood for mosquitoes as well as cold-blooded animals such as snakes and turtles Frogs, frogs, and other insects are known to feed on earthworms. There are some species that are active at night or afterglow while some species are active during daylight hours.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Mosquitoes hiding places in your house?</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Many homeowners complain that they can hear mosquitoes spread around your ear, but it is difficult to find them, there is a way to find them just turn on a flashlight when you hear a sound, put the lamp directly on the wall so that the light goes in a straight line across the wall, and if The mosquitos were on the wall, their shadow would be huge, you can wrap the lamp in all directions until you find mosquitoes, you can also leave one small light source like the lamp and wait for the mosquitoes to get close to it as mosquitoes land on the wall near the light source so that you can find and kill them.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Mosquitoes and disease</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">female mosquitoes absorb blood of&nbsp; both humans and animals, and they may transmit pathogens that cause disease, pathogens that are transmitted by mosquitoes include viruses, nematodes, helminths, protozoa and malaria, and mosquitoes are the indirect cause of many Mortality between humans, and it also pinches humans, especially from the bare parts of their bodies, it may cause red and inflamed dots, with a strong itching sensation, some people may show strong symptoms of allergy caused by mosquito bites, and in the case of severe injuries Mosquitoes in some homes cause anemia to young people as a result of the constant feeding on their blood and the loss of large quantities of blood.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Getting rid of mosquitoes</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">mosquitoes are carriers of infectious diseases, preventing them from feeding on humans is a major global health strategy, this can be reduced through using of mosquito repellent, long clothes&nbsp; which cover arms and legs, placing screens on open doors and windows, using mosquito nets And treatment with insecticides, mosquito infestation can be controlled by eliminating water sources, which provide ideal breeding sites for them, a surface layer of oil can be placed on water to block breathing opportunities for insects on the surface, which kills them,&nbsp; pesticides may be effective for destroying adult mosquitoes inside, identify the type of mosquitoes and determine the breeding sites And permanent monitoring through removal or management of breeding sites, the application of larvicides, application of insecticides, the Pest Control Company in Jeddah integrates these control programs for comprehensive integrated treating.</p>', 7, 1, 0, '2020-03-10 23:49:52', '2020-05-08 06:04:03', 'البعوض', 'Mosquito', 'حشرة البعوض : معلومات وحقائق وكيفية التخلص من الناموس', NULL, 'البعوض', 'Mosquito', 'Mosquito | Facts & Identification, Control & Prevention', 'If there is one insect that has proven a nuisance to humans, it is definitely a mosquito, you can hear them and feel their pinch, but you cannot see them, they have the ability to carry deadly diseases and transmit them to humans, the mosquito family includes about 41 recognized species, and includes about 3,500 other species, many mosquitoes carry pathogens that affect humans and domestic animals, including malaria, filariasis, encephalitis, yellow fever, and dengue fever. Mosquitoes also may cause allergies, a lot of blood loss when they bite humans.'),
(10, 'النمل', 'Ants', 'uploads/pest_libraries/158891830001d4db0ee1fc9ab62ed678efb5cb52f7image.png', 'هل تعلم أن العالم يضم أكثر من 10000 نوع من النمل حول العالم؟ ربما تكون هذه المعلومة جديدة بالنسبة لك وقد تثير الفزع داخلك، لكن الخبر الجيد أن عدد أقل بكثير من هذا الرقم هو الذي يقوم بغزو منازلنا أو يتواجد في البيوت أو حولها، ويعتبر النمل من الحشرات الإجتماعية التي تعتب جزء هام من النظام البيئي والذي يسهم بشكل كبير في صحة البيئة والكرة الأرضية، ومن مزايا النمل أن بعض الأنواع يمكنها أن تعمل على تخصيب التربة، كما أنه عنصر مهم في السلسلة الغذائية حيث تتغذى عليه الكثير من الحشرات والحيوانات الأكبر حجما، كما أن النمل يقوم بتطهير الأرض من المواد العضوية مثل الحيوانات الميتة والحشرات الميتة ويقوم بتنظيف فضلات الأوراق.', 'النمل', '<p dir=\"rtl\" style=\"text-align:justify\">هل تعلم أن العالم يضم أكثر من 10000 نوع من النمل حول العالم؟ ربما تكون هذه المعلومة جديدة بالنسبة لك وقد تثير الفزع داخلك، لكن الخبر الجيد أن عدد أقل بكثير من هذا الرقم هو الذي يقوم بغزو منازلنا أو يتواجد في البيوت أو حولها، ويعتبر النمل من الحشرات الإجتماعية التي تعتب جزء هام من النظام البيئي والذي يسهم بشكل كبير في صحة البيئة والكرة الأرضية، ومن مزايا النمل أن بعض الأنواع يمكنها أن تعمل على تخصيب التربة، كما أنه عنصر مهم في السلسلة الغذائية حيث تتغذى عليه الكثير من الحشرات والحيوانات الأكبر حجما، كما أن النمل يقوم بتطهير الأرض من المواد العضوية مثل الحيوانات الميتة والحشرات الميتة ويقوم بتنظيف فضلات الأوراق.</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">وللنمل طوائف وفئات متعددة وهي تضم عدة طبقات وهم العمال والذكور والملكات، ويختلف الضرر الذي يسببه النمل من نوع إلى آخر، ومعظمها في المقام الأول يعتبر مصدر إزعاج للمنازل، ويساعدك معرفة نوع النمل بمنزلك والأطعمة التي يأكلها في السيطرة على مستعمرات النمل والقضاء الفعال على الحشرة، وإذا لم تتمكن من تحديد نوع النمل في منزلك يمكنك التواصل مع خبراء شركة مكافحة الحشرات بجدة لمساعدتك في هذه المهمة، ومن خلال هذا المقال سوف نتعرف على أهم المعلومات التي تحتاجها عن النمل وكيفية التعامل معها ولذلك تابع القراءة معنا.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>المظهر الخارجي للنمل&nbsp;</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">للنمل ألوان مختلفة تتمايز من اللون الأحمر إلى البني أو الأسود أو الأصفر ويختلف النمل وفقا لنوع الحشرة حيث يوجد العديد من الأنواع للنمل،&nbsp; وتتشابه النمل في أنها جميعا تملك ثلاثة أجزاء مميزة للجسم وهي منطقة الرأس والصدر والبطن، وتملك جميع أنواع النمل زوج من قرون الإستشعار في منطقة الرأس، وتكون المنطقة بين القفص الصدري والبطن ضيقة للغاية وهي من العلامات المميزة بين النمل والنمل الأبيض&nbsp;ويشبه النمل حشرة النمل الأبيض كثيرًا ، وغالبًا ما يتم الخلط بينهما خاصة من قبل أصحاب المنازل، ولكن يمكن التمييز بينهما من خلال منطقة خصر النمل التي تكون ضيقة بين البطن والصدر، على عكس النمل الأبيض، كما يملك النمل رؤوس كبيرة، وهوائيات وفكين قويين، ويكون لون النمل الأبيض مائلاً إلى اللون الأبيض أو الكريمي، أما النمل فيختلف لونه من الأسود إلى البني أو الأحمر.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>تكاثر النمل</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يتكون النمل من أربعة أنواع وهي الملكة، الذكور ، النمل المجنح، العمال، وتتكون فئة العمال من الإناث وهي حشرات عقيمة ولا تملك أي أجنحة ويتراوح حجمها ما بين 1/20 بوصة إلى حوالي 1/2 بوصة ويتنوع العمال في الحجم وينقسمون إلى عمال كبيرة وعمال ثانوية أو صغيرة، وتعتبر الملكات والذكور هي الفئات المسئولة عن التكاثر وتوسعة حجم المستعمرة، وللذكور عادة نفس حجم العمال أو أكبر وهم يملكون أجنحة ورأس صغير بعيون كبيرة، ويتم العثور عليهم في المستعمرات الكبيرة ويكون هدفهم الوحيد هو التزاوج مع الملكات الجديدة ويموتون بعد ذلك بوقت قصير.</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">وتعتبر الملكة هي أكبر أعضاء المستعمرة، وغالبًا ما تكون أكبر مرتين أو ثلاث مرات من العمال، ويكون لها أجنحة ولكنها تنكسر بعد التزاوج ويكون لديها بطون كبيرة جدا ويمكن أن تعيش لعدة سنوات&nbsp;وتيدأ عملية التكاثر حينما يطير سرب من الذكور والملكات من العش في مواسم التكاثر والتي تكون خلال وقت محدد من السنة، وبعد التزاوج تسعى الأنثى إلى موقع التعشيش المناسب وتبدأ في تكوين مستعمرة جديدة، ويٌعد وجود النمل المجنح داخل المبني مؤشرا على وجود عش للنمل في الداخل.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>طعام النمل</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يتواصل النمل ويتعاون مع زملائه باستخدام مواد كيميائية تٌسمى بالفيرمونات، ويمكن أن تٌنبه الآخرين للخطر أو ترشدهم إلى مصدر للغذاء، ويأكل النمل عادة الرحيق والبذور، والفطريات أو الحشرات، ومع ذلك، فإن بعض الأنواع تتناول الحيوانات الميتة والمواد العضوية، وفي المنازل ينجذب النمل للمواد الكربوهيدراتية والسكريات والمواد الدهنية مثل الحلويات والزبدة والسكر، وهو يتغذى على بقايا الطعام والفتات التي يتركوها أصحاب المنازل في المطبخ أو في حجرات تناول الطعام.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>السلوك الاجتماعي للنمل</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">النمل من الحشرات الاجتماعية الحماسية، وهي تعيش عادة في مجتمعات منظمة قد توجد تحت الأرض أو في التلال أو فوق مستوى الأرض أو في الأشجار، حيث يعيش النمل النجار في الخشب ويمكن أن يدمر المباني، وبعض الأنواع تتحدى القاعدة ولا يكون لها مساكن دائمة، وبدلاً من ذلك تبحث عن الطعام لمستعمراتها خلال فترات الهجرة، ويرأس مجتمعات النمل الملكة والتي تكون وظيفتها في الحياة هي وضع الآلاف من البيض التي تضمن بقاء المستعمرة، في حين أن النمل العامل يكون بدون أجنحة ولا يتكاثر أبدً وهو يقوم بتوليد الملكة ورعايتها والعمل داخل العش وحماية مجتمعه.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>أضرار التي يسبببها النمل للبشر؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يٌسبب النمل الكثير من الأضرار لأصحاب المنازل وذلك وفقاَ لنوع النمل الذي يقوم بغزو منزلك، فعلى سبيل المثال يغزو النمل الفرعوني المنزل من أجل الطعام ويتسبب في تلويث الأسطح والأطعمة التي يسير فوقها، أما النمل النجار فهو يٌضعف الخشب في الهياكل، وبشكل عام يتسبب النمل في الكثير من المشاكل، كما بمكن أن تتحول مستعمرات النمل التي تعيش حول منزلك أو داخله مصدر إزعاج حقيقي، حيث يمكن للنمل نشر البكتيريا والطفيليات، وغيرها من مسببات الأمراض، كما أنه يمكن أن يلحق الضرر في الحديقة الخاصة بك وفي منزلك، ولذلك فإن حماية عائلتك ومنزلك من تهديد الإصابة بالنمل هي عملية مستمرة لا يجب أن تتوقف.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>أماكن اختباء النمل في المنازل</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يتواجد النمل داخل المنزل في العديد من الأماكن سواء في المطبخ أو الحمام، ولذلك قم بفحص الأحواض والمراحيض بحثًا عن أي تسربات أو قطرات تجذب النمل للمنزل الذي يبحث عن المأوي والغذاء والرطوبة، حيث يمكن للنمل العثور بسهولة على طريقة للدخول إلى المنزل حتى من خلال أصغر الشقوق، لذا فإن المناطق الأخرى في المنزل هي أيضًا مخابئ شائعة ومنها الجدران الداخلية وغرف النوم وغرف المعيشة والطوابق السفلية ووحدات تكييف الهواء والتدفئة.</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">ولمنع النمل من العثور على طريقة للتسرب إلى الداخ ، يجب على مالكي المنازل مقاومة الحشرات في الخارج، حيث يوصي الخبراء بإغلاق أي تشققات بسد سيليكون، وإصلاح الثقوب في شاشات النوافذ والأبواب، وسد الثقوف حول الأساس السفلي والنوافذ، والحفاظ على النباتات والأشجار على مسافة بعيدة عن المنزل حتي لا تدخل الحشرات بسهولة للمنزل.&nbsp;</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>كيف تتخلص من النمل؟</strong></h2>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align:justify\">معرفة مكان مستعمرة النمل والأطعمة التي يتناولها هي أفضل طريقة للتخلص منها.</li>\r\n	<li style=\"text-align:justify\">رش تراب الدياتومي في الأماكن التي يتواجد بها النمل.</li>\r\n	<li style=\"text-align:justify\">مسح الأرضيات بمواد مطهرة لها رائحة قوية.</li>\r\n	<li style=\"text-align:justify\">مسح الأرضيات بالخل لأن له تأثير طارد للنمل.</li>\r\n	<li style=\"text-align:justify\">زراعة النعناع البري خارج المنزل لطرد النمل.</li>\r\n	<li style=\"text-align:justify\">رش النمل مباشرة بمحلول الماء وصابون الأطباق.</li>\r\n	<li style=\"text-align:justify\">رش النمل بمحلول الليمون والقرفة.</li>\r\n	<li style=\"text-align:justify\">سد أماكن الدخول والخروج الخاصة بالنمل.</li>\r\n	<li style=\"text-align:justify\">التواصل مع شركة مكافحة الحشرات بجدة للتعرف على نوع النمل والقضاء الفوري عليها.</li>\r\n</ul>', '<p dir=\"ltr\" style=\"text-align:justify\">Ants are social insects, there is more than 10,000 known types of ants around the world, many of them are found in and around human houses, ants are considered a vital part of the ecosystem and they contribute to environmental health all over the world, as it cleanses the earth of organic materials such as Dead animals and dead insects, create fertile soil and they are also an essential part of the food chain.</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Ants are divided into many classes, workers, males and queens, damage caused by ants varies from one type to another, most of which are primarily a source of inconvenience for homes. Knowing the type of ants in your home and the foods you eat helps you control ant colonies and effectively eliminate the insect, and if you do not You can determine the type of ants in your home, you can contact the experts of the pest control company in Jeddah to help you in this task.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Ants external appearance&nbsp;</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Ants differ in color from yellow to red to brown and black according to the type of insect, ants contain three distinct parts of the body which are the head, chest and abdomen, all types of ants have a pair of antennas in the head area, and the area between the chest and abdomen is very narrow&nbsp;Ants are very similar to termites, they often cause confusion especially by homeowners, they can be distinguished by the narrow waist region between the abdomen and chest in ants, unlike termites, as the ants also have large heads, strong antennas and jaws. termites are white or creamy, while ants varies from black to brown or red.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Ants colonies</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Ants consist of four types, the queen, males, flying ants, workers, workers consist of females, which do not have any wings and their size ranges between 1/20 inches to about 1/2 inches, workers vary in size and are divided into large workers and small or secondary workers, the queens and males are the groups responsible for the reproduction and expansion of the colony size, and the males usually have the same size of workers or greater and they have wings and a small head with large eyes, they are found in the large colonies and their only goal is to mate with the new queens and die shortly after that .</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">The Queen is the largest member of the colony, often two or three times larger than the workers, and has wings but is broken after mating and has very large stomachs and can live for several years.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Ant matting</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">The male and queue swarms fly from the nest in the breeding seasons, and they mate during a specific time of the year. After mating, the female seeks the appropriate nesting site and begins the formation of a new colony. The presence of winged ants inside the building is an indication of the presence of ant colony.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Ant food</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Ants communicate and collaborate with their colleagues using chemicals called pheromones, they can alert others to danger or guide them to a source of food. ants usually eat nectar and seeds, fungi or insects. However, some species eat dead animals and organic materials, in homes ants are attracted to carbohydrates. Sugars and fatty substances such as sweets, butter and sugar, which feed on leftovers and crumbs that homeowners leave in the kitchen or in the dining room.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Social behavior of ants</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Ants are social insects, they usually live in organized societies that may be found under the ground, in the hills, above the ground level, or in trees. Where ants live in wood and can destroy buildings, some species defy the base and do not have permanent housing. That searches for food for its colonies during periods of migration, ant societies are headed by the Queen, whose function in life is to lay thousands of eggs that ensure the colony&#39;s survival, while the working ants are without wings and never reproduce as they generate and care for the Queen and work within the nest and protect his community.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Ant damage</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Ants cause a lot of damage to homeowners, according to the type of ants that invade your home. For example, the Pharaonic ants invade the house for food and pollute the surfaces and foods that go on it, while the carpenter ants weakens the wood in the structures, in general the ants cause a lot of Problems, ant colonies that live around or inside your house can become a real inconvenience, as ants can spread bacteria, parasites, and other pathogens, and it can damage your garden and your home, so protecting your family and home from a threat Infection with ants is a process Continuous should not stop.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Where do ants hide in homes?</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Ants inside the house are found in many places, whether in the kitchen or bathroom, so examine the basins and toilets for any leaks or drops that attract the ants to the house looking for shelter, food and moisture, where the ants can easily find a way to enter the home even through the smallest cracks, other areas of the house are&nbsp; common hideouts including interior walls, bedrooms, living rooms, basements, air conditioning and heating units&nbsp;To prevent the ants from finding a way to leak into the interior, homeowners must resist insects outside, as experts recommend closing any cracks with a silicone seal, fixing holes in window and door screens, plugging cultures around the bottom foundation and windows, and keeping plants and trees away from House so that insects do not get inside easily.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>How to get rid of ants?</strong></h2>\r\n\r\n<ul dir=\"ltr\">\r\n	<li style=\"text-align:justify\">Knowing the location of the ant colony and the foods it eats is the best way to get rid of it.</li>\r\n	<li style=\"text-align:justify\">Spray diatomaceous earth where the ants are.</li>\r\n	<li style=\"text-align:justify\">Wiping floors with disinfectants that have a strong smell.</li>\r\n	<li style=\"text-align:justify\">Wiping floors with vinegar because it has an ant repellant effect.</li>\r\n</ul>', 5, 1, 0, '2020-05-06 03:38:18', '2020-05-08 06:03:29', 'النمل', 'Ants', 'حشرة النمل : معلومات وحقائق وكيفية التخلص من النمل', NULL, 'النمل', 'Ants', 'Ants | Facts & Identification, Control & Prevention', 'Ants are social insects, there is more than 10,000 known types of ants around the world, many of them are found in and around human houses, ants are considered a vital part of the ecosystem and they contribute to environmental health all over the world, as it cleanses the earth of organic materials such as Dead animals and dead insects, create fertile soil and they are also an essential part of the food chain.');
INSERT INTO `pest_libraries` (`id`, `name_ar`, `name_en`, `image`, `meta_description_ar`, `image_alt`, `description_ar`, `description_en`, `sort`, `is_active`, `home_order`, `created_at`, `updated_at`, `slug_ar`, `slug_en`, `meta_title_ar`, `sub_pest`, `keywords_ar`, `keywords_en`, `meta_title_en`, `meta_description_en`) VALUES
(11, 'البراغيث', 'Fleas', 'uploads/pest_libraries/1588919304e7e6723174c29456a40fdd2153ae0961image.png', 'إذا كنت مٌعتادا على تربية الحيوانات الأليفة في منزلك مثل القطط والكلاب، فلا شك أن منزلك وحيواناتك تكون عٌرضة للبراغيث فهي من الحشرات الشائعة في المنازل التي تعتاد على تربية مثل هذه الحيوانات الأليفة لأنها تعتبر العائل الأول لها، وهي من الكائنات التي تبحث عن مضيف لكي تتغذى على دمه، وتضع البيض عليه، حيث يمكن لأنثى البرغوث وضع ما يصل إلى 500 بيضة في حياتها، وهي تفضل القطط المنزلية لكنها يمكن ان تصيب أي حيوان ثديي آخر بما في ذلك الفئران والكلاب.', 'Fleas', '<p dir=\"rtl\" style=\"text-align:justify\">إذا كنت مٌعتادا على تربية الحيوانات الأليفة في منزلك مثل القطط والكلاب، فلا شك أن منزلك وحيواناتك تكون عٌرضة للبراغيث فهي من الحشرات الشائعة في المنازل التي تعتاد على تربية مثل هذه الحيوانات الأليفة لأنها تعتبر العائل الأول لها، وهي من الكائنات التي تبحث عن مضيف لكي تتغذى على دمه، وتضع البيض عليه، حيث يمكن لأنثى البرغوث وضع ما يصل إلى 500 بيضة في حياتها، وهي تفضل القطط المنزلية لكنها يمكن ان تصيب أي حيوان ثديي آخر بما في ذلك الفئران والكلاب.</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">والاسم العلمي للبراغيث هو&nbsp; Ctenocephalides felis، وهي حشرة صغيرة تقفز سريعا من مكان لآخر لكنها لا يمكنها الطيران لأنها لا تملك أجنحة، وهي تنتقل إلينا من خلال الحيوانات ذات الفرو على الأغلب، ونتيجة لذلك، يكون أصحاب الحيوانات الأليفة أكثر عرضة للإصابة بالبراغيث من أولئك الذين ليس لديهم حيوانات أليفة، خاصةً إذا سمحوا لحيواناتهم الأليفة بالذهاب للخارج لفترات طويلة، وفيما يلي سوف نتعرف على أهم المعلومات المتعلقة بالبراغيث كي تتمكن من القضاء الفعال عليها.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>المظهر الخارجي للبراغيث</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يبلغ طوله البراغيث البالغة حوالي 1/8 بوصة طولاً، ولا تملك أي أجنحة وبالتالي فهي لا تتمكن من الطيران لكنها تتمتع بقدرة فائقة على القفز السريع كما ذكرنا منذ قليل ويكون جسم البرغوث مٌسطح للغاية، مما يجعله سهل المرور على أصغر المساحات غير المتوقعة في منزلك، وعادة ما يكون للبراغيث ون أسود أو لون بني محمر في بعض الأنواع، وتتغذى البراغيث على دم المضيف، وهي تقوم بلدغ القطط والكلاب والبشر أيضاً وتتسبب اللدغة في شعور بالحكة.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>دورة حياة البراغيث</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">من المهم التعرف على دورة حياة البراغيث وفهمها عندما تحاول القضاء على وجودها في منزلك، فهناك أربع مراحل تمر بها البراغيث خلال دورة حياتها وهي البيض واليرقة والخادرة والبراغيث البالغة وذلك اعتمادًا على درجة الحرارة والرطوبة البيئية، وتستغرق دورة الحياة الكلية من أسبوعين إلى عدة أشهر في الظروف المثلى للبراغيث التي تتراوح درجة حرارتها بين 70-85 درجة فهرنهايت و 70٪ رطوبة.</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align:justify\">بيض البراغيث:عندما تضع الأنثى البالغة البيض بعد وجبة دم من المضيف، يكون عبارة عن كائنات صغيرة بيضاء (أصغر قليلاً من حبة الرمل) توجد في فراء الحيوان الأليف في عناقيد، ويمكن للإناث البالغة وضع حوالي 40 بيضة يوميًا، ويستغرق البيض من يومين إلى أسبوعين حتى يتطور ويفقس وإذا كانت درجات الحرارة باردة وجافة، فإن البيض سيستغرق وقتًا أطول.</li>\r\n	<li style=\"text-align:justify\">يرقات البراغيث: تكون اليرقات الناشئة عمياء وتتجنب التعرض للضوء، وتتطور على مدى عدة أسابيع عن طريق تناول الدم المهضوم، ويصل طولها إلى بوصة ونصف وتكون بيضاء أو شبه شفافة وبدون أرجل.</li>\r\n	<li style=\"text-align:justify\">الشرانق: تمثل مرحلة الشرانق في دورة حياة البراغيث حوالي 10 بالمائة من سكان البراغيث في المنزل، وهي المرحلة التي تسبق ظهور البراغيث البالغة وتحمي الشرنقة الشرانق لعدة أيام أو أسابيع قبل ظهور البراغيث البالغة، وتحتوي على طبقة خارجية لزجة تسمح لها بالاختباء عميقًا في السجادة ولا يمكن إزالتها بسهولة عن طريق الكنس.</li>\r\n	<li style=\"text-align:justify\">البراغيث البالغة: بمجرد خروج البراغيث من الشرنقة ، ستحتاج إلى البدء بالتغذية من المضيف في غضون ساعات قليلة وبعد فترة وجيزة من الوجبة الأولى&nbsp; تتكاثر البراغيث البالغة وتبدأ في وضع البيض في غضون بضعة أيام، ويكون لها مظهر مسطح وهي صغيرة جدًا وغامقة اللون ومع الوقت والتغذية تصبح أكبر وأخف في اللون ويمكنها العيش في أي مكان من أسبوعين إلى عدة أشهر على الحيوان المضيف.</li>\r\n</ul>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>هل البراغيث خطرة على البشر؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تتسبب البراغيث في شعور قوي بالحكة وعلامات حمراء وبارزة على الجلد وهذا أمر مزعج للكثير منا، ويمكنهم نقل حشرة الدودة الشريطية للحيوانات المنزلية من الكلاب والقطط ومن المعروف أنهم ينقلون الطاعون والتيفوس من الفئران أيضا، ولذلك إذ كان لديك حيوان أليف، وبالتحديد كلب أو قطة، فمن المحتمل أنك تعرضت للإصابة بالبراغيث التي لا تجعل من السهل عليك مواصلة حياتك بشكل طبيعي حيث يجب عليك فور اكتشاف البراغيث القيام برش وغسل وتطهير كل شيء في منزلك لمجرد التخلص منه كما يمكنك رش السجاد أو حيواناتك الأليفة أو الغرف بأكملها داخل منزلك وغسل ملابسك وأسًرة حيواناتك الأليفة وحتى حيواناتك الأليفة نفسها في محاولة للتخلص من مشكلة البراغيث.</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">كما يمكن للبراغيث&nbsp; أن تشكل خطراً على الصحة بالنسبة لك ولحيواناتك الأليفة، وعندما يعضون البشر يمكنهم نقل العديد من الأمراض مثل حمى خدش القط التي تنتقل من خلال لدغة أو خدش أو ملامسة لعابهم في مناطق معينة، كما يوجد أكثر من 22 نوعًا محددًا من البكتيريا من جنس بارتونيلا يمكن أن تؤثر على الكلاب والقطط، ويمكن أن تسبب التيفويد، والتي يمكن أن تسببها بكتيريا Rickettsia felis&nbsp; وحينما تصيب البشر تسبب لهم ضعف شديد ولكنها لن تؤثر على صحة قطتك أو كلبك بنفس الطريقة، ويمكن&nbsp; أن تعرض البشر لقشعريرة الحمى أو ألم في الجسم أو غثيان أو قيء أو طفح، ومن المحتمل أن يعاني حيوانك الأليف من حمى خفيفة لبضعة أيام نتيجة لذلك.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>ما هي علامات الإصابة بالبراغيث؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">أول إشارة للإصابة بالبراغيث هي قيام حيواناتك بالخدش المستمر لفروها، ومن هنا يجب القيام بفحص الحيوان والبحث عن حشرات صغيرة تتحرك فوق جلده، وفي معظم الوقت تصيب البراغيث القط أو الكلب، مما يتسبب في نقلها لمنزلك، حيث يمكنها أن تقفز حتى 6 بوصات في كل مرة، مما يجعل من السهل جدًا عليها الدخول إلى منزلك بحثًا عن الطعام والدفء&nbsp;ويٌمكن أن تُصاب الحيوانات الأليفة بالتهاب الجلد التحسسي الناجم عن البراغيث، والذي يصاحبه غالبًا تساقط الشعر والجروح على الجلد، وعندما يتغذى عدد كبير من البراغيث على حيوانك الأليف، فقد يصاب بفقر الدم وهذا يمكن أن يؤدي أيضا إلى مزيد من المشاكل الصحية له، وعلى الرغم من ندرة الطاعون فلا يزال يشكل تهديداً على الحيوانات الأليفة وأصحابها لأن البراغيث ناقلة لهذا المرض.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>كيف أتخلص من الإصابة بالبراغيث؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">من الصعب التخلص من البراغيث ببساطة حتى لو قمت بمعالجة حيوانك الأليف، حيث يمكنها أن تعيش في السجاد والأثاث، وقد يساعد استخدام المكنسة الكهربائية في التخلص من البراغيث، ولكن غالباً ما يتم ربط البيض واليرقات والشرانق بإحكام في ألياف السجاد ولن يتم التخلص منها عن طريق التنظيف بالمكنسة الكهربائية بانتظام، ولذلك إذا كنت تواجه مشكلات في التحكم في البراغيث في منزلك فلا بد من طلب المساعدة المهنية من شركة مكافحة الحشرات بجدة، ولحين طلب المساعدة المتخصصة عليك القيام بما يلي:</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align:justify\">عرض حيوانك على طبيب بيطري متخصص فوراً لوصف العلاج المناسب للحالة.</li>\r\n	<li style=\"text-align:justify\">&nbsp;التأكد من امتلاك حيواناتك الأليفة لمنتجات البراغيث التي يصفها الطبيب البيطري كخطوة أولى جيدة لحماية حيواناتك الأليفة ونفسك.</li>\r\n	<li style=\"text-align:justify\">تنطيف المنزل باستمرار بالمكنسة الكهربائية وتمريرها على السجاد والمناطق التي يعيش بها الحيوان الأليف للتخلص من أكبر عدد ممكن من البراغيث.</li>\r\n	<li style=\"text-align:justify\">مسح الأرضيات وتطيرها بمطهرات قوية للقضاء على بيض البراغيث.</li>\r\n	<li style=\"text-align:justify\">غسل السجاد المصاب بالبراغيث لقتل البيض والبراغيث العالقة في الألياف.</li>\r\n	<li style=\"text-align:justify\">تغيير الملاءات في حجرات النوم وكذلك أغطية الحيوانات الأليفة وتنظيف منزلها.</li>\r\n</ul>', '<p dir=\"ltr\">If you have a flea infestation in your home, the first thing that you need to ask is &quot;How did these fleas enter my home?&quot; Perhaps homeowners find the answer to this question by examining their pets, there is a close relationship between fleas and pets, especially cats, because they are the first host of these insects, fleas do not jump to our homes, they are transmitted to us through animals with their fur, Pet owners are more likely to catch fleas than those without pets, especially if they allow their pets to go outside for long periods.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Fleas identification</strong></h2>\r\n\r\n<p dir=\"ltr\">Adult fleas have no wings and their length is about 1/8inches flea body is very flat, which makes it easy to pass through the smallest spaces in the house, it has a black color or a reddish brown color, fleas feed on the&nbsp; host blood.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Fleas life cycle</strong></h2>\r\n\r\n<p dir=\"ltr\">First, it is very important to recognize and understand the life cycle of fleas when trying to eliminate the infestation completely. There are four stages in the life cycle of fleas, which are eggs, larva, pupa and adult fleas, depending on the temperature and environmental humidity. The total life cycle takes from two weeks to several months in optimum conditions for fleas with temperatures ranging from 70-85 degrees Fahrenheit to 70% humidity.</p>\r\n\r\n<ul dir=\"ltr\">\r\n	<li>Flea eggs: When an adult female lays eggs after a blood meal from the host, it is small white organisms (smaller than a grain of sand) that are found in pet fur in clusters, adult females can lay about 40 eggs per day, eggs take from two days to Two weeks until it develops and hatches and if the temperatures are cold and dry, the eggs will take longer time.</li>\r\n	<li>Flea larvae: The emerging larvae are blind, so they avoid exposure to light. They develop over several weeks by eating digested blood. They reach one and a half inches long and are white or semi-transparent and without legs.</li>\r\n	<li>Cocoons: The cocoon stage in the flea life cycle represents about 10 percent of the flea population at home, the stage that precedes the emergence of adult fleas and protects the cocoon for days or weeks before the appearance of adult fleas, it contains a sticky outer layer that allows them to hide deeply in the carpet, so it cannot be removed easily by vacuuming.</li>\r\n	<li>Adult fleas: Once the fleas get out of the cocoon,&nbsp; they need to start feeding from the host within a few hours and shortly after the first meal the adult flea multiplies and starts laying eggs within a few days, has a flat appearance, dark color and with time and feeding They become larger and lighter in color and can live from two weeks to several months on the host animal.</li>\r\n</ul>\r\n\r\n<h2 dir=\"ltr\"><strong>Are fleas dangerous for humans?</strong></h2>\r\n\r\n<p dir=\"ltr\">Fleas often leave a red bump and a strong itchy feeling, which is very annoying to animals, they can spread to dogs and cats and they are known to transmit typhus from mice, so if you have a pet, specifically a dog or cat, you may have been infected. if you have dealt with flea infestation, it does not make it easy for you to continue your life, and it will require you to spray, wash, and disinfect everything in your home just to get rid of it. wash your clothes and even your pets in an attempt to get rid of the flea problem.</p>\r\n\r\n<p dir=\"ltr\">&nbsp;fleas can also threat your health and your pets, and when they bite humans they can transmit many diseases such as fever caused by Bartonella Henselae, and it can be transmitted from fleas to cats and then from cats to people through a bite, scratch or It touches their saliva in certain areas, and there are more than 22 specific bacteria of the Bartonella genus that can affect dogs and cats, and can cause typhoid, which can be caused by Rickettsia felis and when it infects humans it causes them severe weakness but it will not affect your cat&#39;s health Or your dog in the same way, you can display the b An evil for chills, fever, body pain, nausea, vomiting, or a rash, and your pet may have a mild fever for a few days as a result.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>What are the signs of flea infection?</strong></h2>\r\n\r\n<p dir=\"ltr\">If you have pets at home, this will be the first sign of flea infestation, just watch your dog or cat for constant scratching, because this is the first sign of fleas, check the animal and look for small bugs that move over its skin, in most of the Time fleas infect a cat or dog, causing it to move to your home, as it can jump up to 6 inches at a time, making it very easy for it to enter your home in search of food and warmth.</p>\r\n\r\n<p dir=\"ltr\">Pets can develop flea-allergic dermatitis, which is often accompanied by hair loss and cuts to the skin, and when a large number of fleas feed on your pet, they may develop anemia and this can also lead to more health problems for them, although The scarcity of plague is still a threat to pets and their owners because fleas are a vector of this disease.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>How do I get rid of a flea infection?</strong></h2>\r\n\r\n<ul dir=\"ltr\">\r\n	<li>Take your pet to a veterinarian immediately to prescribe appropriate treatment for the condition.</li>\r\n	<li>Make sure your pets have the flea products that your veterinarian prescribes as a first step to protect your pets and yourself.</li>\r\n	<li>&nbsp;clean the house and vacuum especially the carpets and areas where the pet lives to get rid of the largest number of fleas possible.</li>\r\n	<li>Wipe floors with strong disinfectants to eliminate flea eggs.</li>\r\n	<li>Wash flea-infected carpets to kill eggs and flea stuck in the fibers.</li>\r\n	<li>Changing bed linen as well as pets bedding and clean their house</li>\r\n</ul>', 8, 1, 0, '2020-05-06 04:01:41', '2020-05-08 06:04:06', 'البراغيث', 'Fleas', 'حشرة البراغيث : معلومات وحقائق وكيفية التخلص من البراغيث', NULL, 'البراغيث', 'Fleas', 'Fleas & Flea Control: Get Rid of Fleas in Homes', 'If you have a flea infestation in your home, the first thing that you need to ask is \"How did these fleas enter my home?\" Perhaps homeowners find the answer to this question by examining their pets, there is a close relationship between fleas and pets, especially cats, because they are the first host of these insects, fleas do not jump to our homes, they are transmitted to us through animals with their fur, Pet owners are more likely to catch fleas than those without pets, especially if they allow their pets to go outside for long periods.'),
(12, 'الصراصير', 'Cockroaches', 'uploads/pest_libraries/1589097412Cockroaches.jpg', 'يشكو الكثير من أصحاب المنازل أن مهاجمة الصراصير لبيوتهم سرعان ما يتحول إلى كابوس مٌزعج بسبب التلوث الذي تسببه، وشكلها المرعب والمقزز للكتيرين، والأهم هو الأعداد الكبيرة التي تتكاثر بها، وعلى الرغم من أن جميع الصراصير قد تبدو متشابهة في الشكل لأصحاب المنازل، حيث يٌوجد أكثر من 4500 نوع من الصراصير في العالم وهي تٌعرف بالاسم العلمي Blattodea', 'Cockroaches', '<p dir=\"rtl\" style=\"text-align:justify\">يشكو الكثير من أصحاب المنازل أن مهاجمة الصراصير لبيوتهم سرعان ما يتحول إلى كابوس مٌزعج بسبب التلوث الذي تسببه، وشكلها المرعب والمقزز للكتيرين، والأهم هو الأعداد الكبيرة التي تتكاثر بها، وعلى الرغم من أن جميع الصراصير قد تبدو متشابهة في الشكل لأصحاب المنازل، حيث يٌوجد أكثر من 4500 نوع من الصراصير في العالم وهي تٌعرف بالاسم العلمي Blattodea، وعلى الأغلب لا يهتم أصحاب المنازل إلا بمعرفة الأنواع التي تدخل بيوتهم والتي تكون حوالي خمسة أو ستة أنواع من الصراصير فقط وهي تدخل المنازل بحثاًً عن الطعام والمأوى والرطوبة، كما يمكنها أن تتواجد في أي مكان بمنزلك وتقوم بنشر الفوضى والتلوث في كل مكان نظرا لما تحمله من مسببات المرض التي تضر بأفراد أسرتك، ويفيد معرفة كيفية التفريق بين أنواع الصراصير، ومعرفة دورة حياتها ستساعدك في التخلص منها ، وفيما يلي سوف نتحدث عن الصراصير والمظهر الخارجي لها ودورة حياتها وكذلك كيفية القضاء عليها بطرق ناجحة.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>المظهر الخارجي للصراصير</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">الصراصير تتميز بلون بني أو أسود، ويتراوح طولها من 0.07 بوصة إلى 3 بوصات وفقا لنوعها، وهي تملك قرون استشعار طويلة، وسيقان رفيعة وطويلة، وتكون أرجلها الستة مزودة بشعر صغير، وقد يكون للصراصير البالغة أجنحة مطوية على ظهورها، لكن ليس كل صرصور يمكنه الطيران، ، وهذا يتوقف على نوعها، وفيما يلي سوف نتحدث عن المظهر الخارجي لأشهر أنواع الصراصير المنزلية.</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align:justify\">الصراصير الألمانية: تٌعتبر أكثر أنواع الصراصير شيوعًا، وهي سريعة التكاثر ولذلك تمثل تهديدًا مباشرًا للمنزل ولأصحابه، فهي يمكنها أن تنتج أكثر من 30000 صرصور في العام، كما أنها تقوم بوضع بين 20 و 40 من الصراصير الصغيرة داخل كيس البيض، وتحمل الأنثى البالغة البيض معها حتى تصبح جاهزة للفقس، ويصل طولها 0.6 بوصة، ويكون لها لون بني فاتح، وخطان مظلمان متوازيان من الرأس إلى الأجنحة، وهي تعيش بالقرب من مصادر المياه والغذاء مثل المطابخ ومناطق التخزين والحمامات، وهي بالكاد تتمكن من الطيران، وتدخل المنزل من خلال الملابس والحقائب والأثاث المستعمل وهي تنتشر بسهولة عبر الجدران.</li>\r\n	<li style=\"text-align:justify\">الصراصير البنية المخططة: يٌفضل هذا النوع مساحات أكثر دفئًا وجفافًا، خاصة في الأماكن المرتفعة وداخل الإلكترونيات والتلفزيون والثلاجة وتميل إلى الابتعاد عن الماء، ويصل طولها 0.5 بوصة وهي أصغر من الصراصير الأمريكية، ويكون لونها بني داكن يميل إلى الذهبي نحو الجناح، ويكون للإناث أجنحة بنية محمرة، وهي تعيش في المناطق الداخلية الدافئة والجافة والسقوف ووراء إطارات الصور والساعات وفي الخشب المجوف وداخل الأجهزة الكهربائية، ويمكن للذكور فقط أن تطير، ويتم نقلها عادة عبر الملابس والأثاث المستعمل.</li>\r\n	<li style=\"text-align:justify\">الصراصير الأمريكية: يتميز بحجمه الأكبر على الإطلاق بين بقية الصراصير، وطول الفترة التي يعيشها حيث يمكن أن يحيا لفترة تصل إلى عامين، ويكون أطول بكثير من أنواع الصراصير الأخرى، ويصل طوله من 1 إلى 3 بوصات، ويكون له لون بني إلى البني محمر وحواف صفراء اللون، وهو يٌفضل الهواء الطلق والمناطق الدافئة والرطبة والمجاري والصرف الصحي والمطابخ وغرف الغسيل والطوابق السفلية والحمامات، ويمكن لكل من الذكور والإناث البالغة أن تطير، وهي تدخل المنازل من الأبواب، ونوافذ الطابق السفلي، والكراجات والصرف الصحي</li>\r\n</ul>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>دورة حياة الصراصير</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تتمتع جميع أنواع الصراصير بثلاث مراحل مميزة تمر بها خلال دورة حياتها بدءا من البيضة إلى الحورية وصولا إلى الحشرة البالغة، ويمكن للصراصير أن تعيش الصراصير ما بين 6 إلى 15 شهرًا، ويختلف عمر الصراصير على حسب النوع وكذلك الرطوبة ودرجة الحرارة، وفيما يلي سوف نتطرق إلى كل فترة من فترات دورة حياة الصراصير بالتفصيل.</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align:justify\">البيض: تبدأ دورة حياة حياة الصراصير مع البيضة، حيث تضع الإناث من 10 إلى 50 بيضة في المناطق الدافئة والرطبة وتقوم بوضع البيض في مناطق معزولة في كبسولات لحمايتها وتتركها تحت المواقد، والمصارف، والأجهزة وفي الحمامات والمخازن، حيث تقوم بلصقها على الأسقف والجدران والأثاث باللعاب، وتستغرق الصراصير حوالي شهر إلى شهرين حتى تفقس وعلى الرغم من أن مرحلة البيض يمكن أن تستمر من 14 إلى 100 يومًا اعتمادًا على الأنواع والمناخ وبشكل عام ، كلما ارتفعت درجة الحرارة، زادت سرعة الصراصير، ويمكن للصرصور تاواحد إنتاج أكثر من 300 بيضة في السنة.</li>\r\n	<li style=\"text-align:justify\">الحوريات: هي المرحلة التالية من دورة حياة الصراصير، وذلك بعد فقس البيض، وفي كل مرة تتخلص من جلودها، ويكون لها أجسام بيضاء ناعمة وتغير لونها إلى اللون البني أو الأسمر أو الرمادي حتى تصل إلى اللون الحجم الكامل للحشرة البالغة، ويتم العثور عليها حول المنازل أو المطاعم أو متاجر البقالة وتستمر مرحلة الحورية حوالي شهر إلى ثلاثة أشهر.</li>\r\n	<li style=\"text-align:justify\">الصراصير البالغة: تدخل الحوريات مرحلة البلوغ وتكرر نفس دورة الحياة.</li>\r\n</ul>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>مكان تواجد الصراصير في المنزل&nbsp;؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">الصراصير حشرات ليلية وهي تفضل العيش والتغذية في الظلام، لذلك فإن الصراصير التي تظهر خلال اليوم هي علامة محتملة على الإصابة، وتفضل الصراصير الأماكن المظلمة والرطبة للاختباء والتكاثر، ويمكن العثور عليها خلف الثلاجات والمصارف والمواقد وداخل المحركات والأجهزة الرئيسية كما يمكنها تسطيح أجسادها لتناسب المساحات الضيقة، ويمكن أيضًا العثور على الصراصير خلف ورق الحائط وداخل شقوق الجدار.</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align:justify\">علامات الصراصير في المنازل</li>\r\n	<li style=\"text-align:justify\">الصراصير: رؤية الصراصير في المنازل وفي أماكن الاختباء المختلفة كما يمكن العثور على الصراصير الميتة في جميع أنحاء المنزل.</li>\r\n	<li style=\"text-align:justify\">البراز: العثور على براز الصراصير أثناء الإصابة حيث تنتج الصراصير الصغيرة برازًا يشبه القهوة أو الفلفل الأسود.</li>\r\n	<li style=\"text-align:justify\">الرائحة: حيث أن الروائح الكريهة حيث أن بعض الأنواع تنبعث منها رائحة كريهة وفي حالة الإصابة الخطيرة، قد توجد رائحة زيتية أو عفن قوية.</li>\r\n	<li style=\"text-align:justify\">بيض الصراصير:العثور على بيض الصراصير خلف الأثاث وفي الأماكن المخفية.</li>\r\n</ul>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>أضرار الصراصير</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تتواجد الصراصير في الأماكن المظلمة والغير نظيفة ولذلك تقوم بحمل مسببات المرض على أقدامها وتنشرها في كل مكان تسير به داخل منزلك، ولأن الصراصير سريعة الحركة وتتنقل من مكان للثاني تقوم بتلويث الأسطح التي تمر عليها سواء في المطبخ أو الحمام كما أنها تلوث الأطعمة التي تمر فوقها وقد تسبب حالات التسمم ومشاكل الجهاز الهضمي والسالمونيلا، ويٌعرف عن الصراصير أنها تتسبب في إصابة كبار السن والأطفال بالحساسية.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>كيفية التخلص من الصراصير</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">إذا كنت تعتقد بوجود الصراصير داخل منزلك، فاتصل بأخصائي مكافحة الحشرات بشركة أوركيدا بجدة، كما يمكن التخلص من الصراصير والوقاية من إصابتها من خلال:</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align:justify\">التخلص من بقايا الطعام المنتشرة في كل مكان وتجذب الصراصير لمنزلك</li>\r\n	<li style=\"text-align:justify\">إصلاح مشاكل الرطوبة في منزلك.</li>\r\n	<li style=\"text-align:justify\">تجفيف الأرضيات بإستمرار لقطع مصادر الماء عن الصراصير.</li>\r\n	<li style=\"text-align:justify\">تنظيف المنزل وتطهير الأسطح بمطهرات قوية.</li>\r\n	<li style=\"text-align:justify\">استخدام المبيدات الحشرية ورشها مباشرة فوق الصراصير.</li>\r\n	<li style=\"text-align:justify\">رش تراب الدياتومي في المطبخ وأماكن تواجد الصراصير.</li>\r\n	<li style=\"text-align:justify\">استخدام طعم الصراصير المحلي الذي يباع في المتاجر والصيدليات للتخلص من الصراصير.</li>\r\n	<li style=\"text-align:justify\">استخدام المواد الطبيعية الطاردة للصراصير مثل زيت النيم والنعناع البري ويمكن رشه الزيت حول المنزل لمنع دخول الصراصير.</li>\r\n</ul>', '<p dir=\"ltr\">There are more than 4,500 species of cockroaches in the world, they are known as Blattodea, you only have to worry about five or six different types of cockroaches, cockroaches invade human homes to search for food, shelter, and water, they can be found anywhere in your home, they also spread chaos and pollution everywhere due to the pathogens they carry that harm your family members.</p>\r\n\r\n<p dir=\"ltr\">Cockroaches invasion turns into an annoying nightmare because they multiply in very large numbers, although all cockroaches may seem similar in appearance to homeowners, knowing how to differentiate between them and their life cycle will help you get rid of them, in this article we will talk about cockroaches and their external appearance and how to them successfully.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Cockroaches identification</strong></h2>\r\n\r\n<p dir=\"ltr\">Cockroaches have long antennas and long legs, their six legs are equipped with small hair, adult cockroaches have wings folded on their backs, not every cockroach can fly, most of them are brown or black and can range from 0.07 inches to 3 inches in length, depending on their kind</p>\r\n\r\n<ul>\r\n	<li>German cockroaches: the most common types of cockroaches, they have a high reproduction rate, they can produce more than 30,000 cockroaches per year. They place between 20 and 40 small cockroaches inside an egg&nbsp; otheca, the adult female carries eggs with them until they are ready For hatching, it reaches 0.6 inches in length, has a light brown color, two dark lines parallel from head to wings,&nbsp; they live near water and food sources such as kitchens, storage areas and bathrooms, they can barely fly, and enter the house through clothes, bags and used furniture as it spreads Easily across the walls.</li>\r\n	<li>Striped brown cockroaches: German and brown cockroaches live in the same house, but they rarely hide in the same places, where brown cockroaches prefer warmer and drier spaces, especially in high places and inside electronics, television and fridges and tend to get away from the water, 0.5 inches in length and are smaller Of American cockroaches, and the color is dark brown tends to golden towards the wing, and the females have reddish brown wings, and they live in warm and dry interior areas and ceilings and behind picture frames and clocks and in hollow wood and inside electrical devices, and only males can fly, and are male They are usually transported through used clothes and furniture</li>\r\n	<li>&nbsp;American cockroaches: American cockroach is the largest type of them, it can stay for up to two years, which is much longer than other types of cockroaches, and its length ranges from 1 to 3 inches, it has a brown to reddish brown color and yellow edges, which is preferable to the outdoors and warm areas Humid, sewage, sanitation, kitchens, laundry rooms, basements, and bathrooms, and both adult males and females can fly, entering homes through doors, basement windows, garages and sanitation</li>\r\n</ul>\r\n\r\n<h2 dir=\"ltr\"><strong>Cockroaches life cycle</strong></h2>\r\n\r\n<p dir=\"ltr\">All cockroaches go through three stages of life, which are eggs, nymphs and adults, cockroaches usually live about 6 to 15 months, and the life of cockroaches varies depending on the type as well as humidity and temperature, and understanding the life cycle of cockroaches can help you get rid of the infection before it turns into a serious problem .</p>\r\n\r\n<ul>\r\n	<li>&bull; Egg: The life cycle of cockroaches begins with the egg, females lay 10 to 50 eggs in warm and humid areas and place eggs in isolated areas in capsules to protect them and leave them under stoves, drains, appliances and in bathrooms and warehouses, ceilings, walls and furniture, cockroaches take about one to two months to hatch and although the egg stage can last from 14 to 100 days depending on the species and climate and in general, the higher the temperature, the faster the cockroaches, and one cockroach can produce more than 300 eggs per year .</li>\r\n	<li>&bull; Nymph: It is the next stage in the life cycle of cockroaches, after hatching eggs, and every time they get rid of their skins, they have soft white bodies and change their color to brown, brown or gray until they reach the color the full size of the adult insect, and it is found around Homes, restaurants, or grocery stores, and the mermaid stage lasts about one to three months.</li>\r\n	<li>Adult cockroaches: nymphs enter adulthood and multiply to start a new cycle.</li>\r\n</ul>\r\n\r\n<h2><strong>Where to find cockroaches in your home?</strong></h2>\r\n\r\n<p dir=\"ltr\">Cockroaches are nocturnal insects, they prefer to live and feed in the dark. Therefore, cockroaches that appear during the day are a possible sign of infection. Cockroaches prefer dark and humid places to hide and reproduce. They can be found behind refrigerators, drains, stoves, inside the main engines and devices, and they can flatten their bodies to accommodate narrow spaces. Cockroaches can also be found behind wallpaper and inside wall cracks.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Signs of cockroaches in homes</strong></h2>\r\n\r\n<p dir=\"ltr\">Seeing cockroaches in homes and in different hiding places. Dead cockroaches can also be found all over the house.</p>\r\n\r\n<ul dir=\"ltr\">\r\n	<li>&bull; Finding cockroach stools which is similar to coffee or black pepper.</li>\r\n	<li>&bull; Unpleasant odors, as some species emit an unpleasant smell, and in the event of serious injury, there may be a strong oil or mild odor.</li>\r\n	<li>&bull; Find cockroach eggs, behind furniture, and in hidden locations.</li>\r\n</ul>\r\n\r\n<h2 dir=\"ltr\"><strong>Cockroach dangers to humans</strong></h2>\r\n\r\n<p dir=\"ltr\">Cockroaches exists in dark and unclean places, they carry pathogens on their feet and spread them everywhere they go inside your home, because cockroaches are fast-moving and move from place to another, they pollute the surfaces that pass by them, whether in the kitchen or bathroom, as they contaminate foods and other surfaces.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>How to get rid of cockroaches</strong></h2>\r\n\r\n<p dir=\"ltr\">If you think cockroaches are inside your home, contact a specialist at pest control company in Jeddah. Cockroaches can also be eliminated by these steps:</p>\r\n\r\n<p dir=\"ltr\">Get rid of scattered food debris that attract cockroaches to your home</p>\r\n\r\n<ul>\r\n	<li>Fix moisture problems in your home.</li>\r\n	<li>dry floors to cut off water sources.</li>\r\n	<li>clean your house surfaces with strong disinfectants.</li>\r\n	<li>Use pesticides and spray them directly on cockroaches.</li>\r\n	<li>Spray diatomaceous earth in the kitchen and where the cockroaches are.</li>\r\n	<li>Use natural repellant of cockroaches, such as neem oil and catnip.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>', 2, 1, 0, '2020-05-06 04:16:02', '2020-05-09 23:56:52', 'الصراصير', 'Cockroaches', 'حشرة الصراصير : معلومات وحقائق وكيفية التخلص من حشرة الصراصير', NULL, 'الصراصير', 'Cockroaches', 'Cockroaches | Facts & Identification, Control & Prevention', 'There are more than 4,500 species of cockroaches in the world, they are known as Blattodea, you only have to worry about five or six different types of cockroaches, cockroaches invade human homes to search for food, shelter, and water, they can be found anywhere in your home, they also spread chaos and pollution everywhere due to the pathogens they carry that harm your family members.'),
(13, 'الخنافس', 'Beetles', 'uploads/pest_libraries/1588920960e96c0333f0b078a144b50abd606f1053image.png', 'هل سبق أن رأيت خنفساً في حديقة منزلك أو داخل بيتك من قبل؟ قد لا يحدث أن نرى الخنافس إلا في فترات معينة وفي مناطق معينة على الرغم من النسبة الكبيرة التي تمثلها بين الحشرات والتي تقترب من 40% من نسبة الحشرات مجتمعة، كما يوجد قرابة الـ 360 ألف نوع اكتشفه العلماء حتى الآن، ويٌطلق على الخنافس Coleoptera كما يوجد لكثير  من الأشكال والألوان لها، فالبعض له ألوان رائعة، أو أشكال مبهجة تجذب الانتباه لها، وهناك العديد من الأسباب المختلفة التي تدفعنا للحديث عنها بما في ذلك أهميتها الاقتصادية وحجمها وكثرة عددها ومظهرها وعاداتها الرائعة، وتتمثل القاعدة الأولى عند التعامل مع الإصابة بأي حشرة، هي معرفة بعض المعلومات الأساسية عنها، مثل شكل الحشرة وكذلك دورة حياتها وعاداتها المختلفة وكيفية التخلص منها، كل هذا وأكثر سوف تتعرف عليه من خلال هذا المقال.', 'الخنافس', '<p dir=\"rtl\" style=\"text-align:justify\">هل سبق أن رأيت خنفساً في حديقة منزلك أو داخل بيتك من قبل؟ قد لا يحدث أن نرى الخنافس إلا في فترات معينة وفي مناطق معينة على الرغم من النسبة الكبيرة التي تمثلها بين الحشرات والتي تقترب من 40% من نسبة الحشرات مجتمعة، كما يوجد قرابة الـ 360 ألف نوع اكتشفه العلماء حتى الآن، ويٌطلق على الخنافس Coleoptera كما يوجد لكثير&nbsp; من الأشكال والألوان لها، فالبعض له ألوان رائعة، أو أشكال مبهجة تجذب الانتباه لها، وهناك العديد من الأسباب المختلفة التي تدفعنا للحديث عنها بما في ذلك أهميتها الاقتصادية وحجمها وكثرة عددها ومظهرها وعاداتها الرائعة، وتتمثل القاعدة الأولى عند التعامل مع الإصابة بأي حشرة، هي معرفة بعض المعلومات الأساسية عنها، مثل شكل الحشرة وكذلك دورة حياتها وعاداتها المختلفة وكيفية التخلص منها، كل هذا وأكثر سوف تتعرف عليه من خلال هذا المقال.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>المظهر الخارجي للخنافس</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تختلف الخنافس في الحجم الذي يتراوح بين جزء من الملليمتر إلى حوالي 8 بوصات في الطول، كما يتنوع شكل جسم الخنافس البالغة وفقا لنوعها، فالبعض يكون له جسم بيضاوي مٌسطح منتظم، ويكون له قرون استشعار وساقين لها طول معتدل ونحيف مع فم مصمم للعض والمضغ، ويكون الجانب السفلي لمعظم الخنافس&nbsp; بيضاوي وأملس ومٌسطح ويكون لها قرون استشعار إما قصيرة أو رفيعة جدًا، وتكون الساقان الأماميتان قصيرتان وتستخدم شعرها كمجاديف للسباحة، وللخنافس جسم صلب تستخدمه كدرع لها يقيها من العوامل الخارجية، ولبعض الخنافس أجنحة خلفية تُستخدم في الطيران وفي أغلب الأنواع تكون مطوية ولا تستخدمها.</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">وٌيمكن التعرف على بعض أنواع الخنافس بواسطة زوجي الأجنحة، ويكون للزوج الأمامي أغطية قرنية تٌخفي الزوج الخلفي وتجتمع أسفل الظهر في خط مستقيم، وتصدر معظم الخنافس الكبيرة ضوضاء عالية وتنجذب إلى الضوء في الليل، والبعض منها يتكيف مع البيئة المائية والبعض منها يعيش في ارتباط مع النمل، وليرقات الخنافس مجموعة متنوعة من الأشكال والأحجام حسب المكان الذي تعيش فيه والطعام الذي تأكله، ويكون لليرقات بشكل عام كبسولة رأس محددة جيدًا وتكون شديدة الصلابة ولديها هوائيات قصيرة وقطعة فم ماضغة وقد تكون الساقين موجودة أو غائبة، وغالبًا ما يتم الخلط بين الخنافس والصراصير ولكن يمكن تمييزها من خلال الأجنحة الأمامية التي يتم تعديلها وتلتقي في خط مستقيم لأسفل الظهر.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>دورة حياة الخنافس</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تستغرق عملية تطور الخنافس خلال دورة حياتها من بضعة أسابيع إلى عدة سنوات في بعض الأنواع، حيث تقوم الأنثى بوضع البيض بالقرب من مصدر الغذاء سواء في التربة أو على سطح بعض النباتات، ويعتمد عدد البيض الذي يتم وضعه على نوع الخنفسة وقد يتراوح بين واحدة أو اثنين إلى مئات البيضات، وبعد الفقس تظهر اليرقات والتي تكون صغيرة في الحجم وتتطور اليرقات من خلال مجموعة من الأطوار أو مراحل النمو والتي تتراوح من 3 إلى 5 مراحل قبل أن تصل في النهاية إلى الشكل النهائي للخنافس البالغة وذلك وفقا لنوع كل حشرة، ثم تبدأ الخنفسة في تكرار دورة الحياة من جديد مرة أخرى من خلال التكاثر وإنتاج أجيال جديدة من الخنافس.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>غذاء الخنافس</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تٌلقب الخنافس عموما بالحشرات العٌشبية، حيث إن غالبية الخنافس تتغذى على النباتات بشكل أو بآخر مثل الرحيق وأوراق الأشجار والأخشاب والبذور واللحاء والبعض قد يتغذى على الأخشاب المتعفنة والروث والفطريات أو فضلات الأوراق، وبعض الأنواع أيضا تكون مفترسة حيث تتغذى على اليرقات وعلى الخنافس البالغة الأضعف أو الأصغر حجما.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>أين تعيش الخنافس؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تعيش بعض أنواع الخنافس بالقرب من المياه العذبة ويمكن العثور على بعضها في البيئات البحرية، ويمكن العثور على بعض أنواع الخنافس في التربة بالقرب من فضلات الأوراق أو تحت لحاء الأشجار الميتة أو المتحللة أو تحت الحجارة وفي الروث والجيفة والفطريات، وتعيش بعض الأنواع في الكهوف بينما تعيش أنواع أخرى في أعشاش الفقاريات والبعض منها حشرات اجتماعية مثل النمل الأبيض تعيش في بيئات من صنع الإنسان مثل المنازل والبيوت والحدائق وأماكن تخزين الحبوب، وفي البيئات المائية، يمكن العثور على الخنافس في تجمعات مؤقتة أو بين تيارات المياه المالحة والشقق الطينية ويمكن العثور عليها في الرمال أو الحصى أو الطين عند حواف الجداول والبرك وعلى شاطئ البحر أو بين الصخور والشعاب المرجانية في منطقة المد.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>أضرار الخنافس على البشر</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">الكثير من الخنافس يضر بالحدائق والنباتات لأنه في حالة الإصابات الشديدة، تستهدف النباتات وتقوم بالتغذية عليها وتتلف الأوراق والأشجار والثمار، وقد تضر المحاصيل الزراعية في الحدائق والحقول، كما أنها تتغذى على جذور النباتات والبذور ويمكن أن توقف نمو بعض النباتات مما يضر البشر، وفي الحالات التي تصيب فيها الخنافس المنازل تتسبب في الكثير من الإزعاج والخوف لأصحاب المنازل مما يتطلب القضاء عليها فور اكتشافها قبل تفشي الإصابة بأعداد كبيرة.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>كيفية التخلص من الخنافس؟</strong></h2>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align:justify\">يمكن محاربة الخنافس في الحدائق من خلال دودة النيماتودا والتي يمكنها أن تتغذى على الخنافس في التربة وتخلصك منها فورا.</li>\r\n	<li style=\"text-align:justify\">رش المبيدات الحشرية المضادة للخنافس والتي يمكنها أن تقضى عليها فوراً.</li>\r\n	<li style=\"text-align:justify\">رش زيت النيم فوق الخنافس مباشرة كما يمكن رشه في المنزل كطارد للخنافس والكثير من الحشرات الأخرى الضارة</li>\r\n	<li style=\"text-align:justify\">رش الخنافس بسائل الماء والصابون الفعال في القضاء على الخنافس وغيرها من الحشرات الأخرى.</li>\r\n	<li style=\"text-align:justify\">تغطية النباتات بغطاء مناسب حتى لا تتسلل له الخنافس وتدمره.</li>\r\n	<li style=\"text-align:justify\">تطهير الأرضيات ورشها بمطهرات قوية.</li>\r\n	<li style=\"text-align:justify\">استخدام المكنسة الكهربائية للتخلص من الخنافس والبيض في السجاد والأرضيات.</li>\r\n	<li style=\"text-align:justify\">طلب المساعدة المهنية المتخصصة من شركة مكافحة الحشرات بجدة فوراً في حالة صعوبة السيطرة على الإصابة الحادة.</li>\r\n</ul>', '<p dir=\"ltr\">Beetles which has the scientific name called Coleoptera are one of the largest classes of insects in the world, they count about 40 percent of insects, they also have more than 360,000 species, they have many shapes and colors, some of them have wonderful metallic colors, cheerful and surprising shapes&nbsp;Beetles attract attention for many different reasons such as economic importance, size, abundance, appearance and wonderful habits, first of all when dealing with insect infestation, it is very important to know some basic information about it, the shape of the insect, its life cycle, its different habits and how to get rid of it.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Beetles identification</strong></h2>\r\n\r\n<p dir=\"ltr\">Beetles can usually be identified by the pair of wings, large beetles cause loud noises while flying, many of them are attracted to light at night, some of them live in a water environment, some of them live in association with ants, beetles differ greatly in size ranging from a part of a millimeter to more than 200 mm (about 8 inches) in length. Adult beetles body varies according to their type, some are primitive and have a flat oval and regular grooves, it has an antenna,&nbsp; slim Legs with&nbsp; mouth designed for biting and chewing, water beetles has an oval body, smooth and flat shape, antennae either short or very thin, the front legs are short they use their hair for swimming, the beetles have a solid body that you use as a shield and protects them from external factors, Some beetles have membranous wings that are used in flight and in other species they are folded and do not use them.</p>\r\n\r\n<p dir=\"ltr\">larvae have a variety of shapes and sizes depending on where they live and the food they eat. Larvae generally have a well-defined head capsule that is very hard and has short antennae and a chewing mouth piece. Legs may be present or absent, and beetles and cockroaches are often confused but can be distinguished Through the modified front wings and converge in a straight line to the lower back.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>life cycle of beetles</strong></h2>\r\n\r\n<p dir=\"ltr\">Beetles take a few weeks to several years during their life cycle, eggs are usually placed near the food source in the soil or on the host plant and this depends on the species, number of eggs that were laid depends on the type and may range from one or two to Hundreds of eggs, after hatching, the larvae develop through a series of commonly known stages of growth ranging from 3 to 5 stages before reaching the final shape of the adult insect.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>&nbsp;What do Beetles eat?</strong></h2>\r\n\r\n<p dir=\"ltr\">Beetles are generally called herbal insects, although some adult beetles do not feed at all, the majority feed on plants such as nectar, tree leaves, woods, seeds, some may feed on rotting woods, dung, fungi, or leaf litter,&nbsp; some species are predators,They feed on larvae and adult beetles.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Where do beetles live?</strong></h2>\r\n\r\n<p dir=\"ltr\">Many families of beetles live in fresh water, some types of beetles can be found in the soil near leaf or under the bark of dead or decomposing trees or under stones and fungi, some species live in caves while you live Other species in vertebrate nests, some of which are social insects such as termites that live in man-made environments such as homes, gardens, grain storage areas, and in water environments, beetles can be found in temporary pools or between saltwater currents and mud flats. To find them in the sand or gravel or mud at the edges of streams and ponds on the sea shore or between rocks and coral reefs in the tidal area.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Do beetles harm humans?</strong></h2>\r\n\r\n<p dir=\"ltr\">Many types of beetles harm gardens and plants in the case of severe injuries, they eat plants and damage leaves, trees and fruits, they may damage agricultural crops in gardens and fields, they feed on the roots of plants and seeds and can stop the growth of some plants, which harms humans, and in cases In which beetles infest the homes, they cause a lot of inconvenience and fear to homeowners, which requires their elimination as soon as they are discovered before large numbers of outbreaks occur.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>How to get rid of beetles?</strong></h2>\r\n\r\n<ul dir=\"ltr\">\r\n	<li>You can kill beetles in the gardens through the Nematodes worm, which can feed on the beetles in the soil and get rid of them immediately.</li>\r\n	<li>Spraying anti-beetle insecticides that can kill them immediately.</li>\r\n	<li>use neem oil directly above the beetles, and it can also be sprayed at home as a repellent of beetles and many other harmful insects.</li>\r\n	<li>Use liquid soap and water that is effective in eliminating beetles and other insects.</li>\r\n	<li>Cover plants with a suitable cover so that the beetles do not infiltrate and destroy it.</li>\r\n	<li>disinfection to your house using strong disinfectants.</li>\r\n	<li>Use a vacuum cleaner to get rid of beetles and eggs in carpets and floors.</li>\r\n</ul>', 11, 1, 0, '2020-05-06 05:02:30', '2020-05-12 13:22:30', 'الخنافس', 'Beetles', 'حشرة الخنافس : معلومات وحقائق وكيفية التخلص من الخنافس', NULL, 'الخنافس', 'beetles', 'Beetles | Facts & Identification, Control & Prevention', 'Beetles which has the scientific name called Coleoptera are one of the largest classes of insects in the world, they count about 40 percent of insects, they also have more than 360,000 species, they have many shapes and colors, some of them have wonderful metallic colors, cheerful and surprising shapes Beetles attract attention for many different reasons such as economic importance, size, abundance, appearance and wonderful habits, first of all when dealing with insect infestation, it is very important to know some basic information about it, the shape of the insect, its life cycle, its different habits and how to get rid of it.');
INSERT INTO `pest_libraries` (`id`, `name_ar`, `name_en`, `image`, `meta_description_ar`, `image_alt`, `description_ar`, `description_en`, `sort`, `is_active`, `home_order`, `created_at`, `updated_at`, `slug_ar`, `slug_en`, `meta_title_ar`, `sub_pest`, `keywords_ar`, `keywords_en`, `meta_title_en`, `meta_description_en`) VALUES
(14, 'ذبابة الفاكهة', 'Fruit flies', 'uploads/pest_libraries/1588921970cf2805348cdbb7d4b788a3b3b7548310image.png', 'تمثل ذبابة الفاكهة مصدر قلق لأنها حشرة مزعجة وتعتبر من الملوثات الخطيرة للأغذية، فهي يمكن أن تتراكم بأعداد كبيرة وبسرعة فائقة في المنازل وأماكن خدمات الطعام، مما يستوجب ضرورة القضاء عليها فور دخولها المنازل لحماية صحة الأسرة والعائلة وتميل أعداد ذبابة الفاكهة إلى الزيادة، عادة في فصل الربيع ، عندما تكون درجات الحرارة دافئة وفي ظل الظروف الجيدة تتطور ذبابة الفاكهة من البيض إلى الحشرة البالغة في غضون أربعة إلى خمسة أسابيع، وهي تستغرق حوالي أسبوع كي تصل إلى النضج الجنسي، وتميل للتزاوج في فترة الغسق ويمكنها البقاء على قيد الحياة طوال فصل الشتاء.', 'ذبابة الفاكهة', '<p dir=\"rtl\" style=\"text-align:justify\">تمثل ذبابة الفاكهة مصدر قلق لأنها حشرة مزعجة وتعتبر من الملوثات الخطيرة للأغذية، فهي يمكن أن تتراكم بأعداد كبيرة وبسرعة فائقة في المنازل وأماكن خدمات الطعام، مما يستوجب ضرورة القضاء عليها فور دخولها المنازل لحماية صحة الأسرة والعائلة وتميل أعداد ذبابة الفاكهة إلى الزيادة، عادة في فصل الربيع ، عندما تكون درجات الحرارة دافئة وفي ظل الظروف الجيدة تتطور ذبابة الفاكهة من البيض إلى الحشرة البالغة في غضون أربعة إلى خمسة أسابيع، وهي تستغرق حوالي أسبوع كي تصل إلى النضج الجنسي، وتميل للتزاوج في فترة الغسق ويمكنها البقاء على قيد الحياة طوال فصل الشتاء.</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">ويفيد فهم دورة حياة ذبابة الفاكهة في التعرف على أفضل طريقة للسيطرة عليها، وبدون فهم دورة حياتها قد ينتهي بك الأمر باستخدام طرق المكافحة الخاطئة،&nbsp; كما يمكن أن يساعدك الفهم الأساسي لدورة حياة ذبابة الفاكهة في تحديد كيف تؤثر مرحلة حياة معينة من حياة الحشرة على محصولك، ومتى تتحول الحشرة إلى تهديد قوي، وكيف يمكن السيطرة عليها، وكيفية طردها من منزلك أو حديقتك.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>ذبابة الفاكهة</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">لذبابة الفاكهة شكل بيضاوي كما أنها حشرة صغيرة في الحجم، وقد يصل طول الحشرة البالغة&nbsp; إلى 1/5 بوصة، أما اليرقات فهي أصغر في الحجم وقد يصل طولها إلى 1/5 بوصة وهي بيضاء اللون وقد يبدو الأبيض متعكراً ولها صدر وبطن يتميزان بلون أسود أو رمادي ويكون لذبابة الفاكهة عيون حمراء اللون.&nbsp;&nbsp;</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>دورة حياة ذبابة الفاكهة؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تبدأ أنثى ذبابة الفاكهة بوضع البيض في أماكن معينة مثل الخضروات والفواكه المتعفنة، ويمكن أن تضع قرابة ال 500 بيضة خلال دورة حياتها، وقد تمر اليرقات بعدد مرتين من التحول حتى تتحول إلى الذبابة البالغة، وتبدأ اليرقات في التغذية على المواد المتحللة التي توجد في الفطريات وغيرها، وتعتمد في تناولها الطعام على تحويل المواد الصلبة إلى مواد سائلة يمكنها أن تمتصها، و تكتمل دورة الحياة خلال 10 أيام بحد أقصى، وما إن تتحول ذبابة الفاكهة إلى الحشرة البالغة حتى تبدأ في التزاوج&nbsp; خلال ساعات قليلة، وتبدأ الأنثى في وضع البيض من جديد لتستمر دورة الحياة، ويكون لذبابة الفاكهة أربع مراحل للتطور وهي: البيض ، واليرقات والشرانق والحشرة البالغة.&nbsp;&nbsp;</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align:justify\">البيض: تضع الأنثى البالغة البيض في الثمار الناضجة الناضجة وتفقس بعد بضعة أيام من 2-4 أيام، وقد لا يمكن التعرف على وجود بيض ذبابة الفاكهة في فواكهك لأن بيضها صغير جدًا.</li>\r\n	<li style=\"text-align:justify\">اليرقات: تتغذى اليرقات حينما تفقس داخل الثمرة وترافقها بكتيريا مختلفة، ويؤدي نشاط الديدان والبكتيريا إلى تعفن الثمرة داخليًا وغالبًا ما تسقط الفاكهة المتأثرة قبل الأوان.</li>\r\n	<li style=\"text-align:justify\">الشرانق: تترك اليرقات الناضجة الثمرة وتحفر في التربة لتخرج وتصبح اليرقات غير نشطة وتتحول إلى شكل بيضاوي داخل شرنقة صلبة لها لون بني فاتح.</li>\r\n	<li style=\"text-align:justify\">الذبابة البالغة: تخرج الذبابة البالغة من الشرانق في أقل من سبعة أيام خلال فصل الصيف، أو بعد عدة أشهر خلال فصل الشتاء، وتبحث الذبابة البالغة عن الغذاء الذي تحتاجه للوصول إلى مرحلة النضج، وفي هذه المرحلة من دورة الحياة تكون قادرًا على التعرف على الذباب البالغ.</li>\r\n</ul>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>لماذا تدخل ذبابة الفاكهة المنزل؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تدخل ذباب الفاكهة إلى المنازل من خلال الأبواب والنوافذ المفتوحة أو عبر الثغرات الموجودة حول النوافذ والأبواب الخارجية، كذلك عن طريق شراء فواكه أو خضروات أو عبوات صغيرة أو محكمة الغلق مملوءة بذباب الفاكهة أو يرقاتها، كما أنها تنجذب إلى منزلك إذا كان لديك حديقة، أو كومة من السماد، أو لديك قمامة لا تضعها في وعاء بغطاء مغلق.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>عادات التغذية لذبابة الفاكهة</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تتغذى ذبابة الفاكهة على الفواكه والخضروات وغيرها من المواد العضوية المتحللة، وعندما تزداد أعداد ذبابة الفاكهة في الهواء الطلق خلال فصل الصيف يتم العثور عليهم بالقرب من المصادر الغذائية، وتكون الذبابة أكثر نشاطًا خلال اليوم وهي تتغذى على المواد النباتية الناضجة أو المتحللة، وهي تحدث طنين مزعج حينما تدخل المنازل وتسبب إزعاج شديد، وهي تقوم بتحويل جميع المواد الغذائية الصلبة إلى مواد سائلة حتى تتمكن من التغذية عليها وإمتصاصها بواسطة الأجزاء الفمية المزود بها وجهها.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>الأضرار التي تسببها ذبابة الفاكهة</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">لا يعرف عن ذبابة الفاكهة أنها تعض أو تلدغ البشر، بل يٌنظر إليها عمومًا على أنها حشرة مزعجة، وتسبب مشاكل صحية لأصحاب المنازل، ولهذا يجب توخي الحذر الشديد للتخلص منها ومنعها دخول المنزل أو التواجد حوله لأنهم يعيشون في مناطق ملوثة حيث القمامة والصرف صحي والمصارف والمواد المتحللة أو المتعفنة ولذلك يقومون بحمل الأمراض والبكتيريا الخطيرة على أجسامهم وأرجلهم ويقومون بنشر هذه الأمراض والبكتيريا لمنزلك وطعامك وأي شئ يقومون بلمسه، ومن المعروف عن الذباب قيامه بتلويث الطعام المكشوف ويمكن أن يتسبب في إسهال أو مغص معوي أو تسمم في بعض الحالات وكذلك نقل بعض البكتيريا الضارة مثل السالمونيلا التي تصيب الجهاز الهضمي ويكون لها أعراض حادة</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>كيفية التخلص من ذبابة الفاكهة؟</strong></h2>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align:justify\">التحكم في الطقس الدافئ بالمنزل من خلال فحص جميع الفتحات الموجودة في المنزل ووضع شاشة شبكية قوية بحيث لا تترك مساحة لدخول الذباب.</li>\r\n	<li style=\"text-align:justify\">&nbsp;لا تترك قطع من الفاكهة المكشوفة أو الخضروات أو سلطات الفواكه وعصائر الفاكهة، والمربى مكشوفة في حاويات مفتوحة.</li>\r\n	<li style=\"text-align:justify\">&nbsp;لا تبقي الفاكهة الكاملة في متناول اليد بكميات كبيرة حتى لا تتعرض لعدوى ذبابة الفاكهة.</li>\r\n	<li style=\"text-align:justify\">&nbsp;تحقق من الخضروات مثل البطاطس والبصل التي يتم تخزينها بكميات كبيرة، حيث يمكن أن يكون هناك خضروات فاسدة في الجزء السفلي.</li>\r\n	<li style=\"text-align:justify\">احتفظ بالأطعمة الحساسة في مكان بارد لتثبيط نمو ذباب ثمار اليرقات.</li>\r\n	<li style=\"text-align:justify\">إذا أصبحت الإصابة بذباب الفاكهة حادة فيجب أن يقوم صاحب المنزل بالبحث الدقيق عن مناطق تكاثر اليرقات.</li>\r\n	<li style=\"text-align:justify\">التخلص من الحشرات البالغة من خلال المرشات أو المبيدات الحشرية.</li>\r\n	<li style=\"text-align:justify\">يمكن أن يكون من الصعب تحديد مناطق التكاثر في بعض الأحيان ولذلك يجب التخلص من علب القمامة بطريقة سليمة.</li>\r\n	<li style=\"text-align:justify\">التوقف عن ممارسات الصرف الصحي غير اللائقة.</li>\r\n	<li style=\"text-align:justify\">التخلص من أماكن تراكم المواد الغذائية والتي عادة ما تكون صغيرة ومخفية، فقد تتراكم الفضلات في الزوايا، وتحت العدادات، وتحت الألواح، وفي الشقوق أو حول لوحات التصريف، وفي الشقوق حول الأحواض ، وما إلى ذلك</li>\r\n</ul>', '<p dir=\"ltr\" style=\"text-align:justify\">Fruit flies cause a nuisance and they are considered a serious contaminant of human food. They can accumulate in large numbers very quickly in houses and food service places, which necessitates the elimination of them as soon as possible, you should reduce their entrance to your house to protect your family health&nbsp;Numbers of fruit flies tend to increase, usually in the spring, when temperatures are warm and under good conditions and it develops from eggs to adults within four to five weeks, it takes about a week to reach sexual maturity.</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Understanding the fruit flies life cycle helps to know the best way to control it, and without understanding its life cycle you may end up using wrong control methods, understanding of the fruit flies life cycle can help you determine how a life stage of an insect&rsquo;s life affects your crop, And when the insect turns into a powerful threat, how to control it, and how to drive it out of your home or garden.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Fruit flies identification</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Fruit flies are a very small oval fly, the adult insect grows and its length ranges between 1/10 and 1/5 inch, while the larvae are very small where the length ranges between 1/10 and 1/5 inch and has a white dirty color, chest and abdomen are black with a gray bottom and often they have red eyes.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Fruit flies life cycle</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">fruit fly lays its eggs on the surface of rotting fruits and vegetables, each female lays up to 500 eggs, where they hatch in larvae that turn twice before they become an adult insect, larvae feed on yeast and fungi that grow in decomposing and rotting material, they transfer food into Liquids to digest it , flies life cycle transfer from eggs to adults from 8 to 10 days, mating occurs shortly and then the adult insect usually appears within a few hours, egg laying begins after about 24 hours.</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">There are four stages of life cycle of fruit fly, eggs, larvae, cocoons, and adult.</p>\r\n\r\n<ul dir=\"ltr\">\r\n	<li style=\"text-align:justify\">Eggs: The adult female lays eggs in mature fruits and they hatch after a few days&nbsp; from 2-4 days, it may not be possible to recognize the presence of flies eggs in your fruits because their eggs are very small.</li>\r\n	<li style=\"text-align:justify\">Larvae: larvae feed when they hatch inside the fruit and are accompanied by different bacteria. The activity of worms and bacteria causes the fruit to rot internally and the affected fruit that often falls prematurely.</li>\r\n	<li style=\"text-align:justify\">Cocoons: the mature larvae leave the fruit and dig into the soil to come out and become inactive larvae and it turn into an oval solid cocoon with a light brown color.</li>\r\n	<li style=\"text-align:justify\">Adult fly: The adult fly emerges from the cocoon in less than seven days during the summer, or after several months during the winter, the adult fly searches for the food with needed to reach maturity, and at this stage you are able to recognize the flies Adult.</li>\r\n</ul>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Why do fruit flies enter the house?</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Fruit flies enter house through doors and open windows, or through gaps around windows and external doors, as well by buying fruits, vegetables, or small or tightly packed containers filled with fruit flies or their larvae. They are also attracted to your home if you have a garden or have trash.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Nutritional habits of fruit flies</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">fruit flies feed on fruits, vegetables and other decomposing organic matter. When the number of fruit flies increases in the open air during the summer, they are found near food sources. The fly is more active during the day and it feeds on mature or decomposed plant matter.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>damage caused by fruit flies</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Fruit flies are not known bite people, but rather they are generally seen as a nuisance insect, yet great care must be taken to get rid of them and prevent them inside and around the house because they live in contaminated areas where garbage, sanitation and drains are so they carry dangerous diseases and bacteria on their bodies and legs and spread these diseases and bacteria for your home, food and anything they touch.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>How to get rid of fruit flies?</strong></h2>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Control warm weather at home by examining all openings in the house and setting a strong mesh screen so that it does not leave room for flies to enter.</li>\r\n	<li style=\"text-align:justify\">Do not leave pieces of open fruits, vegetables, or fruit salads and fruit juices, and jam in open containers.</li>\r\n	<li style=\"text-align:justify\">Do not keep whole fruits on hand in large quantities so as not to be exposed to fruit flies infection.</li>\r\n	<li style=\"text-align:justify\">Check vegetables such as potatoes and onions that are stored in large quantities, as there can be rotten vegetables at the bottom.</li>\r\n	<li style=\"text-align:justify\">Keep sensitive foods in a cool place to inhibit the growth of larval fruit flies.</li>\r\n	<li style=\"text-align:justify\">If the infection with fruit flies becomes severe, the homeowner should carefully search for the eggs places.</li>\r\n	<li style=\"text-align:justify\">&bull; Get rid of adult insects with pesticides.</li>\r\n	<li style=\"text-align:justify\">&bull; It can be difficult to identify breeding areas at times, so garbage cans must be disposed of properly and you have to stop improper sanitation practices.</li>\r\n	<li style=\"text-align:justify\">&bull; Disposal of food accumulation places that are usually hidden. waste may accumulate in the corners, under the meters, under the boards, in the cracks or around the drainage plates, in the cracks.</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>', 15, 1, 0, '2020-05-07 23:12:50', '2020-05-12 13:23:37', 'ذبابة-الفاكهة', 'Fruit-flies', 'حشرة ذبابة الفاكهة : معلومات وحقائق وكيفية التخلص من ذبابة الفاكهه', NULL, 'ذبابة الفاكهة', 'Fruit flies', 'Fruit flies | Facts & Identification, Control & Prevention', 'Fruit flies cause a nuisance and they are considered a serious contaminant of human food. They can accumulate in large numbers very quickly in houses and food service places, which necessitates the elimination of them as soon as possible, you should reduce their entrance to your house to protect your family health Numbers of fruit flies tend to increase, usually in the spring, when temperatures are warm and under good conditions and it develops from eggs to adults within four to five weeks, it takes about a week to reach sexual maturity.'),
(15, 'العث', 'Mites', 'uploads/pest_libraries/1588922833291daedb172a13dac3c6147ce2051c50image.png', 'هل يعاني أحد من أفراد منزلك من حساسية مفاجئة قد تهدد صحته؟ هل يوجد الكثير من التراب في منزلك؟ هل تعلم أن هناك بعض الحشرات التي يمكن أن تتسبب في إحداث تهيج شديد في الصدر أو حساسية خاصة للأطفال وكبار السن؟ هل تعلم أن العث قد يكون  له يد في ما تمر به بصورة أو أخرى؟ فعلى الرغم من أن معظم العث لا يتصل بالبشر أبدًا، لكن البعض الآخر قد يؤثر على صحتهم، وقد تسبب لهم حساسية أو تهيج، وهم نادرا ما ينقلون الأمراض للبشر، لكنهم مصدر إزعاج شديد بسبب التهيج الشديد في الجلد يمكن أن العث يسبب حكة شديدة', 'حشرة العث', '<p style=\"text-align:justify\">هل يعاني أحد من أفراد منزلك من حساسية مفاجئة قد تهدد صحته؟ هل يوجد الكثير من التراب في منزلك؟ هل تعلم أن هناك بعض الحشرات التي يمكن أن تتسبب في إحداث تهيج شديد في الصدر أو حساسية خاصة للأطفال وكبار السن؟ هل تعلم أن العث قد يكون&nbsp; له يد في ما تمر به بصورة أو أخرى؟ فعلى الرغم من أن معظم العث لا يتصل بالبشر أبدًا، لكن البعض الآخر قد يؤثر على صحتهم، وقد تسبب لهم حساسية أو تهيج، وهم نادرا ما ينقلون الأمراض للبشر، لكنهم مصدر إزعاج شديد بسبب التهيج الشديد في الجلد يمكن أن العث يسبب حكة شديدة</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">وهناك الكثير من أنواع العث أو ال mites، فبعضها يعض أو يسبب تهيجًا للإنسان خاصة من يعانون من مشاكل في الجهاز التنفسي أو أمراض الحساسية الصدرية، في حين أن بعض العث يتطفل على الحيوانات، والبعض الآخر يتغذى على النباتات، فهناك العديد من الأنواع المختلفة من العث والتي تمر بأربع مراحل من التطور مروراً من البيضة إلى اليرقة إلى الحورية إلى الحشرة البالغة، وجميع المراحل لها ثماني أرجل ماعدا اليرقة ذات الستة أرجل، ومن المهم معرفة بعض المعلومات عن حشرة العث إذا أردت التخلص منها بشكل صحيح لأن معرفة بعض المعلومات عن الحشرة يفيد أصحاب المنازل بشكل كبير ولذلك تابع معنا قراءة هذا المقال لمعالجة مشاكل العث والحساسية التي يسببها للبعض منا.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>المظهر الخارجي للعث</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يٌصنف العث على أنه من الحشرات العنكبوتية التي تتكون من ثمانية أرجل مفصلية ويختلف حجمها حسب نوعها، ومعظم العث لا يمكن رؤيته بالعين المجردة، ويبلغ طول العث من&nbsp; 1إلى 6 مم على الأكثر، ويكون للعث أجسام منتفخة أو مستديرة أو على شكل حبوب وهي تظهر بلون أسمر أو بني أو بني محمر ومن أشهر الأنواع عث التراب والذي يوجد في معظم المنازل على المراتب ويتغذى على جلد البشر، كما أن هناك عث الأثاث والذي يتواجد في أماكن التنجيد الرطبة، وهناك عث الدقيق والذي يصيب الحبوب الرطبة ويسبب الحكة، وهناك عث الجرب والذي يسبب الجرب في الحيوانات الأليفة عن طريق اختراق الجلد، مما تسبب في الطفح الجلدي، وهناك عث الطيور الذي يدخل المنازل من الأعشاش القديمة للعصافير والدواجن، وهناك عث العنكبوت الأحمر والذي يدخل المنازل ويتغذى&nbsp; على النباتات.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>علامات الإصابة بالعث</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">كما ذكرنا منذ قليل فمعظم أنواع العث تكون غير مرئية لكنه يعيش في كل مكان حولنا بما في ذلك التربة العميقة والبيئات المائية وطفيليات الطيور والثدييات والحشرات، وقد ينتشر العث على الحيوانات المضيفة، كما أن بعض الأنواع مثل الجرب تدفن نفسها تحت الجلد البشري وغالبًا ما يعيش العث الذي يصيب البشر في السجاد والأثاث والمراتب، ويتغذى العث المنزلي الشائع مثل عث الغبار، على رقائق الجلد الميت من البشر والحيوانات الأليفة، ومن أشهر العلامات على الإصابة بعث التراب هي إصابة البعض بحساسية صدرية غير مبررة خاصة إذا كان أصحاب المنزل لا يهتمون بتهوية المنزل أو إزالة التراب من على الأسطح أولا بأولا سواء في الأثاث أو المفروشات، أما عث الجرب فيتميز بحدوث حكة شديدة وتهيج جلدي لمن يقوم بإصابته.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>دورة حياة العث</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تكون دورة حياة العث عبارة عن أربع مراحل وهي البيضة، واليرقة ، والحورية ثم الحشرة البالغة، حيث يفقس البيض وينتج عنه يرقات سداسية الأرجل، والتي تمر بعدة مراحل مثل الحوريات وعادة ما يستغرق النمو من البويضة إلى الحشرة البالغة عدة أسابيع وفي ظل الظروف المثالية، يمكن أن تقصر المدة إلى بضعة أيام فقط ويعيش العث لعدة أشهر وتكرر الحشرة البالغة دورة الحياة مرة أخرى.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>أضرار حشرة العث</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">في حين أن بعض لسعات العث لا يلاحظها البشر، فقد يكون بعضها مؤلمًا، وكلها تؤدي إلى ظهور علامات حمراء أو حكة في الجلد، وقد تحدث حكة شديدة تؤدي إلى الخدش والإصابة بالعدوى وقد تتطور الأعراض عادةً في غضون يوم واحد وقد تستمر لمدة أسبوع، ويمكن أن يتسبب عث الجرب في ظهور طفح جلدي شديد الحكة، لأن الحشرة تخترق الجلد لتتكاثر ثم تتغذى على اللحم مما قد يؤدي إلى حالة طبية حادة وخطيرة، ويمكن أن تسبب حشرة عث الغبار التي تعيش في السجاد والأثاث إلى ردود فعل تحسسية عند استنشاقها، مما يؤدي إلى انسداد الأنف والعيون الدامعة والعطس، ويتسبب عث العنكبوت في إتلاف المحاصيل، ولأنه من الصعب رؤية العث فقد لا تكون على دراية بالعدوى حتى فوات الأوان.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>كيفية منع حشرة العث من غزو المنزل</strong></h2>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align: justify;\">التخلص من العشب الضار بانتظام، والحفاظ على أوراق الشجر بعيدا عن المنزل.</li>\r\n	<li style=\"text-align: justify;\">&nbsp;الحفاظ على الحيوانات الأليفة نظيفة وبحالة صحية جيدة.</li>\r\n	<li style=\"text-align: justify;\">&nbsp;تنظيف المنزل بانتظام، و مسح الأسطح من الغبار وكنس السجاد بالمكنسة الكهربائية، وغسل بياضات الأسرة في الماء الساخن، والحفاظ على مستويات الرطوبة منخفضة.</li>\r\n	<li style=\"text-align: justify;\">التخلص من الفوضى بما في ذلك أكوام الورق والغسيل وأكوام الصحف والأوراق المهملة حيث أنه يمكن أن يتواجد في الفراش، والسجاد، ومجاري الهواء، والسندرات المغبرة، وحتى أغطية المصابيح و الغسيل المتسخ.</li>\r\n	<li style=\"text-align: justify;\">معالجة جميع المناطق المصابة بمواد مطهرة.</li>\r\n	<li style=\"text-align: justify;\">تنظيف&nbsp; المنزل بالمكنسة وامسح الغبار بانتظام واجعل التنظيف أمرًا أسبوعيًا ولا تتجاهل الأرائك والستائر والسجاد واستخدام المكنسة الكهربائية بقدر ما تستطيع حيث يمكن القضاء على العث بسرعة باستخدام مكنسة كهربائية خاصة عث الغبار الذي يمكن أن يحفر في الأقمشة ويتشبث بها مثل السجاد والأثاث وحتى الملابس.</li>\r\n	<li style=\"text-align: justify;\">مسح جميع الأشياء غير المتحركة، مثل المزهريات ، ومجموعات الأقراص المضغوطة، والهواتف ، والمقابض، وإطارات الصور، والأرفف وأي شيء يمكن أن يجمع الغبار باستخدام قطعة قماش مبللة أو خرقة حتى لا ترفع الغبار في الهواء.</li>\r\n	<li style=\"text-align: justify;\">&nbsp;&nbsp;تغطية السرير والمرتبة والوسادة بأغطية واقية من الغبار وربما يكون سريرك هو المكان الذي ستجد فيه أعلى تركيز للعث ولذلك يمكنك حماية نفسك من خلال وضع مراتب ووسائد في الأغطية المقاومة للغبار والتي تمنع دخول العث إلى سريرك.</li>\r\n	<li style=\"text-align: justify;\">غسل كل الملاءات والفراش بالماء الساخن فمن المرجح أن يصنع عث الغبار منزله في فراشك، ولكن الحقيقة هي أنه يمكنك العثور عليه في معظم الأقمشة لذلك من الأفضل غسل الأدوات المنزلية مثل الفراش، وأغطية الوسائد، والستائر، والبطانيات، والأغطية ، وما إلى ذلك كل أسبوع إلى أسبوعين في 130-140 درجة من الماء الساخن فهو أفضل طريقة لضمان قتل العث.</li>\r\n</ul>', '<p dir=\"ltr\" style=\"text-align:justify\">There are many types of mites, some of them can bite or cause irritation to humans, especially those who suffer from respiratory problems or chest allergies, while some mites harm animals, others feed on plants, there are many different types of mites which go through four stages of life cycle, from the eggs to the larva to the nymph to the adult mites, all stages have eight legs except the six-leg larva.</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Most mites never contact humans, but others may affect their health, they cause allergies or irritation, they rarely transmit diseases to humans, but they are a source of severe discomfort due to severe skin irritation. Mites can cause severe itching, and it is important to know some information About mites if you want to get rid of them properly because knowing some information about the bug is very beneficial to home owners.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Mites identification</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Mites have swollen, round, or grainy bodies, and they are classified as a spider insect with eight articular legs and their size varies by species, most mites are usually invisible to the naked eye, and the length of the larger mites is about 6 mm, while the smallest is about 0.1 Mm, and the color of mites varies greatly, most of them appear in brown, brown or reddish brown, but some species are light red, blue, or green, and there are many types of mites, including dirt mites, which are found in most homes on mattresses and feed on human skin, and there Furniture mites, which are found in damp upholstery, and there are flour mites, which Moist pills or pasta cause itchiness, and there are scabies mites that cause scabies in pets by penetrating the skin, causing skin rashes, and there are bird mites that enter homes from the old nests of birds and poultry, and there are red spider mites that enter homes and feed on plants .</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Signs of mites infestation</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Most types of mites are invisible, they live almost everywhere, including deep soils, aquatic environments, parasites of birds, mammals, and insects. Mites may spread to host animals, some species such as scabies bury themselves under the human skin and often the mites that infect Humans in carpets, furniture and mattresses, household mites such as dust mites, feed on dead skin parts from humans and pets.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Life cycle of mites</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">life cycle of mites consists of four stages which are eggs, larva, nymph, and adult insect, where eggs hatch in six-legged larvae, which go through several stages such as nymphs, the transformation from the egg to the adult insect usually takes several weeks and under ideal conditions, it can shorten Duration to just a few days, mites live for several months.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Mites damage</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">some mites sting but not observed by humans, some of them may be painful, and all lead to the appearance of red marks or itchy skin, severe itching that leads to scratching and infection may occur and symptoms may develop within one day and may last for a week, Scabies mites cause severe itchy skin rashes, because the insect penetrates the skin to multiply and then feed on the meat, which may lead to an acute and dangerous medical condition, and dust mites that live in carpets and furniture can cause allergic reactions when inhaled, which leads to a blockage Nose, watery eyes and sneezing, spider mites damage crops, Because mites are difficult to see, you may not be aware of infection until it is too late.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>How to prevent mites infestation?</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Get rid of harmful grass regularly and keep tree leaves away from your house</p>\r\n\r\n<ul dir=\"ltr\">\r\n	<li style=\"text-align: justify;\">Keep pets clean and in good health condition.</li>\r\n	<li style=\"text-align: justify;\">Regular cleaning of the house, wiping dust surfaces, vacuuming carpets, washing bed linen in hot water, and keeping moisture levels low.</li>\r\n	<li style=\"text-align: justify;\">Eliminate clutter, including piles of paper, washing, newspaper piles, and discarded papers, as they can be found in bed, carpets, air ducts, dusty attics, and even lamp covers and dirty laundry.</li>\r\n	<li style=\"text-align: justify;\">Treat all affected areas with antiseptic substances.</li>\r\n	<li style=\"text-align: justify;\">Clean the house and wipe the dust in weekly matter, do not ignore couches, curtains and carpets, use the vacuum cleaner as often as you can. The mites can be eliminated quickly using a special vacuum cleaner. Dust mites can dig into the fabrics and cling to them such as carpets, furniture, and even clothing.</li>\r\n	<li style=\"text-align: justify;\">Wipe all non-moving objects, such as vases, CD sets, phones, knobs, picture frames, shelves and anything that can collect dust with a damp cloth or rag so as not to lift dust in the air.</li>\r\n	<li style=\"text-align: justify;\">Cover your bed, mattress and pillow with dust-proof covers and your bed may be the place where you will find the highest concentration of mites, so you can protect yourself by placing mattresses and pillows in dust-proof covers that prevent the mites from entering your bed.</li>\r\n	<li style=\"text-align: justify;\">Wash all sheets and bedding with hot water, dust mites are likely to make their home in your mattress, but the truth is that you can find them in most fabrics so it is best to wash household items such as bedding, pillow covers, curtains, blankets, blankets.</li>\r\n</ul>', 19, 1, 0, '2020-05-07 23:27:13', '2020-05-12 13:24:02', 'العث', 'Mites', 'حشرة العث : معلومات وحقائق وكيفية التخلص من حشرة العث', NULL, 'العث', 'Mites', 'Mites | Facts & Identification, Control & Prevention', 'There are many types of mites, some of them can bite or cause irritation to humans, especially those who suffer from respiratory problems or chest allergies, while some mites harm animals, others feed on plants, there are many different types of mites which go through four stages of life cycle, from the eggs to the larva to the nymph to the adult mites, all stages have eight legs except the six-leg larva.'),
(16, 'الدبور', 'Hornets', 'uploads/pest_libraries/1588924363f3e1310247447b8f1c8a43b797ac09adimage.png', 'الدبور من الآفات اللاذعة أو الحشرات القارصة والتي تأتي في مجموعة مختلفة الأشكال والأحجام، ويكون العامل المشترك بينها جميعاً هو الإبرة التي تسبب لدغة قوية أو قاتلة في بعض الأنواع، ومن أشهر أنواع الحشرات  الأخرى النحل والعقارب والدبابير الصفراء، وهي يمكن أن تسبب الكثير من المشاكل الصحية لك أو لمنزلك، وتكون لدغة الدبور قوية بما يكفي لتسبب التورم وردود فعل جلدية مختلفة، وعادة ما يبني عشه حول منازلنا وحدائقنا، وعندما نخرج يقوم الدبور بالدفاع عن منزله باللدغ والقرص.', 'الدبور', '<p style=\"text-align:justify\">الدبور من الآفات اللاذعة أو الحشرات القارصة والتي تأتي في مجموعة مختلفة الأشكال والأحجام، ويكون العامل المشترك بينها جميعاً هو الإبرة التي تسبب لدغة قوية أو قاتلة في بعض الأنواع، ومن أشهر أنواع الحشرات&nbsp; الأخرى النحل والعقارب والدبابير الصفراء، وهي يمكن أن تسبب الكثير من المشاكل الصحية لك أو لمنزلك، وتكون لدغة الدبور قوية بما يكفي لتسبب التورم وردود فعل جلدية مختلفة، وعادة ما يبني عشه حول منازلنا وحدائقنا، وعندما نخرج يقوم الدبور بالدفاع عن منزله باللدغ والقرص.</p>\r\n\r\n<p style=\"text-align:justify\">والدبور من الحشرات القارصة الشائعة جدًا حول منازلنا خاصة خلال فصل الربيع عندما تتفتح الأزهار، وهو ينجذب الدبابير إلى المناطق العامرة بالسكان، وهو يفضل العيش في الهواء ويبني الأعشاش في المباني والمصانع أو في الجدران والأسقف والنوافذ والحدائق عند الأشجار المجوفة أو تحت الأرض أو في الصخور، وفيما يلي سوف نتعرف على أهم أنواع الحشرات القارصة وبعض المعلومات المتعلقة بها وكيفية التعامل معها.</p>\r\n\r\n<h2 style=\"text-align:justify\"><strong>المظهر الخارجي للدبور</strong></h2>\r\n\r\n<p style=\"text-align:justify\">للدبور جسم ناعم وعيون بارزة، وقطعة فمية يستخدمه في عملية المضغ، وللدبور ثلاثة أجزاء وهم الرأس والصدر والبطن، ولهم قرون استشعار طويلة نسبياً ويتمتع الدبور بجسم أرفع من النحل وكذلك دبابير الطين لها أجسام رفيعة جدًا مثل العصا وتحتوي الدبابير ذات السترات الصفراء على أشرطة ذلات لوان الصفراء، أما اليرقات تكون صغيرة الحجم ولها لون أبيض، وتملك الحشرة البالغة أجنحة للطيران وتكون مخططة سواء باللون البني المحمر أو الأصفر، وتتميز الملكة بأنها أكبر الأفراد حجماً.</p>\r\n\r\n<h2 style=\"text-align:justify\"><strong>أين يعيش الدبور؟</strong></h2>\r\n\r\n<p style=\"text-align:justify\">يعيش الدبور في جميع البيئات والمناخات حول العالم، المكان الوحيد على الأرض الذي لا يحتوي على حشرات بشكل طبيعي هو القطب الشمالي مثل القارة القطبية الجنوبية، وتزداد كثافة الدبور في المناخات الأكثر دفئًا، ولهذا يتم العثور عليه في كل مكان، وهناك عدد من الأماكن التي تصنع فيها الحشرات القارصة وخاصة الطائرة أعشاشها، فبعض الدبابير تمضغ بعض المواد وتبني بها أعشاشها، أما دبابير الطين تقوم ببناء أعشاش من أنابيب طينية على الشرفات وجوانب المنازل وكذلك في المساحات الداخلية للجدران والمنازل، وهناك بعض الأنواع التي تبني أعشاشها تحت الأرض مثل النحل الطنان والدبابير ذات السترات الصفراء، وتبدو أعشاش الدبابير المبنية حول المنازل على شكل كمثرى مصنوعة من الورق، ويمكن أن تصبح ضخمة ومأهولة بالآلاف من الدبابير، ويمكن أن تكون الأعشاش في الداخل والخارج كما يقومون أيضًا ببناء الأعشاش في السندرات، وثقوب أو جذوع الأشجار، ويكون التخلص من أعشاش الحشرات القارصة صعب للغاية ولذلك يجب الإتصال بشركة مكافحة الحشرات بجدة.</p>\r\n\r\n<h2 style=\"text-align:justify\"><strong>غذاء الدبور</strong></h2>\r\n\r\n<p style=\"text-align:justify\">الدبور من الحشرات الاجتماعية ولذلك فهي تبني مستعمرات تحتوي على آلاف العمال وقد تعيش الحشرات المنفردة مثل دبور الطين بمفردها أو في مجموعات صغيرة وتكون لدغة الدبور خطرة على الشخص المصاب بالحساسية ويمكنهم التعرض لصدمة الحساسية والسعال بما يهدد حياتهم، ولذلك يجب مراقبة أي شخص يعاني من اللدغة لعدة ساعات بحثًا عن علامات الحساسية، مثل الحكة الشديدة وصعوبة التنفس أو تورم الفم أو الشفتين أو الحلق أو ضيق التنفس أو الغثيان أو فقدان الوعي، وتنجذب العديد من الحشرات القارصة إلى الروائح المنبعثة من صناديق القمامة والنفايات، ولتقليل احتمالية حدوث ذلك تأكد من تغطية جميع صناديق القمامة الخارجية، ويمكن للدبور صيد الحشرات الكبيرة مثل اليعسوب والخنافس والعث، وكذلك التغذية على رحيق النباتات والزهور والفواكه.</p>\r\n\r\n<h2 style=\"text-align:justify\"><strong>عادات الدبور</strong></h2>\r\n\r\n<p style=\"text-align:justify\">بقوم الدبور بلسع الضحية عدة مرات، أما الدبادبير ذات السترات الصفراء تكون أكثر عدوانية، ويمكن أن تدخل في الفراغات مثل جحور الفئران في الأرض أو في فراغات الجدار في الهياكل، وتقوم بإطلاق فيرمونات لتحذير الجماعات لذا تجنب الإقتراب من أعشاشها وهي تنجذب إلى الأطعمة والمشروبات خاصة في أواخر الصيف وأوائل الخريف، وتقوم الدبابير الورقية ببناء أعشاش على شكل قرص العسل وتسبب لدغات مؤلمة إذا شعرت بالتهديد، وقد تطارد الذكور البشر دون قرصها، وتقوم الدبابير ببناء أعشاش ورقية مكشوفة على شكل كرة قدم تتدلى من أطراف الأشجار أو في الشجيرات كما يمكنهم بناء أعشاش على السقف المتدلي وفي المداخن أو بالقرب منها ويمكن أن تكون شديدة العدوانية، لذلك إذا كنت تشك في وجود مشكلة لديك ابق بعيدًا وأبق عائلتك وحيواناتك الأليفة على مسافة.</p>\r\n\r\n<h2 style=\"text-align:justify\"><strong>كيف يتكاثر الدبور؟</strong></h2>\r\n\r\n<p style=\"text-align:justify\">تهتم الدبابير ببناء العش، ويتولى العمال بناء أعشاش كبيرة تشبه&nbsp;&nbsp; &nbsp; الورق من الخشب أو المواد العضوية&nbsp; داخل الأشجار المجوفة والأماكن المحمية، وتقوم الملكة بوضع البيض والذي ترعاه العاملات، وفي موسم التكاثر&nbsp; يترك الإناث والذكور العش ويطير للتزاوج وبناء مستعمرة جديدة، وبعد نجاح عملية التزاوج تضع البيض وتكرر دورة التكاثر من جديد، ويمكن أن يصل العدد في المستعمرة من 100 إلى 400 دبور.</p>\r\n\r\n<h2 style=\"text-align:justify\"><strong>علامات الإصابة بالدبور</strong></h2>\r\n\r\n<p style=\"text-align:justify\">يفضل الدبور التواجد في المنازل وذلك لتوافر الغذاء والمياه ولذلك تكون المنازل معرضة للإصابة خاصة في الربيع والصيف، ويمكن البحث عن الدبابير في الشقوق أو عند أساسات المنزل أو النوافذ والأبواب والفتحات والجدران، كما أنها تقوم ببناء أعشاش في الأغصان أو داخل الشجيرات أو التجاويف، ولذلك إذا كنت تشك في إصابة منزلك بالدبور يمكنك البحث في هذه المناطق، علاوة على التأكد من شكل الحشرة قبل مراجعة شركة مكافحة الحشرات بجدة.</p>\r\n\r\n<h2 style=\"text-align:justify\">كيف تعالج حساسية لدغات الدبور؟</h2>\r\n\r\n<p style=\"text-align:justify\">بالنسبة لمعظم الناس، فإن الخطوة الأولى للتخلص من قرصات الدبور هي بغسل المنطقة بالصابون والماء للتعامل مع الألم والتورم بسبب اللسعة، وبالنسبة للأشخاص الذين لا يعانون من الحساسية، فإن تناول مضادات الهيستامين أحيانًا يساعد أيضًا في الحد من التورم، وبالنسبة لأولئك الذين يعانون من الحساسية، يمكن أن يكون الأمر أكثر خطورة ويمكن أن تحدث ردود الفعل التي يمكن أن تنتقل من المؤلمة إلى الخطيرة والمهددة للحياة بسرعة كبيرة ولذلك من الضروري طلب المساعدة الطبية المختصة، ومن يعاني من الحساسية من قرصات الحشرات يحمل قلم الإبينيفرين الكيميائي وهو يساعد الشخص المصاب بالحساسية على تقليل رد فعل الجسم تجاه مسببات الحساسية.</p>\r\n\r\n<h2 style=\"text-align:justify\"><strong>كيفية التخلص من الدبور؟</strong></h2>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\">قم برصد العش من خلال فحص الأشجار والهياكل المفتوحة والفتحات في الجدران والهياكل.</li>\r\n	<li style=\"text-align: justify;\">اهدم أعشاش جديدة في وقت مبكر قبل نموه.</li>\r\n	<li style=\"text-align: justify;\">تذكر أن العديد من النحل والدبابير تعشش على الأرض ولذلك احرص دائمًا على ارتداء الأحذية عند المشي في ساحة منزلك.</li>\r\n	<li style=\"text-align: justify;\">الحد من مصادر الطعام والمياه، قم بإغلاق صناديق القمامة بإحكام، وتخلص من علب المشروبات الغازية وحاويات الطعام، احتفظ بأغذية الحيوانات الأليفة مغطاة أو في الداخل، وتخلص من الفاكهة المتساقطة.</li>\r\n	<li style=\"text-align: justify;\">أغلق الأبواب والنوافذ والشقوق بشكل صحيح في منزلك وفتحات التهوية على الجدران.</li>\r\n	<li style=\"text-align: justify;\">قم بتقليل الفوضى والحطام داخل الفناء وحوله للحد من مأوى الحشرات القارصة.</li>\r\n	<li style=\"text-align: justify;\">لا تقم بزراعة الأشجار المزهرة والشجيرات والزهور بالقرب من مداخل المباني والمنازل لتجنب جذب الحشرات إلى المنطقة.</li>\r\n	<li style=\"text-align: justify;\">الاتصال بشركة مكافحة الحشرات بجدة فورا.</li>\r\n</ul>', '<p dir=\"ltr\">Hornet is a famous biting insect which have different shapes and sizes, the common thing between them is they have a needle which causes a strong bite, the most famous other types of biting insects are bees, scorpions and yellow wasps, they can cause many health problems to your home, the hornet sting is strong enough to cause swelling and allergic skin reactions, they build their nest around our homes and gardens, they bite people to defend themselves.</p>\r\n\r\n<p dir=\"ltr\">Hornet comes around our houses during spring, they are attracted to crowded areas, they prefer to live outside in the air and build nests in buildings and factories or in walls, ceilings, windows and gardens, we will learn more about hornets through this article.&nbsp;</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Hornet external appearance&nbsp;</strong></h2>\r\n\r\n<p dir=\"ltr\">Hornet has prominent eyes, oral piece used in suction, hornet has three body parts, head, chest, and abdomen. They have relatively long antennae. it has a thinner body than bee. Mud wasps have very thin bodies such as the stick, the larvae are small in size and have a white color, adult has wings to fly, they have reddish brown or yellow color, the queen is the largest member in the colony.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Where hornets live?</strong></h2>\r\n\r\n<p dir=\"ltr\">Hornets live in all weather and places around the world, they increase in warmer climates, there are a number of places that they live, they build their nests in several places, for example mud wasps build nests from mud pipes in balconies and house corners, walls and windows. There are some types that build their nests under the ground, the nests seem to made of paper and huge which contain thousands of hornets.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>&nbsp;What Hornets eat?</strong></h2>\r\n\r\n<p dir=\"ltr\">Hornet is a social insect and builds colonies that contain thousands of workers, mud wasp may live in small groups, hornet bite is dangerous and, so if anyone suffers from the sting should be monitored for several Hours for allergies, such as itching, swelling of the mouth, lips, throat, shortness of breath, nausea, unconsciousness, in this case you may contact a DR, in other hand hornets are attracted to garbage and&nbsp; can hunt large insects such as beetles, and mites, they also eat nectar of plants, flowers, and fruits.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Hornet habits</strong></h2>\r\n\r\n<p dir=\"ltr\">hornet stings the victim several times, yellow jacket is more aggressive, and can enter into voids in the ground or in the wall, they are attracted to foods and drinks, especially in summer and autumn, they build nests in the shape of a honeycomb and cause painful bites if they feel threatened, they can also build nests in ceiling and near chimneys, so if you suspect there is a problem you have to stay away and keep your family and your pets at a distance.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>How hornet breed?</strong></h2>\r\n\r\n<p dir=\"ltr\">Workers build large nests from wood or organic materials inside trees and hidden places, queen lays eggs that the workers care for, females and males leave the nest and fly for mating and build a new colony, the queen lay eggs and resume the breeding cycle, the total number of the colony can reach from 100 to 400.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Signs of hornet infection</strong></h2>\r\n\r\n<p dir=\"ltr\">Hornet live in homes due to food and water availability, especially in spring and summer. you can search for hornet in cracks, windows, doors openings, and walls. They also build nests in the branches of bushes or cavities, so therefore if you suspect that your home was infected with a hornet, you can search in these areas, as well as check the shape of the insect before checking with a Jeddah pest control company.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>How to treat hornet allergy?</strong></h2>\r\n\r\n<p dir=\"ltr\">First of all, you have to wash the area with soap and water to deal with swelling, you may need antihistaminic drugs to reduce swelling, if you suffer from allergies you have to seek professional medical assistance, also epinephrine helps to reduce allergic reactions.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>How to get rid of hornets?</strong></h2>\r\n\r\n<ul>\r\n	<li>Monitor hornet nest by examining trees, openings in walls and structures and destroy new nests early before growing, remember that many hornet nest may be on the floor, so make sure to wear shoes when walking in your yard.</li>\r\n	<li>Reduce food and water sources and dispose of soft drinks boxes and food containers, don&rsquo;t forget to keep pet food covered or inside, and dispose of fallen fruit.</li>\r\n	<li>seal doors, windows and cracks properly in your home, also reduce clutter in the yard to reduce biting insects and don&rsquo;t plant flowering trees and flowers near the entrances to home to avoid attracting hornts,</li>\r\n	<li>Contact Pest control company in jeddah immediately</li>\r\n</ul>', 23, 1, 0, '2020-05-07 23:52:43', '2020-05-12 13:23:54', 'الدبور', 'Hornet', 'حشرة الدبور : معلومات وحقائق وكيفية التخلص من الدَبُور', NULL, 'الدبور', 'Hornet', 'Hornet insect | Facts & Identification, Control & Prevention', 'Hornet is a famous biting insect which have different shapes and sizes, the common thing between them is they have a needle which causes a strong bite, the most famous other types of biting insects are bees, scorpions and yellow wasps, they can cause many health problems to your home, the hornet sting is strong enough to cause swelling and allergic skin reactions, they build their nest around our homes and gardens, they bite people to defend themselves.');
INSERT INTO `pest_libraries` (`id`, `name_ar`, `name_en`, `image`, `meta_description_ar`, `image_alt`, `description_ar`, `description_en`, `sort`, `is_active`, `home_order`, `created_at`, `updated_at`, `slug_ar`, `slug_en`, `meta_title_ar`, `sub_pest`, `keywords_ar`, `keywords_en`, `meta_title_en`, `meta_description_en`) VALUES
(17, 'القراد', 'Ticks', 'uploads/pest_libraries/1588925787c7362d663511a01f3cbbc0c15320ee37image.png', ' ينتمى القراد إلى فئة القراديات أو اللبوديات، والتي يٌطلق على الاسم العلمي لها Ixodida، وهي من الحشرات التي تتغذى على الدم وتدخل منازل البشر في كثير من الأوقات ولذلك يخلط الكثير من أصحاب المنازل بين القراد والبراغيث، والحقيقة أن الشبه بينهما كبير فهما من الحشرات المتطفلة والتي تعيش على المضيف، وكلاَ منهما يتغذى على دم المضيف، وقد يقومون بنقل بعض الأمراض له، ولا يختار كلاً من القراد أو البراغيث البش كمضيف رئيسي ولكنها تلجأ له في حالة غياب المصادر الأخرى.', 'القراد', '<p dir=\"rtl\" style=\"text-align:justify\">ينتمى القراد إلى فئة القراديات أو اللبوديات، والتي يٌطلق على الاسم العلمي لها Ixodida، وهي من الحشرات التي تتغذى على الدم وتدخل منازل البشر في كثير من الأوقات ولذلك يخلط الكثير من أصحاب المنازل بين القراد والبراغيث، والحقيقة أن الشبه بينهما كبير فهما من الحشرات المتطفلة والتي تعيش على المضيف، وكلاَ منهما يتغذى على دم المضيف، وقد يقومون بنقل بعض الأمراض له، ولا يختار كلاً من القراد أو البراغيث البش كمضيف رئيسي ولكنها تلجأ له في حالة غياب المصادر الأخرى.</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">ويملك القراد أربعة أزواج من الأرجل،&nbsp; ويمكن العثور عليها في الحدائق ومنازل البشر، وهي تتغذى على الدم خلال جميع مراحل دورة حياتها، أما البراغيث فلها ستة أرجل ولا تتمكن من الطيران لكنها تقوم بالقفز السريع بسبب أجسامها المسطحة ورشاقتها وهي تتحرك بسهولة على الفراء الكثيف والشعر وتوجد في كل مكان بالعالم، وهي تخضع لعملية تحول كامل خلال دورة حياتها ولا يتغذى على الدم سوى البالغين، ولذلك إذا لم تنجح في التعرف على الحشرة التي توجد في منزلك يمكنك فوراً التعاقد مع شركة مكافحة الحشرات بجدة للتعرف على الحشرة و التعامل معها بشكل ناجح، وفي هذا المقال سوف تتعرف على المظهر الخارجي للقراد ودورة حياتها&nbsp; والخطورة التي يمثلها وكيفية دخوله للمنزل وكيفية القضاء عليه بشكل ناجح ولذلك تابع القراءة معنا.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>المظهر الخارجي للقراد</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">للقراد البالغ ثمانية أرجل أما اليرقات تمتلك ستة أرجل يختلف لون القراد حسب النوع، ويصل طول اليرقة إلى 1مم أما الحشرة البالغة تصل ل1 سم إذا كانت منتفخة بالدم، وعلى الأغلب يصل&nbsp; حجمها أقل من بذرة عباد الشمس، وهناك نوعين من القراد وهم القراد الصلب والقراد اللين، ويكون للذكور والإناث لون مختلف في القراد الصلب، وتكون الإناث في الحجم من الذكور، ويكون لهذا النوع أجزاء فم مرئية عند رؤية القراد من الأعلى، أما القراد اللين يكون له جسم متجعد ويكون للذكور والإناث نفس الحجم، وتقع الأجزاء الفمية على الجانب السفلي من الجسم ولهذا يمكن للجزء الأمامي من الجسم أن يخفيه.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>دورة حياة القراد</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">للقراد أربع مراحل يمر بها خلال دورة حياته، والتي تكون عبارة عن البيضة واليرقة والحوية والحشرة البالغة، وفي مرحلة الحورية يحدث تسريع في عملية التطور تنتقل بعدها إلى القرادة البالغة، وبعد التزاوج، تشرب الإناث كميات كبيرة من الدم وتضع الآلاف من البيض وتموت، ولهذا توجد دورة واحدة فقط للقراد، ويزداد حجم الإناث بشكل كبير أثناء الرضاعة، وفي بعض الأحيان تزيد عن حجمها الأصلي بنحو 100 مرة وتترك البيض عند النباتات المتحللة أو الأوراق النباتية المتساقطة أو في الفجوات داخل المنازل.</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">وتضع الأنثى في المتوسط ​​5،380 بيضة، وقد يحتاج القراد خلال دورة حياته إما إلى مضيف واحد أو اثنين أو ثلاثة، وخلال دورة حياة المضيف الواحد يبقى القراد على نفس المضيف في كل المراحل ويتركه فقط قبل وضع البيض، أما في دورة الحياة ذات المضيفين يندفع القراد من اليرقة إلى الحورية في المضيف الأول ويتركه في المنتصف وقد يكون المضيف الثاني هو نفس المضيف الأول، أما في حالة الثلاثة مضيفين، يغادر القراد المضيف الأول ويتنقل في كل مرحلة إلى مضيف آخر وقد يكون نفس الشخص اعتمادًا على توفر المضيف للقراد.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>أين يعيش القراد؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يحب ان يعيش القراد في الاماكن ذات الرطوبة المرتفعة لكن بالطبع يمكنها التكيف في الكثير من الاماكن الاخرى وبشكل عام يميل ان يعيش بالقرب من مصدر الغذاء مثل الكلاب والقطط والقوارض والطيور ولسوء الحظ يمكن بالطبع أن يتغذى على دماء البشر على عكس الاعتقاد الشائع بأنه لا يقوم بقرص الانسان ،وعادة ما يتم تواجد القراد في الهواء الطلق والمناطق العشبية لتواجد الحيوانات بها ليبدأ بالحصول على الغذاء وبعد تناول وجبة الدماء يقوم بالاختباء لحين الحصول علي مصدر غذاء آخر وهذا ليس بالطبع في جميع أنواع القراد وعلى الرغم من أن قراد الكلاب البني يمكن أن يتكاثر داخل المنزل و يمكن ان يعيش القراد في المناطق المظلمة والعالية الرطوبة كما يوجد بالقرب من الغطاء النباتي والأشجار، وداخل المنزل يتواجد في الشقوق و الأعشاش والأحجار.&nbsp;&nbsp;</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>كيف يدخل القراد المنزل؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يلتصق القراد في الملابس أو جلد البشر أو فرو الحيوانات، وهو لا يتمكن من القفز والطيران، وهو يدخل المنزل من خلال الحيوانات الأليفة أو الفئران، ويبحث القراد اللين دوماً عن مضيف.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>ما مدى خطورة القراد؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">لا يٌمثل القراد تهديدًاً كبيرًا&nbsp; على المنازل، ولكن في حالة وجود ثقوب أو فجوات في أساس المنزل، أو في حالة أن هيكل المبنى قديم وبحاجة إلى صيانة يمكنها أن تدخل للمنزل من خلال الحيوانات، وفي الكثير من الأحيان يتغذى القراد على البشر وتقوم بقرصهم وحينما تحصل على وجبة الدم تعود مرة أخى للمكان الذي أتت منه، ويمكن للقراد أن ينقل أنواع مختلفة الأمراض وأكثر من 15 فيروسًا وعدوى، ويٌعد مرض لايم أشهر الأمراض التي تنتقل للبشر والخطورة أنه إذا تُرك دون علاج يؤثر على القلب والجهاز العصبي و تشمل الأعراض الصداع والحمى والطفح الجلدي وألم المفاصل، ولهذا يجب استشارة الطبيب فوراً.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>كيفية القضاء على القراد؟&nbsp;&nbsp;</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يمكن لشركة مكافحة الحشرات بجدة تبني خطة علاج شاملة ومتكاملة للتحكم في القراد من خلال تفتيش المنزل وتحديد نوع الحشرة وفحص داخل وخارج المنزل، كما سيقون أخصائي الشركة بشرح بعض بعض التفاصيل الأساسية عن القراد ودورة الحياة وكيفية التعرف عليها والتعامل معها ومن أهم النصائح للقضاء على القراد:</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align: justify;\">تهوية المنزل وتعريض المناطق المعتمة لأشعة الشمس.</li>\r\n	<li style=\"text-align: justify;\">معالجة مشكلة الرطوبة.</li>\r\n	<li style=\"text-align: justify;\">&nbsp;التخلص من القمامة سواء بالمنزل أو حول محيطه.</li>\r\n	<li style=\"text-align: justify;\">&nbsp;التخلص من الحطام وجذوع الأشجار المتساقطة والصخور والحطب والأكوام الصخرية.</li>\r\n	<li style=\"text-align: justify;\">استخدام منتجات كيميائية للتخلص من القراد بشكل فعال.</li>\r\n	<li style=\"text-align: justify;\">تقليل الغطاء النباتي في الحديقة.</li>\r\n	<li style=\"text-align: justify;\">التخلص من أعشاش الطيور والقوارض وتقليم الأعشاب في الحديقة.</li>\r\n	<li style=\"text-align: justify;\">وضع حاجز من الحصى حول الشجر والفناء.</li>\r\n	<li style=\"text-align: justify;\">&nbsp;الفحص المنتظم للحيوانات الأليفة لإحتمالية اكتشاف القراد.</li>\r\n	<li style=\"text-align: justify;\">&nbsp;في حالة اكتشاف القراد على جسم الحيوان قم بمسكهم بملقاط خاص من خلف الرأس وسحبهم ببطء بعيدًا عن الجلد، من المهم إزالة الفم تمامًا من جلد الحيوان وغسل وتعقيم المنطقة المصابة.</li>\r\n</ul>', '<p dir=\"ltr\">Ticks have a latin name called Ixodida, they feed on blood, enter human houses and homeowners confuse between ticks and fleas, both live on a hosts and feed on their blood, they may transmit diseases to them, they don&rsquo;t choose humans as a main host&nbsp;Ticks ​​have four pairs of legs, they may live in gardens and human houses, they feed on blood during all stages of their life cycle, while fleas have six legs and cannot fly, they jump by the help their of their flat bodies, they move easily on thick fur and hair, found Everywhere in the world, only adults fed on blood, Therefore, if you do not identify the insect in your home, you can immediately call a pest control company in Jeddah to identify ticks and deal with it successfully.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Ticks External Appearance&nbsp;</strong></h2>\r\n\r\n<p dir=\"ltr\">Adult ticks have eight legs and larvae have six legs, ticks color varies depending on the type, the larva length may reach to 1 mm, adults reach up to 1 cm when they bloated with blood. ticks size resembles the size of sunflower seeds, both males and females have different colors, females in hard ticks are bigger than males, they have mouth parts when seeing them from the top, while the soft ticks have body wrinkles and both males and females have the same size.&nbsp;</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Ticks ​​life cycle</strong></h2>\r\n\r\n<p dir=\"ltr\">Tick has four stages during its life cycle, eggs, the larva, nymph, and the adults, after mating, females feed on large amounts of blood, then they lay thousands of eggs and die, females size increases dramatically during lactation, sometimes it exceeds its original size by about 100 times, they eaves eggs on plants leaves or organic materials.</p>\r\n\r\n<p dir=\"ltr\">females lays about 5,380 eggs, ticks may need either one or two or three hosts, during one host life cycle, tick remains on the same host in all stages, leaves it only before laying eggs, while in the two hosts Ticks transfer ​​from larva to nymph in the first host and leave it in the middle stage, the second host may be the same first host, in the three hosts life cycle, tick leaves the first host and moves at each stage to another host.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Where ticks live?</strong></h2>\r\n\r\n<p dir=\"ltr\">Ticks live in dark and humid environments, they also live in trees, inside houses they are found in cracks, nests, and stones&nbsp;Ticks feed on mammal blood, birds, they may feed on the blood of cats, dogs, deer, humans and animals.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>How ticks enter the house?</strong></h2>\r\n\r\n<p dir=\"ltr\">Tick sticks to clothes, human skin and animal fur, it cannot jump and fly, it enters the house through pets or mice, once they enter the house they start to look for a suitable host.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>How dangerous are ticks?</strong></h2>\r\n\r\n<p dir=\"ltr\">We may not consider ticks as a major threat to humans, you may find them in holes and gaps, in the building&#39;s structure, they may pinch humans and feed on their blood, ticks can transmit various types of diseases and more than 15 viruses and infections. Lyme disease is the most common disease transmitted to humans through ticks, if it left untreated it affects the heart and nervous system.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>How to get rid of ticks?</strong></h2>\r\n\r\n<p dir=\"ltr\">A pest control company in Jeddah adopts an integrated treatment plan to control ticks through home inspection, determine insect type, our specialist will explain some basic facts about ticks and their life cycle and how to identify and treat them, you may also get rid of ticks by these simple steps:</p>\r\n\r\n<ul>\r\n	<li>&nbsp;Ventilate your house and expose the sun to dark areas.</li>\r\n	<li>fix humidity problems in your house.</li>\r\n	<li>Get rid of garbage inside your house.</li>\r\n	<li>Get rid of rocks, wood, stones in your garden.</li>\r\n	<li>Use chemical products to get rid of ticks effectively.</li>\r\n	<li>Get rid of birds and mice nests in the garden and put barrier around trees in the yard.</li>\r\n	<li>Inspect your pets for possible tick infestation.</li>\r\n	<li>If you notice ticks in your pets, hold them with special tongs and pull them slowly away from pet skin, then wash and sterilize the affected area.</li>\r\n</ul>', 9, 1, 0, '2020-05-08 00:16:27', '2020-05-08 06:05:16', 'القراد', 'Ticks', 'حشرة القراد : معلومات وحقائق وكيفية التخلص من القراد', NULL, 'القراد', 'Ticks', 'Ticks | Facts & Identification, Control & Prevention', 'Ticks have a latin name called Ixodida, they feed on blood, enter human houses and homeowners confuse between ticks and fleas, both live on a hosts and feed on their blood, they may transmit diseases to them, they don’t choose humans as a main host Ticks ​​have four pairs of legs, they may live in gardens and human houses, they feed on blood during all stages of their life cycle, while fleas have six legs and cannot fly, they jump by the help their of their flat bodies, they move easily on thick fur and hair, found Everywhere in the world, only adults fed on blood, Therefore, if you do not identify the insect in your home, you can immediately call a pest control company in Jeddah to identify ticks and deal with it successfully.'),
(18, 'صرصور الليل', 'Crickets', 'uploads/pest_libraries/158892731809671a84a9256a540f14b7501924afb0image.png', 'الجداجد السوداء أو صراصير الليل والحقل من عائلة ( Gryllidae ) والتي يوجد فيها ما يقرب من 2400 نوع من الحشرات القافزة، المنتشرة في جميع أنحاء العالم والمعروفة بأحداث النقيق الموسيقي للذكور، وتختلف الصراصير في الطول من 3 إلى 50 ملم (0.12 إلى 2 بوصة) ويكون لديهم هوائيات رقيقة، وأرجل خلفية معدلة للقفز، وأجزاء ثلاثية (مفصلية) واثنين من الزوائد الحسية البطنية النحيلة وتكون الأجنحة الأمامية قاسية وجلدية، وتملك اثنين من الأجنحة الخلفية الغشائية الطويلة تستخدم في الطيران.', 'صرصور الليل', '<p dir=\"rtl\" style=\"text-align:justify\">الجداجد السوداء أو صراصير الليل والحقل&nbsp;من عائلة (&nbsp;Gryllidae )&nbsp;والتي يوجد فيها ما يقرب من 2400 نوع من الحشرات القافزة، المنتشرة في جميع أنحاء العالم والمعروفة بأحداث النقيق الموسيقي للذكور، وتختلف الصراصير في الطول من 3 إلى 50 ملم (0.12 إلى 2 بوصة) ويكون لديهم هوائيات رقيقة، وأرجل خلفية معدلة للقفز، وأجزاء ثلاثية (مفصلية) واثنين من الزوائد الحسية البطنية النحيلة وتكون الأجنحة الأمامية قاسية وجلدية، وتملك اثنين من الأجنحة الخلفية الغشائية الطويلة تستخدم في الطيران.</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">وينتج الذكور من الصراصير السوداء أصوات النقيق الموسيقي عن طريق فرك أو كشط سنون توجد على الجناح على طول صف من حوالي 50 إلى 250 سنًا ويعتمد تكرار النغمات على عدد الأسنان المضروبة في الثانية ويختلف من 1500 دورة في الثانية في أكبر أنواع الصراصير السوداء إلى ما يقرب من 10000 دورة في الثانية في الأصغر، وأكثر أغاني الصراصير السوداء شيوعًا هي الأغنية التي تجذب الأنثى والتي تكون بهدف المغازلة أو التزاوج وهي الأغنية التي تحفز الأنثى على التزاوج، وكذلك أغنية القتال الذي تصد الذكور الآخرين، ولدى كلا الجنسين أعضاء حساسة للغاية على مقدمة القدم لاستقبال الصوت، وهناك علاقة مباشرة بين معدل غناء الصراصير السوداء ودرجة الحرارة، حيث تزداد مع زيادة درجة الحرارة، وفيما يلي سوف نتعرف على أهم عادات الصراصير السوداء ودورة حياتها وكذلك كيفية التعرف عليها من خلال المقال ولذلك تابع القراءة معنا.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>المظهر الخارجي لصرصور الليل</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">شكل الصراصير السوداء متوسطة الحجم أو حشرات كبيرة، ولديها أجزاء فم ماضغ، وأرجله خلفية أكبر وأقوى من الزوجين الآخرين، كما لديها رؤوس مستديرة، هوائيات طويلة ورقيقة، وأجنحتها تنحني على جوانب الجسم، وغالبًا ما تبدو مسطحة، أو على الأقل الجزء العلوي من جسمها يكون مسطح، ومعظمها بني اللون، ولكن بعضها أسود وبعضها أخضر بأجنحة بيضاء، وتقوم الذكور بالتغريد أو إحداث صوت عالي يشبه الغناء عن طريق فرك الأجنحة الأمامية معًا، ويكون لأجنحتهم هياكل خاصة لهذا الغرض، ولكل من الذكور والإناث آذان، لكنها توجد على أرجلهم ولهم هياكل مستديرة ناعمة على أرجلهم السفلية، ولدى الصراصير أنبوبة مستديرة رفيعة في نهاية بطنها تستخدم لوضع البيض، وهذا الهيكل يسمى مبيض البيض ويكون لدى الإناث ويكون بيضاوي ومسطح، في حين أن مبيض البيض للصراصير الذكور مستدير.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>دورة حياة صراصير الليل</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تقوم أنثى صرصور الليل&nbsp;بوضع البيض في التربة حتى يفقس، ثم تخرج الصراصير الصغيرة ويبدأ بالحفر حتى تظهر على سطح الأرض،&nbsp; وقد تمر بعدد من الانسلاخات يصل إلى 10 مرات خلال فترة شهرين إلى 3 أشهر حتى تصل إلى الشكل النهائي للصراصير البالغة، وتبدأ الصراصير السوداء في ممارسة نشاطها خلال فصل الربيع وأوائل الصيف، كما أنها تظهر بأعداد كبيرة في أغسطس وسبتمبر.</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">وتستكمل دورة حياتها خلال ثلاثة أشهر بحد أقصى، وتضع البيض في أي مكان رطب مثل أسفل التربة كما ذكرنا منذ قليل، ويكون للصغار شكل مماثل للصراصير البالغة لكنها تكون بحجم أصغر وتفتقر إلى الأجنحة، وتقوم الأنثى بإدخال مبيضها&nbsp; في بعض النباتات ولهذا قد تسبب بعض الأضرار البالغة لها،&nbsp; وحينما تفقس الحوريات تتحول إلى حشرة بالغة بعد 6-12 يوم، وتبدأ في حفر البيوت في الشقوق والكهوف تحت الأرض ويمكن مشاهدتها بكثرة في الحقول والمساحات الخضراء وأحيانًا&nbsp; ما تدخل إلى المنازل والبيوت، ويصل طول الحشرة البالغة إلى 21 ملم ويكون لها لون أصفر مصفر فاتح، وأجنحة تغطي منطقة البطن وهي تتمتع بعدد ثلاثة أشرطة داكنة توجد في أعلى الرأس وبين العينين، ويوجد لها كما ذكرنا أجنحة خلفية طويلة وقد تتخلص منها في فترة لاحقة من حياتها.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>غذاء صرصور الليل</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تتغذى صراصير الليل&nbsp;على أي شيء تقريبًا وهي تتلف أحيانًا المحاصيل المزروعة مثل البرسيم والقطن والفراولة ويمكن أن تضر أيضًا بالخضروات ونباتات الزينة، وهي من الحشرات المزعجة التي تنجذب إلى الأضواء في المنازل والمناطق الحضرية وتصدر النقيق لتصبح مصدر إزعاج للبعض، كما أنها قد تتلف الأقمشة أحيانًا، خاصة إذا كانت متسخة ، وقد تمضغ الخشب أو البلاستيك أو المطاط أو المنتجات الجلدية. وقد تتفشى بأعداد كبيرة مثلما حدث في عام 1953 عندما غزت أعداد كبيرة من الصراصير السوداء المدن والبلدات في أجزاء كثيرة من أوكلاهوما بالولايات المتحدة وكانت جوانب المباني مغطاة بالكامل بأعداد هائلة.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>كيفية التخلص من صراصير الليل</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">عموماً الصراصير المنزلية لا تضر البشر، ولكن في حالة توافر بعض الأغذية العارية أو الأطعمة الخاصة بالحيوانات الأليفة ستهرب إلى منزلك وتزعجك بصوتها، ولهذا يجب التحكم فيها ومنعها من دخول منزلك من خلال ما يلي:</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align: justify;\">تقضي الصراصير عادة ساعات النهار في الاختباء في المناطق المظلمة والرطبة ويساعد التخلص من أكوام الطوب أو الحجارة أو الخشب أو أي حطام آخر حول المنزل على تقليل الأعداد.</li>\r\n	<li style=\"text-align: justify;\">يجب التخلص من الأعشاب والنباتات الكثيفة التي توجد حول أسس المنازل والتي تعتبر أماكن اختباء جيدة للصراصير.</li>\r\n	<li style=\"text-align: justify;\">&nbsp;يجب تنظيف القمامة القريبة التي توفر الطعام والمأوى. نظرًا لأن الصراصير تنجذب إلى الأضواء فإن التخلص من مصادر الضوء في الليل سيقلل من الأعداد التي تنجذب إلى المنزل، وتساعد تدابير الوقاية مثل سد الفتحات والتأكد من أن جميع الشاشات والأبواب ضيقة تعمل على تقليل الأعداد التي يمكن أن تدخل منزلك أو عملك.</li>\r\n	<li style=\"text-align: justify;\">قد يكون من الصعب السيطرة على الصراصير البالغة داخل المنازل أو المباني ولذلك يتم تطبيق البخاخات أو المبيدات الجاهزة للاستخدام على الألواح الأساسية وعتبات الأبواب و الشقوق والثقوب حيث تختبئ الصراصير عادةً.</li>\r\n	<li style=\"text-align: justify;\">من المفيد في كثير من الأحيان الرش خارج حول المنزل والأساس وفي أسرة الزينة والفناء والمنطقة المحيطة بالمنزل وعند أماكن الخشب أو الحطب أو الوقود المكد كما يساعد العلاج الخارجي على منع الصراصير من الانتقال إلى مبنى آخر مما يحد من الإصابة.</li>\r\n</ul>', '<p dir=\"ltr\" style=\"text-align:justify\">Crickets from the Gryllidae family which contains approximately 2,400 species of insects, they scattered all over the world and known as chirping males Crickets vary in length from 3 to 50 mm (0.12 to 2 inches) and they have Thin antennas, they jump with their legs, they are triple articulated parts and have two slender abdominal sensory appendages, the front wings are hard, they have two long membranous posterior wings used for flight.</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Crickets males produce sounds of chirping by rubbing teeth found on their wings along a row consists of about 50 to 250 teeth. The frequency of tones depends on the number of teeth multiplied per second and varies from 1500 cycles per second in the largest species of black cockroach to approximately 10,000 cycles per second in the smallest, the most common Crickets song is the song that attracts the female and is aimed at flirting or mating, is the song that motivates the female to mate, as well as the fighting song that repels other males, and both sexes have very sensitive members on the front of the foot to receive the sound They are As a direct relationship between vocals Crickets rate and temperature, which increases with the temperature, it is important to know about Crickets habits and their life cycle as well as how to identify them through this article.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Crickets appearance&nbsp;</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Crickets are medium-sized or large insects, they have chewing mouth parts, their back legs are larger and stronger, they also have round heads, long and thin antennas, their wings bend over the sides of the body, often appear flat, the upper part of their body is flat Most of them are brown in color, but some are black and some are green with white wings, the males sing or make a loud singing-like sound by rubbing the front wings together, their wings have special structures for this purpose, both males and females have ears, they are found on their legs and have smooth round structures On their lower legs, Cockroaches have thin, round tube at the end of their abdomen that is used to lay eggs. This structure is called ovary and the female has an oval and flat one, while the ovary of the male cockroaches is round.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Crickets life cycle</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Crickets take two to three months to complete their life cycle at a temperature of 80 to 90 degrees Fahrenheit, they can live around buildings and in landfills, eggs are deposited in any humid place such as sand or algae, young insects resemble adults but smaller and without wings.</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Usually eggs are laid in the soil, when they hatch freshly, they dig on the surface to peel, and they will slaughter 8 to 10 times over two to 3 months before they become adult insects. They are active in spring and early summer, and they appear in very large numbers during a month. August and September after periods of prolonged dry weather.</p>\r\n\r\n<p style=\"text-align:justify\">Most female Crickets enter eggs in the soil where they enter their ovaries in the plant, which sometimes causes serious damage to the plant. Most of the Crickets lay eggs in the autumn and the nymphs hatch in the spring and become adults after 6-12 days. Houses are often dug in shallow burrows. They feed on plants, animals, clothing, or each other, and black cockroaches are common in fields and squares and sometimes enter homes and buildings.</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Crickets are 16 to 21 mm long, light yellowish yellow in color, with wings covering the abdomen, and they have three dark transverse strips on the top of the head and between the eyes, all types of cockroaches have long background wings when they become adults, but they sometimes get rid of them.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Crickets eating habits&nbsp;</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Crickets feed on almost anything, they sometimes damage cultivated crops such as cotton and strawberry, they can also harm vegetables and plants. Especially if it is dirty, and they may chew wood, plastic, rubber or leather products. they may spread in large numbers, as happened in 1953 when large numbers of black cockroaches invaded cities and towns in many parts of Oklahoma in the United States, and the sides of the buildings were completely covered in huge numbers.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>How to get rid of Crickets</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Generally speaking, Crickets do not harm humans, but they escape to your home and disturb you with its sound, this must be controlled and prevented from entering your home through the following:</p>\r\n\r\n<ul dir=\"ltr\">\r\n	<li style=\"text-align: justify;\">Crickets usually spend daylight hours hiding in dark and humid areas and getting rid of piles of bricks, stones, wood, or other debris around the house will help reduce number</li>\r\n	<li style=\"text-align: justify;\">You should get rid of the dense herbs and plants that are around the foundations of the houses and which are good hiding places for Crickets.</li>\r\n	<li style=\"text-align: justify;\">Close garbage that provides food and shelter should be cleaned. Since Crickets are attracted to the lights, getting rid of light sources at night will reduce the numbers that are attracted to the home, and prevention measures such as blocking openings and making sure that all screens and doors are tight will reduce the numbers that can enter your home or business.</li>\r\n	<li style=\"text-align: justify;\">It can be difficult to control adult Crickets indoors or buildings, so ready-to-use sprays or pesticides are applied to basic panels, door sills, cracks, and holes where Crickets usually hide.</li>\r\n	<li style=\"text-align: justify;\">It is often useful to sprinkle outside around the house and foundation, in ornamental beds, the yard, the area around the house, and where the wood, firewood, or fuel is hard</li>\r\n</ul>', 20, 1, 0, '2020-05-08 00:41:58', '2020-05-12 13:24:09', 'صرصور-الليل', 'Crickets', 'حشرة صرصور الليل : معلومات وحقائق وكيفية التخلص من صراصير الليل', NULL, 'صرصور الليل', 'Crickets', 'Crickets | Facts & Identification, Control & Prevention', 'Crickets from the Gryllidae family which contains approximately 2,400 species of insects, they scattered all over the world and known as chirping males Crickets vary in length from 3 to 50 mm (0.12 to 2 inches) and they have Thin antennas, they jump with their legs, they are triple articulated parts and have two slender abdominal sensory appendages, the front wings are hard, they have two long membranous posterior wings used for flight.'),
(19, 'الدبور الأصفر', 'Yellow Jackets', 'uploads/pest_libraries/1588928004032c5080f5d4f7eb08a322bf917a3ac7image.png', 'الدبور الأصفر والذي يٌعرف أيضا بالدبابير ذات السترات الصفراء أو الزنابير الصفراء  والتي تٌعرف بالاسم اللاتيني Vespula spp. and Dolichovespula spp من الحشرات المعروفة بقرصاتها المؤلمة، وهي تٌسبب الإزعاج إذا تواجد عشها في حديقة المنزل أو بالمبني الذي تعيش به حيث يصبح الجميع معرضون للقرص، فهي تقوم ببناء الأعشاش مثل الدبور سواء في الأرض أو الجدران والأسقف أو الأخشاب،  وتدخل وتخرج من العش من خلال فتحة صغيرة تقوم بصنعها فيه، وقد تعيش تحت الأرض في جحور القوارض ويحتوي العش على آلاف الدبابير الذين يسهل استفزازهم وبالتالي يقومون بمطاردة البشر لمسافات طويلة، كما أنها تصدر أصوات واهتزازات مزعجة، ويطير الدبور لمئات الأمتار ليبحث عن الطعام، وهي تتغذى على اليرقات، والحلويات وبقايا الطعام.', 'الدبور الأصفر', '<p dir=\"rtl\" style=\"text-align:justify\">الدبور الأصفر والذي يٌعرف أيضا بالدبابير ذات السترات الصفراء أو الزنابير الصفراء&nbsp; والتي تٌعرف بالاسم اللاتيني Vespula&nbsp;spp. and&nbsp;Dolichovespula&nbsp;spp من الحشرات المعروفة بقرصاتها المؤلمة، وهي تٌسبب الإزعاج إذا تواجد عشها في حديقة المنزل أو بالمبني الذي تعيش به حيث يصبح الجميع معرضون للقرص، فهي تقوم ببناء الأعشاش مثل الدبور سواء في الأرض أو الجدران والأسقف أو الأخشاب،&nbsp; وتدخل وتخرج من العش من خلال فتحة صغيرة تقوم بصنعها فيه، وقد تعيش تحت الأرض في جحور القوارض ويحتوي العش على آلاف الدبابير الذين يسهل استفزازهم وبالتالي يقومون بمطاردة البشر لمسافات طويلة، كما أنها تصدر أصوات واهتزازات مزعجة، ويطير الدبور لمئات الأمتار ليبحث عن الطعام، وهي تتغذى على اليرقات، والحلويات وبقايا الطعام.</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">ويٌعد فصليّ الصيف والربيع هو الوقت الذي يبدأ فيه الدبور الأصفر في الظهور، حيث يكون ظهوره قليل في بداية فصل الصيف، ويزداد بالتدريج حتى يصل ل5000 دبور،ولا شك أنه فور اكتشاف وجود عش للدبور يجب التخلص السريع منه لتجنب القرصات المؤلمة والإزعاج والخطر الذي يسببه، خاصة إذا كان في منطقة مزدحمة أو قريبة من المنزل، ولذلك إذا كان عش الدبور الأصفر يمثل تهديدًا فوريًا للبشر والحيوانات، ستحتاج إلى اتخاذ إجراءات التحكم الفورية بعد التأكد من تحديد الحشرة بشكل صحيح، ويمكنك التعرف على المزيد عن الدبور الأصفر من خلال قراءة هذا المقال.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>المظهر الخارجي للدبور الأصفر</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">المظهر الخارجي للدبور الأصفر يتميز بقرون استشعار سوداء اللون وجسم أقصر وأكثر سماكة من الدبور العادي، ويصل طول الجسم من 10 ل 16 مم، وتتميز بلون أصفر وأسود، ويكون لهم خصر أرفع من النحل ويمكن التمييز بين الملكة والعمال من خلال شكل البطن، فللذكور سبعة أجزاء في البطن بينما تمتلك الإناث ستة أجزاء فقط، ويمكننا القول أن الدبور الأصفر يتبع فئة الدبابير، ولكن ليس كل دبور يكون دبور أصفر، وهي تتميز بالهدوء النسبي حيث يمكنها أن تقف عل يدك ولا تقوم بحركات مفاجئة كما أنها قد تعيش منفردة وليست اجتماعية وللفصل النهائي بين أنواع الدبابير عليك الاستعانة بشركة مكافحة الحشرات في جدة، وهناك نوعين من الدبور الأصفر وهما:</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align: justify;\">الدبور الأصفر الجنوبي: الاسم العلمي له هو Vespula squamosa وهو يقوم ببناء عش أرضي بعرض 20 سم وعمق 20 سم تحت سطح التربة، ومن الممكن بناء عش ضخم في بعض الحالات بارتفاع 2.5 متر في جذع الشجر، والاسم العلمي له&nbsp;</li>\r\n	<li style=\"text-align: justify;\">الدبور الأصفر الشرقي: الاسم العلمي له ، Vespula maculifrons (Buysson):&nbsp; ، ويقوم ببناء عش تحت الأرض أو في الجو، ويكون له فتحة بقطر 1.5 سم، ويشبه الغلاف الخارجي للعش الورق المٌحترق، وعندما يصبح العش أكبر يقوم الدبور بالتخلص من التربة في الحجر، ويرسبها على بعد 1سم من العش،&nbsp; ويكون العش بقطر من 9.5 إلى 30 وقد يحتوى على 2800 دبور، ويقوم ببناءه في الجذوع المتحللة وتجاويف الأشجار وجوانب المنزل والمبنى</li>\r\n</ul>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>كيف يتكاثر الدبور الأصفر؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يبدأ التكاثر لدي الدبور الأصفر حينما تقوم الملكة العثور على موقع مناسب، وتقوم ببناء عش صغير، وتبدأ في وضع البيض الذي يقوم العمال برعايته، وتوسيع العش والبحث عن الطعام ورعاية الصغار، ويكون دور الملكة الوحيد هو إنتاج البيض بشكل مستمر، وتقوم الملكة بتخصيب البيض باستخدام الحيوانات المنوية التي تخزنها، بينما تتطور البويضات غير المخصبة إلى الذكور والتي فور بلوغها تطير للتكاثر مع الملكات وتكوين عش جديد.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>ما هو النظام الغذائي للدبور الأصفر؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يتغذى الدبور الأصفر بشكل أساسي على الحشرات الأخرى مثل الذباب والنحل، كما يتغذى على الفواكه والحشرات الميتة ورحيق الزهور، ويمكنه السفر لعدة أمتار بعيدا عن العش للحصول على الغذاء.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>سلوك الدبور الأصفر</strong></h2>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align: justify;\">بناء العش: الدبور الأصفر من الحشرات الاجتماعية التي تعيش في مستعمرات كبيرة، وفي الربيع تجمع ملكة الدبور الأصفر بعض الخشب لتبني منه العش الذي يشبه الورق وتقوم ببناءه تحت الأرض أو في المنازل أو حولها ويحتوي العش على طبقات متعددة من الخلايا الورقية التي تشبه مشط نحل العسل, ويمكن أن تكون أعشاش الدبابير ضخمة في بعض الأحيان، ويتم تجديده كل عام، حيث أنه غالبًا ما يتم تدميره بواسطة الطيور التي تبحث عن الطعام، ويكون لكل عضو في العش مهام محددة، فالملكة تضع مئات البيض، ويقوم الدبور الذكر بتخصيب الملكة القادمة، أما العمال فلهم أكثر من وظيفة داخل العش كما ذكرنا منذ قليل.</li>\r\n	<li style=\"text-align: justify;\">لدغات الدبور الأصفر: للدبور الأصفر قدرة على إحداث لدغة مؤلمة ولكن بدون أن يترك إبرة على الجلد&nbsp; كما يفعل النحل، وتكون معظم اللدغات عند شعور الدبور بالتهديد أو الخوف على عشه ويكون هدفه هو حماية موقع العش ولذلك تقوم بمهاجمة&nbsp; أي شخص يقترب منها، وهي تحقن سائلًا سامًا تحت الجلد ويمكن أن يلدغ الشخص أكثر من مرة وتكون القرصة مؤلمة جدًا.</li>\r\n</ul>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>كيفية القضاء على عش الدبور الأصفر؟</strong></h2>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align: justify;\">وضع غبار المبيدات الحشرية التي تحتوي على الكرباريل لتدمير العش في الأرض أو في الجداران، حيث يتم رشه حول مدخل العش أثناء الليل وسيلتصق الغبار بالحشرات التي تغادر العش وتدخله مما يساهم في القضاء عليها.</li>\r\n	<li style=\"text-align: justify;\">&nbsp;تكون الدبابير داخل المنزل بطيئة، ولكنها تنشط إذا كانت درجة الحرارة مرتفعة، ولذلك سيساعد سد الثغرات والفتحات التي يأتي منها الدبور الأصفر في منع دخولها لمنزلك.</li>\r\n	<li style=\"text-align: justify;\">&nbsp;يجب في البداية تحديد أين يقع العش أو مكان دخول الدبابير وذلك من خلال مراقبة نمط طيران الحشرات لتحديد موقع عشها ثم مكافحته.</li>\r\n	<li style=\"text-align: justify;\">تجنب العطور والمستحضرات ومنتجات الشعر التي لها رائحة الزهور خاصة في الربيع والصيف. لأنها التي قد تجذب الحشرات.</li>\r\n	<li style=\"text-align: justify;\">ينجذب الدبور الأصفر إلى المشروبات الغازية السكرية وقد تطير داخل العلب المفتوحة لذلك تأكد من إحكام غلقها وقم بسكبها وقت الشرب في كوب زجاجي لرؤية محتويات العلبة.</li>\r\n	<li style=\"text-align: justify;\">قم بتغطية علب القمامة وأغذية الحيوانات الأليفة.</li>\r\n	<li style=\"text-align: justify;\">التعاقد مع شركة مكافحة الحشرات بجدة للتخلص من مشكلة الدبور الأصفر.</li>\r\n</ul>', '<p style=\"text-align:justify\">Yellow jacket also known as Vespula&nbsp;spp and&nbsp;Dolichovespula&nbsp;spp, is one of the painful stings insects. They enter and leave their nests through a small opening and they live underground in the rodents caves, the nest contains thousands of yellow jackets, they also chase people for long distances, they emit annoying sounds and vibrations, they flies for hundreds meters to search for food, they feed on small larvae, sweets.</p>\r\n\r\n<p style=\"text-align:justify\">Summer and spring are time when yellow jackets begin to appear, they appear in small numbers at the beginning and gradually they increase until reaching 5,000. Once their nest is discovered, it must be quickly eliminated to avoid the painful stings and danger, especially in crowded area close to your house, you will need to take immediate control measures after making sure the insect is correctly identified, you can learn more about Yellow jackets through this article.</p>\r\n\r\n<h2 style=\"text-align:justify\"><strong>Yellow jackets external appearance&nbsp;</strong></h2>\r\n\r\n<p style=\"text-align:justify\">The external appearance of the yellow jackets is characterized by dark color antennae, shorter and thicker body than hornet, their length ranges from 10 to 16 mm, they are characterized by a yellow and black color, they have a thinner waist than the bees, Queen and workers can be distinguished by the shape of the abdomen, males have seven parts while females have only six parts in the abdomen&nbsp;They are characterized by calm behavior, they can stand on your hand and not doing sudden movements, they may alone, to differentiate between yellow jackets you have to seek help of Jeddah pest company, there are two main types of yellow jackets:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\">Southern Yellow Jackets: scientific name is Vespula squamosa, it builds nest 20 cm wide and 20 cm deep below the surface of the soil. It is possible to build a huge nest in some cases with a height of 2.5 meters in the tree trunk, and the scientific name for it</li>\r\n	<li style=\"text-align: justify;\">Eastern Yellow Jackets: scientific name is Vespula maculifrons (Buysson). it builds a nest underground or in the air with hole of 1.5 cm in diameter, the outer layer of the nest resembles a burning paper, when the nest becomes larger, it disposes the soil 1 cm away from the nest with a diameter 9.5 upto 30 and may contain 2800 member.</li>\r\n</ul>\r\n\r\n<h2 style=\"text-align:justify\"><strong>How yellow jackets reproduce?</strong></h2>\r\n\r\n<p style=\"text-align:justify\">Reproduction begins when the queen finds a suitable place, it builds a small nest, lays eggs, workers expand the nest, search for food, care for youngsters. The queen&rsquo;s role is to produce eggs continuously, fertilizes them using The storing sperms, unfertilized eggs develop into males,&nbsp; when reaching puberty they fly to multiply with queens and form a new nest.</p>\r\n\r\n<h2 style=\"text-align:justify\"><strong>What yellow jackets eat?&nbsp;</strong></h2>\r\n\r\n<p style=\"text-align:justify\">Yellow jackets eat insects such as flies and bees, they also eat dead fruits and insects and flower nectar, they and can travel several meters away from the nest to obtain food.</p>\r\n\r\n<h2 style=\"text-align:justify\"><strong>Yellow jackets behavior</strong></h2>\r\n\r\n<p style=\"text-align:justify\">Building a nest: yellow jackets are social insects that live in large nests. In the spring, queen collects some wood to build nests underground, in homes, around it. nest contains multiple layers of paper cells that resemble a bee comb. Nests can sometimes be huge, it is renewed every year, because it destroyed by birds, each member of the nest has specific tasks, queen lays hundreds of eggs, male fertilizes the next queen, workers have more than one job inside the nest.</p>\r\n\r\n<p style=\"text-align:justify\">Yellow jackets Bites: they have the ability to cause a painful bite without leaving a needle on the skin as bees do, they attack anyone who come closer to the nest, they inject Toxic fluid under the skin.</p>\r\n\r\n<h2 style=\"text-align:justify\"><strong>How to eliminate yellow jackets nest?</strong></h2>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\">use carbaryl insecticide dust to destroy the nest in the ground or walls, spray it around the nest entrance at night, it sticks to the insects and contributes to its elimination.</li>\r\n	<li style=\"text-align: justify;\">yellow jackets inside the house are slow, they only activate if the temperature is high, so close the gaps and openings that they comes from will help prevent them from entering your home</li>\r\n	<li style=\"text-align: justify;\">it is necessary to determine the nest place and how they enter house by observing the flight pattern to determine the location of its nest.</li>\r\n	<li style=\"text-align: justify;\">Avoid floral perfumes and hair products especially in the spring and summer. Because it may attract insects.</li>\r\n	<li style=\"text-align: justify;\">yellow jackets are attracted to sugary soft drinks and may fly inside cans so make sure to close them and pour them into a glass to see the contents of the can.</li>\r\n	<li style=\"text-align: justify;\">Cover garbage and pet food.</li>\r\n	<li style=\"text-align: justify;\">Contract with a pest control company in Jeddah to get rid of the yellow jackets.</li>\r\n</ul>', 22, 1, 0, '2020-05-08 00:53:24', '2020-05-12 13:24:02', 'الدبور-الأصفر', 'yellow-jackets', 'الدبور الأصفر : معلومات وحقائق وكيفية التخلص من الدبور الأصفر', NULL, 'الدبور الأصفر', 'Yellow Jackets', 'Yellow Jackets | Facts & Identification, Control & Prevention', 'Yellow jacket also known as Vespula spp and Dolichovespula spp, is one of the painful stings insects. They enter and leave their nests through a small opening and they live underground in the rodents caves, the nest contains thousands of yellow jackets, they also chase people for long distances, they emit annoying sounds and vibrations, they flies for hundreds meters to search for food, they feed on small larvae, sweets.');
INSERT INTO `pest_libraries` (`id`, `name_ar`, `name_en`, `image`, `meta_description_ar`, `image_alt`, `description_ar`, `description_en`, `sort`, `is_active`, `home_order`, `created_at`, `updated_at`, `slug_ar`, `slug_en`, `meta_title_ar`, `sub_pest`, `keywords_ar`, `keywords_en`, `meta_title_en`, `meta_description_en`) VALUES
(20, 'العقرب', 'Scorpions', 'uploads/pest_libraries/158892981828e9d4410370fe6c36eecdf725e27a91image.png', 'هناك على كوكبنا ما يقترب من 1200 نوع من العقارب وجميعها خطيرة للغاية، وتكون العقارب الأصغر حجماَ هي الأشد خطورة على الإطلاق،  وتنتمي العقارب إلى ال ARTHROPODA وفئة ال ARACHNIDA، ويٌعد العقرب ذو الذيل الدهني والذي يٌعرف ب Androctonus australis من أشهر الأنواع القاتلة في شمال أفريقيا والشرق الأوسط وهو يتميز بالعدوانية الشديدة تجاه البشر، ويكون تأثيره الأكبر على المسنين والأطفال لضعف الجهاز المناعي، وهناك نوع آخر يٌعرف ب Leiurus quinquestriatus   وهو يعيش في النصف الشمالي والجنوبي لأفريقيا،', 'العقرب', '<p dir=\"rtl\" style=\"text-align:justify\">هناك على كوكبنا ما يقترب من 1200 نوع من العقارب وجميعها خطيرة للغاية، وتكون العقارب الأصغر حجماَ هي الأشد خطورة على الإطلاق،&nbsp; وتنتمي العقارب إلى ال ARTHROPODA وفئة ال ARACHNIDA، ويٌعد العقرب ذو الذيل الدهني والذي يٌعرف ب Androctonus australis من أشهر الأنواع القاتلة في شمال أفريقيا والشرق الأوسط وهو يتميز بالعدوانية الشديدة تجاه البشر، ويكون تأثيره الأكبر على المسنين والأطفال لضعف الجهاز المناعي، وهناك نوع آخر يٌعرف ب Leiurus quinquestriatus &nbsp; وهو يعيش في النصف الشمالي والجنوبي لأفريقيا،</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">والعقارب ماهرة في الصيد وتنشط في الليل، وتختبئ في مناطق بعيدة عن الأعين أو أسفل التربة تحت الصخور أو الأشجار والنباتات والحطام، وفي فترات الجفاف تبحث عن المياه في المنازل القريبة منها، ويمكن للدغة العقرب أن تسبب ألمًا شديدًا يستمر من 48 إلى 72 ساعة، ولذلك إذا قامت العقارب بلدغك اتصل بطبيب متخصص فوراً، ومن المهم أن نعرف شكل العقارب، وفهم سلوكها خاصةًً إذا كنت تعيش في بيئة تنشط فيها هذه الحشرات الخطيرة وذلك كي تتمكن من التعامل معها، وفي هذا المقال سوف نزودك بكل ما تحتاج معرفته عن هذه الحشرات ولذلك تابع القراءة معنا.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>المظهر الخارجي للعقارب</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">للعقارب ثمانية أرجل مزدودة بكماشات أو مخالب، ولها ذيل يحتوي على إبرة شائكة، وعلى الرغم من امتلاكها لمجموعة عيون في وسط رأسها لا تتمكن من الرؤية الجيدة،&nbsp; ، كما أنها تملك خمسة صفوف من العيون على جانبي الرأس، وحينما تتحرك تمتد مخالبها للخارج بشكل منحني ولأعلى نحو الرأس.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>التكاثر ودورة حياة العقارب</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تحمل أنثى العقرب بيضها داخل أجسامها، وعندما تفقس تخرج العقارب الصغيرة التي تشبه العقارب البالغة، وحينما تتقدم في العمر تفقد الطبقة الخارجية من جسمها، وتفضل العقارب العيش في البيئات الدافئة والجافة، وهي تختبيء في النهار أسفل الحجارة والصخر أو تحت الأرض، وهي تنشط في المساء وتخرج لصيد الفريسة والتغذية وهي تأكل السحالي والفئران والطيور، حيث تقوم بحقن ضحاياها بسم عصبي يشلها ويقتلها، وهي تستخدم مخالبها لتناول الطعام وتستخلص منه المواد السائلة وتتخلص من المواد الصلبة، ويتراوح عمر العقارب من ثلاث إلى خمس سنوات ولكن بعض الأنواع قد يعيش حتى 10 إلى 15 سنة وهي حشرات مفترسة، تبقى كامنة في الجحر في انتظار مرور الفريسة وبمجرد أن تشعر باهتزازات الفريسة تقوم بالإمساك بها بمخالبها الموجودة على أطراف أرجلها الأمامية، وإذا كانت الفريسة صغيرة تقوم العقارب بسحقها بسهولة، وتضخ سمها فيها، وبمجرد أن تصبح الفريسة غير قادرة على الهروب تبدأ في تناولها.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>كيف تدخل العقارب إلى المنزل؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تدخل العقارب داخل المنازل بحثاً عن مصادر المياه والطعام، ولذلك يجب إغلاق نقاط الدخول الشائعة باستخدام مادة مانعة للتسرب، كما يجب غلق الفجوات في النوافذ والأبواب والأرضيات، وعند أنابيب السباكة والمرافق وكذلك الفتحات في أساس المنزل أو الحوائط والجدران، كما تدخل العقارب المنزل إذا كنت تخزن الوقود أو الأثاث المستعمل أو تزرع النباتات، وبمجرد دخولها تبحث العقارب عن الأماكن البعيدة عن البشر مثل الحمامات والمطابخ والجراح والقبو&nbsp;وما هي علامات الإصابة بالعقارب في المنزل؟&nbsp;لا تترك العقارب علامات على وجودها في المنزل، ولكن رؤيتها المباشرة هي أكبر دليل على وجودها في منزلك.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>ما مدى خطورة العقارب؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تقوم العقارب بلدغ البشر، ولا تقتل البشر، لكنها سامة، ولها أضرار خطيرة تتمثل في الألم الشديد وشعور بالمخدر الموضعي، والقيء وصعوبة التنفس، كما يكون للأطفال وكبار السن ردود فعل قوية من قرصات العقارب.&nbsp;</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>كيف يمكنني التخلص من العقارب؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">فور اكتشافك للعقارب في منزلك أو بالقرب منه عليك أن تتصل بشركة مكافحة الحشرات بجدة لخطورة التعامل معها، ويقوم الأخصائي بفحص منزلك وإيجاد حل للمشكلة من خلال:</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align: justify;\">فحص الأشياء داخل المنزل وخارجه بما في ذلك الصناديق والمخلفات والمواد المخزنة والحطام.</li>\r\n	<li style=\"text-align: justify;\">&nbsp;المبيدات الحشرية القاتلة للعقارب والتي يتم رشها على المناطق التي توجد فيها أو الأماكن التي تدخل من خلالها للمنزل.</li>\r\n	<li style=\"text-align: justify;\">التخلص من المخلفات والحطام والصخور والنباتات المتساقطة والأثاث والخشب في الهواء الطلق والتي توجد في أنحاء المنزل لأنها تمثل المكان المثالي لها.</li>\r\n	<li style=\"text-align: justify;\">&nbsp;قص العشب والنباتات بالقرب من الأرض وجعلها على بعد قدمين من أساس المنزل.</li>\r\n	<li style=\"text-align: justify;\">معالجة الشاشات الممزقة والأنابيب التالفة وخطوط المرافق والفجوات التي تحيط للمنزل وإغلاقها.</li>\r\n	<li style=\"text-align: justify;\">&nbsp;استخدام المٌعالجات الكيماوية لعلاج العقارب، حيث تٌفضل العقارب الاختباء في أماكن محمية ولذلك تتغلغل المواد الكيميائية لهذه المناطق وتقضى على الحشرات.</li>\r\n</ul>', '<p dir=\"ltr\" style=\"text-align:justify\">There are 1,200 species of scorpions on our earth, most of them are very dangerous, the smallest species are the most dangerous ones, scorpions belong to the ARTHROPODA and to subclass ARACHNIDA, the most dangerous type is known as Androctonus australis in North Africa and the Middle East, it is characterized by aggressiveness towards people, it affects mostly old people and children, because of weak immune system, the other dangerous&nbsp; type known as Leiurus quinquestriatus, it lives in the northern and southern half of Africa.</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Scorpions are perfect killers, they become active at night, hiding in unseen areas under the soil and rocks, trees, plants, they come to our houses searching for water, their bite causes severe pain and lasts from 48 to 72 hours, so you have to contact a specialist immediately. It is important to know the shape of scorpions, and to understand their behavior, especially if you live in an active environment famous for scorpions, so we will provide you with everything you need about scorpions through this article.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>External appearance of scorpions</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">&nbsp;Scorpions have eight legs with claws, they have a tail that contains a needle, they have eyes in the center of their head, but they cannot see well, they have five rows of eyes on head sides, when they move, they extend their claws&nbsp; toward their heads in upward and outward direction.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Scorpions the life cycle</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Females carry eggs inside their bodies, they hatch small scorpions that resemble adult scorpions, they lose the outer layer of their body, scorpions prefer to live in warm and dry environment, they hide in the day under rocks or in the ground, they are active during evening, they hunt and search for food at night, they eat lizards, mice and birds by injecting victims with a neurotoxic that paralyzes and kills them, they extract liquids and get rid of solid food, they live from three to five years, some species may live up to 10 to 15 years.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>How scorpions enter our home?</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Scorpions enter our homes to search of water and food, they enter through entry points such as windows, doors and floors, plumbing pipes and the base of the house, walls, they live in stored furniture or plants, as soon as they enter homes , they search for hidden places such as bathrooms, kitchens&nbsp;signs of scorpions at home Scorpions do not leave any signs at homes, you only seen them directly.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>How serious are scorpions?</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Scorpions bite humans but they rarely kill them, they are toxic and cause serious damage and severe pain, numbness, vomiting and difficulty breathing, children and the old people may suffer from their bites.&nbsp;</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>How can I get rid of scorpions?</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">When you discover scorpions in your home, you must contact Pest Control Company in Jeddah for assistance. The specialist will examine your home and find a solution through:</p>\r\n\r\n<ul dir=\"ltr\">\r\n	<li style=\"text-align: justify;\">Inspect inside and outside the home, including boxes, stored staffs.</li>\r\n	<li style=\"text-align: justify;\">Sprays that kills scorpions when they enter homes.</li>\r\n	<li style=\"text-align: justify;\">Dispose rocks, fallen plants leaves, furniture and wood in outdoors that are found around the house.</li>\r\n	<li style=\"text-align: justify;\">Cut grass and plants near the ground and make them two feet from the foundation of the house.</li>\r\n	<li style=\"text-align: justify;\">Fix torn screens, damaged tubes, seal the gaps that surround the home.</li>\r\n	<li style=\"text-align: justify;\">Use chemical treatments to treat scorpions, because they hide in protected places, so the chemicals in these areas penetrate and eliminate insects</li>\r\n</ul>', 21, 1, 0, '2020-05-08 01:23:38', '2020-05-12 13:24:09', 'العقرب', 'Scorpions', 'حشرة العقرب : معلومات وحقائق وكيفية التخلص من العقارب', NULL, 'العقرب', 'Scorpions', 'Scorpions Insect | Facts & Identification, Control & Prevention', 'There are 1,200 species of scorpions on our earth, most of them are very dangerous, the smallest species are the most dangerous ones, scorpions belong to the ARTHROPODA and to subclass ARACHNIDA, the most dangerous type is known as Androctonus australis in North Africa and the Middle East, it is characterized by aggressiveness towards people, it affects mostly old people and children, because of weak immune system, the other dangerous  type known as Leiurus quinquestriatus, it lives in the northern and southern half of Africa.'),
(21, 'قمل الكتب', 'Booklice', 'uploads/pest_libraries/158893036933e0960b692ed106d7bf32266fe4ef08image.png', 'قمل الكتب أو Order Psocoptera كما يٌطلق على اسمها العلمي عبارة عن حشرات صغيرة للغاية وتٌسمى بهذا الاسم لأنها تنجذب للمناطق العالية الرطوبة والتي تسمح بنمو العفن بسرعة الذي تتغذى عليه للبقاء على قيد الحياة، وكذلك حينما تقوم بتخزين الكتب لفترة طويلة، تٌصبح جاذبة لقمل الكتب، وعلى عكس الإعتقاد الشائع لا تٌشكل هذه الحشرة تهديدا كبيرا على البشر، لكنها تعد مصدر قلق وإزعاج كبير لأصحاب المنازل خاصة أنهم يرونها فجأة وتكون بأعداد كبيرة في المكتبة الخاصة بهم أو في الحمام أو المطبخ حيث أماكن الرطوبة والعفن وقمل الكتب، وقد تكون مٌدمرة بسبب عاداتها الغذائية، وهي تتغذى ببطئ وتتسبب في تلف المحاصيل والكتب وورق الحائط.', 'قمل الكتب', '<p dir=\"rtl\" style=\"text-align:justify\">قمل الكتب أو Order&nbsp;Psocoptera كما يٌطلق على اسمها العلمي عبارة عن حشرات صغيرة للغاية وتٌسمى بهذا الاسم لأنها تنجذب للمناطق العالية الرطوبة والتي تسمح بنمو العفن بسرعة الذي تتغذى عليه للبقاء على قيد الحياة، وكذلك حينما تقوم بتخزين الكتب لفترة طويلة، تٌصبح جاذبة لقمل الكتب، وعلى عكس الإعتقاد الشائع لا تٌشكل هذه الحشرة تهديدا كبيرا على البشر، لكنها تعد مصدر قلق وإزعاج كبير لأصحاب المنازل خاصة أنهم يرونها فجأة وتكون بأعداد كبيرة في المكتبة الخاصة بهم أو في الحمام أو المطبخ حيث أماكن الرطوبة والعفن وقمل الكتب، وقد تكون مٌدمرة بسبب عاداتها الغذائية، وهي تتغذى ببطئ وتتسبب في تلف المحاصيل والكتب وورق الحائط.</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">وفي الكثير من الحالات نٌوصي بالتواصل مع شركة مكافحة الحشرات في جدة لمساعدتك على معرفة الحشرة التي توجد في منزلك وتحديد الإصابة والتعامل معها، ومن خلال هذا المقال سوف نتعرف على المظهر الخارجي للحشرة والأماكن التي تعيش بها وأبرز عاداتها ودورة حياتها وكيفية التخلص منها ولذلك تابع القراءة معنا.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>المظهر الخارجي لقمل الكتب</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يتراوح حجم قمل الكتب من 1 إلى 6 مم، ويختلف لونها حسب نوعها، وللحشرة رأس وبطن كبير، ويكون الجزء الأوسط من جسمها ضيق ولها عيون ضخمة بارزة ومٌركبة على جانبيً الرأس، وتتمتع بقرون استشعار تٌشبه الخيوط وتملك جناحان أماميان أصغر وأجنحة خلفية تستخدمها في الطيران وغزو المنازل، ويكون لها أجزاء فمية لتقوم بمضغ الطعام لكنها لا تعض البشر بها، وهي من الحشرات غير الضارة والتي تتغذى على النباتات المجففة أو المتحللة أو الطعام الذي تقوم بتخزينه.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>دورة حياة قمل الكتب</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تضع أنثى قمل الكتب البيض في فصليً الربيع والصيف، وتكون الحوريات أو الحشرات غير الناضجة مشابهة للحشرة البالغة ولكنها أصغر في الحجم ولا تملك أجنحة، وحينما تتطور في النمو تتحول إلى حشرة قمل الكتب البالغة التي تٌصبح جاهزة لتكرار دورة الحياة والتكاثر من جديد.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>أين يعيش قمل الكتب&nbsp; في المنزل؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">لا يٌعتبر قمل الكتب قملاً حقيقاً، وهو يعيش في الأماكن الدافئة سيئة التهوية وذات الرطوبة المرتفعة، داخل منزلك يوجد قمل الكتب في حجرات التخزين والقبو والأماكن الرطبة والمظلمة، وكذلك في الطوابق السفلية وأماكن تسربات السباكة التي ينمو فيها العفن أو في الحمام حيث أنابيب السباكة وأحواض الإستحمام، وعند خطوط تكييف الهواء، ويزدهر قمل الكتب في القبو أو غرف التخزين المظلمة، ويٌمكنك العثور عليه أيضاً عند الأخشاب والأشجار وتحت الأوراق الجافة والمتساقطة وعلى جذوع وأغصان الأشجار،&nbsp;</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>تغذية قمل الكتب</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يتغذى قمل الكتب على الحبوب والحشرات والنشويات وبالنسبة للكتب يتناول غراء الكتب والأجزاء التالفة والمتحللة منها أو الفطريات والمواد العضوية التي تنمو عليها حينما تٌخزن لفترة طويلة ويمكن أن تجدها في الصناديق الكرتونية المبللة والكٌتب والأوراق والخشب، وكذلك إذا كان لديك مواد مخزنة أو حطام أو كتب مخزنة حول منزلك، ويٌفضل قمل الكتب الورق لذلك يتناول أغلفة الكتب والورق والصور الفوتوغرافية وورق الحائط.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>علامات الإصابة بقمل الكتب؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">العلامة الرئيسية التي تدل على وجود قمل الكتب هو رؤيتها بشكل مباشر في المنزل، وهي تظهر بشكل مفاجئ في المنزل، ويكون حجمها صغير بحيث لا يلاحظها أصحاب المنزل في البداية.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>هل قمل الكتب من الحشرات الضارة؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">لا يٌعتبر قمل الكتب من الحشرات شديدة الخطورة، فهو مجرد مصدر إزعاج للبشر لكنه غير مؤذي لهم أو للحيوانات الأليفة، كما أنه لا يعض البشر، لكن حينما تجده في منزلك فلا شك أن لديك مشكلة في الرطوبة، أو أن حجرة التخزين او المنزل بحاجة إلى المزيد من التهوية والعناية، كما أن وجود قمل الكتب بالقرب من الطعام يٌشير إلى تلفه أو إنتهاء صلاحيته، ولذلك عندما تكتشف مواد مصابة، فقم بالتخلص منها فوراً ورميها بعيدًا وعالج مشكلة الرطوبة والتهوية لديك.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>كيف يمكن القضاء على قمل الخشب؟</strong></h2>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align: justify;\">قم بالبحث عن مصادر الرطوبة خاصة تحت الحوض ومناطق السباكة كما في الحمام، وقم بالبحث تحت الأحواض والجدران وأنابيب الصرف الصحي والأساسات المنزلية، وقم بمعالجة الأماكن المتآكلة لأنها مصدر لنمو العفن والرطوبة.</li>\r\n	<li style=\"text-align: justify;\">قم بفحص الطابق السفلي والجدران للتحقق من أماكن دخول قمل الكتب للمنزل، وقم بسدها بمواد عازلة واستخدم مزيل الرطوبة.</li>\r\n	<li style=\"text-align: justify;\">قم بتهوية المكتبة الخاصة بك وتنظيف الكتب بدقة وتعريضها للهواء الطلق، وإذا كنت رطبة أو مببلة أسمح لها بالجفاف في الشمس لأن غٌلاف الكٌتب قد يحتوي على عدد كبير من الحشرات التي قد تموت بسبب قوة أشعة الشمس.</li>\r\n	<li style=\"text-align: justify;\">البحث عن منطقة تكاثر قمل الكتب والتي قد تكون الكتب الرطبة والأوراق والمراتب القديمة والأثاث المنجد، وتنظيفها جيداً، ورشها بالمبيدات الحشرية التي تحتوي على البيريثرينات أو الروتينون أو الليثرين أو الكلوربيريفوس أو البروبوكور والتي تقضي على الحشرات الزاحفة.</li>\r\n	<li style=\"text-align: justify;\">إذا قام قمل الكتب بتلويث طعامك، قم بالتخلص من الأطعمة التالفة أو الملوثة ورميها، و قم بتهوية المكان وتنظيفه، وقم بتخزين الأطعمة السليمة في أكياس بلاستيكية سواء في الثلاجة أو في مكان آخر بعيد عن أماكن تواجد الحشرة.</li>\r\n	<li style=\"text-align: justify;\">استخدم المواد المبيضة أو الخل أو المواد الكيميائية لقتل الفطريات وتنظيف أماكن العفن الذي ينمو في منزلك.</li>\r\n	<li style=\"text-align: justify;\">تخلص من مصادر المياه وقم بتنظيف الأرضيات وتجفيفها لالتقاط للتخلص من أي بكتيريا.</li>\r\n	<li style=\"text-align: justify;\">الإستعانة بشركة مكافحة الحشرات بجدة لمعالجة مشكلة قمل الكتب بمنزلك.</li>\r\n</ul>', '<p dir=\"ltr\">A Booklice or Order Psocoptera as its scientific name, is a very small insect, it has this name because it is attracted to humid areas like bookstores and libraries, booklice does not has a major threat to humans, but it is a source of inconvenience to homeowners, they notice it suddenly in large numbers in their library or in the bathroom or kitchen where moisture places, they eat slowly and may cause damage to crops, books and wallpapers.</p>\r\n\r\n<p dir=\"ltr\">we recommend you to call the Pest Control Company in Jeddah to help you know more about booklice, determine the infestation and get rid of it, in this article we will discuss the external appearance of the booklice, living places, insect habits, its life cycle and how to get rid of them.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Booklice identification</strong></h2>\r\n\r\n<p dir=\"ltr\">Booklice size ranges from 1 to 6 mm, its color depends on its type, it has a large head and abdomen, narrow middle part of bod, big and prominent eyes on both sides of head, it has two small front wings and larger back wings to fly and invade homes, it has an oral part to chew food, it doesn&rsquo;t bite humans.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>booklice life cycle</strong></h2>\r\n\r\n<p dir=\"ltr\">A female lays eggs in spring and summer, the immature nymphs or are similar to adults insect but they are smaller in size and do not have wings, when they grow up, they turn into adult booklice which is ready for matting.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Where booklice live at your home?</strong></h2>\r\n\r\n<p dir=\"ltr\">Booklice is not considered a real lice, it lives in warm, high humid places. It is located in store rooms, cellar, wet and dark places, lower floors and places where there are plumbing leaks, it may live in the bathroom where plumbing pipes and bath tubes, in air conditioning, you can also find it in the trees and, under falling leaves, tree branches,</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Booklice Feeding habits</strong></h2>\r\n\r\n<p dir=\"ltr\">&nbsp;Booklice eats insects, carbohydrates, books, the glue of books, damaged parts of books, fungi and organic materials that grow on them when stored for a long time, wet cardboard boxes, papers, wood, stored materials, debris, books stored around Your house, it prefers book covers, photographs, and wallpaper.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Signs of infestation of book lice</strong></h2>\r\n\r\n<p dir=\"ltr\">&nbsp;The main sign of booklice infestation is seeing the insect directly at your home, it appears suddenly, home owners may not notice it at first, because of its small size.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>How serious is booklice?</strong></h2>\r\n\r\n<p dir=\"ltr\">Booklice is not considered a dangerous insect, it causes a nuisance to humans but it is not harmful to people or to pets, it does not bite humans, when you find it in your home, you may have a moisture problem, we you find it near food places it indicates that food is damaged or expired, so when you discover an infected material, dispose of it ASAP and throw it away, don&rsquo;t forget to treat humidity problem.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>How to get rid of booklice?</strong></h2>\r\n\r\n<ul dir=\"ltr\">\r\n	<li>Search for moisture location in the plumbing areas, in the bathroom, under the basins, walls, sewage pipes and household structure, treat corrosive places because they are a source of moisture to search for booklice that enter the house, fill them with insulation material and use a dehumidifier.</li>\r\n	<li>Ventilate your library, clean books, and expose them to the open air, allow them to dry in the sun because it may contain insects that may die due to the strong sunlight.</li>\r\n	<li>You have to find booklice breeding area, clean them well, use pesticides that contain pyrethrins, routines, lecithin, chlorpyrifos, or propocur, which kill booklice.</li>\r\n	<li>booklice contaminate your food, so get rid of contaminated food, ventilate food storage areas, store food in plastic bags, refrigerator or away from booklice.</li>\r\n	<li>Use bleaching solution, vinegar or chemicals to kill fungi that grow in your home.</li>\r\n	<li>Get rid of water sources, dry floors to kill any bacteria.</li>\r\n	<li>Call Pest Control Company in Jeddah&nbsp; to help you in solving boolice problem.</li>\r\n</ul>', 14, 1, 0, '2020-05-08 01:32:49', '2020-05-12 13:23:37', 'قمل-الكتب', 'Booklice', 'حشرة قمل الكتب : معلومات وحقائق وكيفية التخلص من قمل الكتب', NULL, 'قمل الكتب', 'Booklice', 'Booklice | Facts & Identification, Control & Prevention', 'A Booklice or Order Psocoptera as its scientific name, is a very small insect, it has this name because it is attracted to humid areas like bookstores and libraries, booklice does not has a major threat to humans, but it is a source of inconvenience to homeowners, they notice it suddenly in large numbers in their library or in the bathroom or kitchen where moisture places, they eat slowly and may cause damage to crops, books and wallpapers.'),
(22, 'قمل الخشب', 'Woodlice', 'uploads/pest_libraries/1588932465f26dc5ea169ba9ed9c27f64e5c8c6142image.png', 'قمل الخشب حشرة بيضاوية، يصل طولها إلى 15 ملم ويكون لها جسم رمادي أو بني غامق وقرون استشعار واضحة، وتملك الحشرة ما يٌشبه القٌشور الخشبية تمتد على الهيكل الخارجي، كما تملك الحشرة حوالي 14 رجل ولا يمكنها أن تطير فقط تقوم بالزحف للبحث عن مكان للاختباء ', 'قمل الخشب', '<p dir=\"rtl\" style=\"text-align:justify\">ينتشر قمل الخشب أو ال Sowbugs&nbsp; في الخريف والشتاء والربيع، بسبب جفاف الجو، ويكون أقل انتشاراً بكثير في فصل الصيف، ويوجد ثلاثة أنواع من قمل الخشب وهم Armadillidium Vulgare أو&nbsp; Pill Woodlouse أو&nbsp; Oniscus asellus، وقد يقوم قمل الخشب بنقل بعض الأمراض، وتلف أثاث المنزل، وفي الكثير من الحالات تدخل هذه الحشرة منزلك عن طريق الصدفة بحثاً عن الرطوبة&nbsp;والعثور على عدد كبير من قمل الخشب أمر غير سار، وعلامة على وجود مشكلة رطوبة في منزلك، حيث تميل الحشرة للتجمع في الأماكن المظلمة والبعيدة عن البشر وتنشط في الليل، حيث يٌعتبر قمل الخشب مصدر إزعاج منتشر لدى الكثير من المنازل ولكنها لا تسبب مشكلة إذا كانت بأعداد كبيرة، ولكن حينما تزداد الإصابة يصعب السيطرة عليها ولا يمكن إيقافها أو التعامل معها، ومن خلال هذا المقال سنتعرف على المظهر الخارجي لقمل الخشب ودورة حياتها وطرق تغذيتها وكيفية التخلص منها بشكل ناجح، ولذلك تابع القراءة معنا.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>المظهر الخارجي لقمل الخشب</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">قمل الخشب حشرة بيضاوية، يصل طولها إلى 15 ملم ويكون لها جسم رمادي أو بني غامق وقرون استشعار واضحة، وتملك الحشرة ما يٌشبه القٌشور الخشبية تمتد على الهيكل الخارجي، كما تملك الحشرة حوالي 14 رجل ولا يمكنها أن تطير فقط تقوم بالزحف للبحث عن مكان للاختباء&nbsp;</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>غذاء حشرة&nbsp;قمل الخشب</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يتغذى قمل الخشب على الأخشاب المٌتحللة أو المتعفة أو الفطريات التي تتكون عليها، كما أنها تتغذى على المواد النباتية والعضوية، وهي تقوم بمهمة الحفاظ على التوازن البيئي الطبيعي من خلال التغذية على الأوراق الميتة والمخلفات والفاكهة المتساقطة وفضلات الحيوانات، كما أنها طعام مٌفضل للكثير من الكائنات الحية وبذلك تدخل في السلسة الغذائية.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>دورة حياة قمل الخشب</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يتكاثر قمل الخشب في الأخشاب المتحللة والمواد العضوية حيث تقوم الأنثى بوضع البيض الذي يوجد على الجانب السٌفلي من جسمها، وتنتظر حتى يفقس الصغار، ويٌمكن لقمل الخشب أن ينٌتج جيل أو جيلين في السنة، وتعيش الحشرة في المناطق الرطبة الباردة تحت الأحجار والصخور وبالقرب من المواد الخشبية، ويمكن للحشرة البالغة أن تعيش لمدة تصل إلى سنتين.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>كيف يدخل قمل الخشب إلى المنزل؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يمكنك العثور على قمل الخشب في حديقة منزلك تحت الصخور والأحجار، وهي تعيش في المناطق الباردة والمظلمة، وتحت ظروف خاصة يمكنها أن تدخل منزلك وهي تعيش في البيئات الرطبة أيضاً لذلك لا تعيش داخل المنزل فترة طويلة، ولكنها إذا نجحت في التواجد داخل منزلك فقد يكون ذلك دليلًا على وجود مشكلة رطوبة يجب عليك معالجتها سريعاً، وقد يكون من الصعب عليك تحديد أماكن الإصابة ولذلك عليك البحث عن أماكن الرطوبة حيث يمكنك أن تجد فيها قمل الخشب.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>ما هي أضرار قمل الخشب؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">قمل الخشب حشرة غير ضارة ولا تشكل أي مخاطر صحية على البشر، لكنها يٌمكن أن تٌسبب بعض الأضرار السطحية لأرضيات الباركيه الخشبية أو الأثاث وأي مناطق خشبية داخل منزلك، وقد يكون التلف سطحياً، وتٌعد رؤية القشرة الخشبية التالفة للخشب دليل على حدوث إصابة ما عليك من التحقق منها من خلال الفحص والمٌعالجة.&nbsp; وتميل الحشرة للتجمع في المناطق المظلمة والرطبة، أما داخل حديقتك، قد تٌسبب الكثير من الأذى بسبب التغذية على النباتات أو المحاصيل كما أنها تتناول الأوراق الجافة.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>كيفية القضاء على قمل الخشب في المنزل؟</strong></h2>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align: justify;\">نظرًا لأن وجود قمل الخشب علامة على مشكلة الرطوبة الزائدة أو تواجد مصادر طعام دون حماية أو وجود خشب تالف أو متعفن، لذلك يجب تحديد سبب المشكلة ومكانها والقيام بمعالجتها سواء بتهوية الطابق السفلي أو استخدام مزيل الرطوبة</li>\r\n	<li style=\"text-align: justify;\">التنظيف بالمكنسة الكهربائية&nbsp; للتخلص من الحشرات التي تراها مباشرة.</li>\r\n	<li style=\"text-align: justify;\">يجب خلو منزلك من مشاكل الرطوبة والفطريات التي تكون العفن، من خلال سد مناطق الدخول المحتملة لقمل الخشب، تنظيف أنابيب السباكة والصرف الصحي ومعالجة المشاكل أو تسربات المياه.</li>\r\n	<li style=\"text-align: justify;\">قبل إعادة تنظيف الحديقة أو تقليم الأشجار دع الحديقة تجف جيداً.</li>\r\n	<li style=\"text-align: justify;\">تخلص بإستمرار من الأوراق والحشرات والحيوانات الصغيرة الميتة حول جدران الحديقة من الداخل والخارج.</li>\r\n	<li style=\"text-align: justify;\">قم بتقليم النباتات المحيطة بالمنزل والقريبة منه للحفاظ على تهوية جيدة للمنزل ولمنع انتشار الحشرات من خلالها للمنزل.</li>\r\n	<li style=\"text-align: justify;\">&nbsp;قم بفحص المواد الخشبية أو الحطب التي تجلبها لداخل المنزل للتأكد من خلوها من الحشرات.</li>\r\n	<li style=\"text-align: justify;\">قم بسد الشقوق والفجوات في أساس المنزل أو الجدران لمنع خول قمل الخشب وغيره من الحشرات.</li>\r\n	<li style=\"text-align: justify;\">رش تراب الدياتومي في الأماكن ذات الرطوبة العالية والأماكن المظلمة وأركان المنزل حيث يعمل كمبيد حشري طبيعي وتٌساعد على التخلص من قمل الخشب والحشرات الأخرى.</li>\r\n	<li style=\"text-align: justify;\">تنظيف الأنابيب التي توجد على سطح المنزل للتخلص من الرطوبة الزائدة.</li>\r\n	<li style=\"text-align: justify;\">تخلص من مناطق معيشة قمل الخشب في المنزل سواء الأثاث الخشبي أو العناصر التالفة والسجاد الرطب، وحينما تقوم بإبعادها عن المنزل يقلل من جذب الحشرة لمنزلك.</li>\r\n	<li style=\"text-align: justify;\">يمكن استخدام النبضات الإلكترونية للتخلص من قمل الخشب، والتي تقوم بإرسال نبضات صامتة متواصلة تقوم بطرد قمل الخشب من المنزل ويمكنك شرائها من النت&nbsp;</li>\r\n	<li style=\"text-align: justify;\">استخدم المواد الكيميائية والمبيدات القاتلة للحشرات الزاحفة مثل قمل الخشب، ولا تنسى تنظيف المنطقة التي تقوم برشها لحماية أهل منزلك والحيوانات الأليفة.</li>\r\n	<li style=\"text-align: justify;\">التواصل مع شركة مكافحة الحشرات بجدة لمساعدتك في اتخاذ خطوات فعّالة للتخلص من قمل الخشب.</li>\r\n</ul>', '<p dir=\"ltr\" style=\"text-align:justify\">A Woodlice is originated from suborder Oniscidea, it spreads in autumn, winter and spring, because of dry weather, it is less dominant in summer season, there are three types of wood lice: Armadillidium Vulgare , Pill Wood Louse&nbsp; and Oniscus asellus,&nbsp; it is known that woodlice could&nbsp; transmit diseases to human, it may cause damage to furniture, it enters your house to search of moisture and safe shelter.</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">When house owners discover woodlice, it may be a sign of a moisture problem, woodlice tend to gather in dark places, it becomes active at night, it causes a nuisance when presents in large numbers, when infection increases, it is difficult to control, through this article we will know how to identify woodlice, life cycle, feeding habits, how to get rid of them.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Woodlice identification</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Woodlice is an oval insect, 15 mm in length, it has a gray or brown color, prominent sensing pods, it has wood scales on the outer structure, it also has about 14 legs.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Woodlice food</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Woodlice eats rotten wood, fungi, it also eats plant and organic materials, it has a great impact on natural environmental balance, because it eats dead leaves, fallen fruits animal waste.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Woodlice life cycle&nbsp;</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Woodlice reproduce in organic materials and rotten wood, females maintain eggs on the lower side of its body, it lays eggs and wait till hatching, and woodlice can produce one or two generations per year, it lives in cold, moisture places, we may find them in rocks and near wooden materials, adults may live up to two years.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>How woodlice get inside your home?</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">You can find wood lice in your garden under rocks and stones, it lives in cold and dark places, under special conditions it enters your home, it also lives in humid environments, so they do not live inside the house for a long time, but if you find it inside your house it is a sign of a moisture problem, it may be difficult for you to determine the locations of the infestation, so you should look for moisture places.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>&nbsp;Wood lice Damages&nbsp;</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Woodlice has no harm effects to humans, but they can cause some damage to wooden floors such as furniture, we you find a wood damage it may be a sign of infestation, woodlice tend to aggregate in the dark and humid areas, inside your garden, it may cause a lot of harm due to eating plants or crops.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>How to get rid of woodlice at your home?</strong></h2>\r\n\r\n<ul dir=\"ltr\">\r\n	<li style=\"text-align: justify;\">In cases of excessive moisture, you have to locate the moisture location, identify and treat the problem either by ventilation or using a dehumidifier</li>\r\n	<li style=\"text-align: justify;\">Vacuuming the house to get rid of woodlice.</li>\r\n	<li style=\"text-align: justify;\">Solve fungi problems, block potential entry areas, clean plumbing and sewage pipes and tackling problems or water leaks.</li>\r\n	<li style=\"text-align: justify;\">Before pruning the trees, let the garden dry first.</li>\r\n	<li style=\"text-align: justify;\">Get rid of dead leaves, insects around the garden walls.</li>\r\n	<li style=\"text-align: justify;\">Trim the plants around and near the house to maintain good ventilation for the home and to prevent insects from spreading.</li>\r\n	<li style=\"text-align: justify;\">Check the wood materials before you enter it to the house</li>\r\n	<li style=\"text-align: justify;\">Seal gaps in the house or walls to prevent wood lice&nbsp;</li>\r\n	<li style=\"text-align: justify;\">use Diatomaceous dust in high humidity places, corners where it acts as a natural insecticide and helps to get rid of wood lice and other insects.</li>\r\n	<li style=\"text-align: justify;\">Clean the roof pipes to get rid of excessive moisture.</li>\r\n	<li style=\"text-align: justify;\">Get rid of damaged furniture and wet carpets to reduce the attraction of woodlice to your home.</li>\r\n	<li style=\"text-align: justify;\">Use Electronic pulses to get rid of wood lice, it sends silent pulses that kill woodlice.</li>\r\n	<li style=\"text-align: justify;\">Use chemicals repellent and pesticides and clean the area to protect your household and pets.</li>\r\n	<li style=\"text-align: justify;\">Contact Pest Control Company Jeddah to help you take effective steps to get rid of wood lice.</li>\r\n</ul>', 17, 1, 0, '2020-05-08 02:07:45', '2020-05-12 13:24:02', 'قمل-الخشب', 'Woodlice', 'حشرة قمل الخشب : معلومات وحقائق وكيفية التخلص قمل الخشب', NULL, 'قمل الخشب', 'Woodlice', 'Woodlice | Facts & Identification, Control & Prevention', 'A Woodlice is originated from suborder Oniscidea, it spreads in autumn, winter and spring, because of dry weather, it is less dominant in summer season, there are three types of wood lice: Armadillidium Vulgare , Pill Wood Louse  and Oniscus asellus,  it is known that woodlice could  transmit diseases to human, it may cause damage to furniture, it enters your house to search of moisture and safe shelter.'),
(23, 'النحل', 'Bees', 'uploads/pest_libraries/1588934010525ba40c14bbf68fff6b56d3c9e7718cimage.png', 'يختلف لون النحل وفقاً لنوعه، ويشترك في وجود مجموعة من الخطوط السوداء والبنية، ويصل حجمها لنصف إلى ثلاثة أرباع البوصة، وتضم خلية النحل من  10000 إلى 50000 نحلة وتنتج من 20 إلى 80 رطل عسل، وتختلف فئات النحل في الشكل، وأغلب النحل الذي نشاهده ينتمي لفئة العمال ويكون طولهم حوالي 3/8 بوصة، ولديهم أربعة أجنحة واضحة وملونة باللون الأسود أو البني والأصفر، وتكون البطن أكبر من الصدر والرأس.', 'النحل', '<p style=\"text-align:justify\">يضم النحل مجموعة كبيرة من الحشرات التي تنتمي للحشرات القارصة، وينتمي لفئة Order&nbsp;Hymenoptera و عائلة ال Super-family&nbsp;Apoidea، ويعد نحل العسل الأوروبي أشهر أنواع النحل المعروفة، ولا يمثل نحل العسل إلا جزء صغير من حوالي 20000 نوع من النحل، ويعتبر نحل العسل أشهر أنواع النحل ولا يٌمثل تهديدا للبشر، ولكن يجب الحذر من نحل العسل الإفريقي والذي يلدغ البشر فور شعوره بالتهديد والنحل من الحشرات غير العدوانية التي لا تلدغ البشر إلا بعد الشعور بالتهديد، ولكنها حينما تلدغ البشر تسبب عدد كبير من القرصات، ويصنع النحل عشه فوق الأرض في أماكن آمنة ويصل عدد المستعمرة لعشرات الآلاف، حيث أنه من الحشرات الاجتماعية التي تضم ثلاث طبقات: الملكة والنحلة العاملة والنحل الطائر من الذكور.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>مظهر&nbsp;النحل</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يختلف لون النحل وفقاً لنوعه، ويشترك في وجود مجموعة من الخطوط السوداء والبنية، ويصل حجمها لنصف إلى ثلاثة أرباع البوصة، وتضم خلية النحل من&nbsp; 10000 إلى 50000 نحلة وتنتج من 20 إلى 80 رطل عسل، وتختلف فئات النحل في الشكل، وأغلب النحل الذي نشاهده ينتمي لفئة العمال ويكون طولهم حوالي 3/8 بوصة، ولديهم أربعة أجنحة واضحة وملونة باللون الأسود أو البني والأصفر، وتكون البطن أكبر من الصدر والرأس.</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">ويملك النحل الطنان شعر أكثر من نحل العسل، ويكون لديه شعر أصفر على الصدر ويملك بطن مستديره، ويصنع عشه تحت الأرض، وتكون أصغر بكثير من مستعمرات نحل العسل، ويبدو عسل النحل الأفريقي مشابه لنحل العسل، إلا أنه أكثر عدوانية ويلدغ في أعداد كبيرة، ويبني المستعمرة في&nbsp; في نفس أماكن نحل العسل، أما النحل يظهر في أسراب خلال وتقوم الملكة بوضع البيض طيلة فترة حياتها التي قد تمتد لخمس سنوات، وتكون مهمة الذكور هي التزاوج مع ملكة النحل وتخصيبها، وتقوم الإناث من النحل العامل بحماية الخلية، وينجذب النحل للنباتات والزهور، وتقوم بتوزيع حبوب اللقاح ونشرها من نبات لآخر حيث أنه يلتصق بالشعر الذي يٌغطي&nbsp; جسدها.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>كيف يدخل النحل للمنزل؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يدخل النحل للمنزل أثناء رحلة بحثه عن العٌش المناسب، ويميل النحل للتواجد في الأماكن الآمنة والمظلمة، وتكون الحدائق والجدران الخارجية للمنزل معرضة لغزو النحل،ويطير النحل في أسراب، وحينما يتجمع فوق شجرة، يقوم بإرسال بعض النحل لتحديد مكان جديد لبناء العش، وحينما يعثر عليه يبدأ في بناء عش جديد، وهناك نوعان من أسراب النحل وهما الأولية والثانوية، تقود ملكة النحل السرب الأولي، وتضم العمال لحماية البيض والملكة، أم السرب الثانوي يقوده النحل البكر وهي التي تغزو المنازل من خلال شقوق الأساس أو الفجوات أو النوافذ المفتوحة.&nbsp;</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>دورة حياة النحل</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تتكون دورة حياة النحل من ثلاث مراحل رئيسية: اليرقات، الخادرة، الحشرة البالغة، حيث تضع الملكة البيض المخصب وغير المخصب، والذي يفقس ليكون النحل العامل والملكة، ويفقس البيض غير مخصب إلى الذكور، وبعد ثلاثة أيام يفقس البيض، وتبدأ مراحل تطور النحل.</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align: justify;\">البيض: يتم وضع البيض في الشمع، وتكون خلية البيض سداسية، و تكون البيضة بحجم حبة الأرز وتقف في نصف خلية النحل، وتفقس في اليوم الثالث، وتضع الملكة ما بين 2000 و 3000 بيضة في اليوم الواحد، ويتحول البيض المخصب لإناث سواء العمال والملكات والبيض غير المخصب الذكور والتي تكون خلاياها أكبر، وتوضع ملكة النحل في خلايا خاصة تسمى الخلية الملكية.</li>\r\n	<li style=\"text-align: justify;\">اليرقة: بعد 3 أيام، تتطور البيضة إلى يرقة ويكون لونها أبيض ولا تملك أجنحة أو أرجل ولا ترى، ويٌغذي النحل العامل اليرقات لمدة ثلاثة أيام من مزيج من العسل وحبوب اللقاح، وتتغذى اليرقة التي ستكون ملكة على غذاء ملكات النحل أو لبن النحل، وهي مادة مصنوعة في الغدد الخاصة في الرأس والغدد اللٌعابية، وينتجها النحل الصغير الذي يكون عمره بين 5 و 14 يومًا، ويحتوي على الفيتامينات والسكر والدهون، ويبدأ الجلد الخارجي في التساقط عدة مرات، وبعد 6 أيام يتم تغطية خلية البويضة التي تحتوي على اليرقة بطبقة من الشمع بواسطة العمال.</li>\r\n	<li style=\"text-align: justify;\">الخادرة: تلف اليرقة داخل الشرنقة حول نفسها وفي النهاية تتطور إلى نحلة بالغة وتخرج الحشرة البالغة من البويضة من خلال مضغ الشمع.</li>\r\n	<li style=\"text-align: justify;\">النحل البالغ: يظهر النحل البالغ من الشرنقة، ويملك الأجنحة والأرجل والرأس والصدر والبطن، ويستغرق ظهور الملكة 16 يومًا من ظهورها من البويضة، ويحتاج النحا العامل إلى 18 أو 22 يومًا للتطور بشكل كامل، أما الذكور تتطلب حوالي 24 يومًا.</li>\r\n</ul>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>ماذا يأكل النحل</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تقوم الإناث بنقل حبوب اللقاح، ولهذا تتغذى على رحيق الزهور والنباتات وكذلك بقية النحل،باستخدام ما يٌشبه اللسان، والبعض يتغذى على حبوب اللقاح خاصة النحل البالغ واليرقات، فهي تحتوى المواد البروتينية التي تمد النحل بالطاقة، ويملك النحل عضو يٌحول به الرحيق إلى عسل.&nbsp;</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>هل النحل خطير؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">على الرغم من فوائد النحل الكثيرة، إلا أن وجود خلية نحل بالقرب من منزلك أمر خطير، حيث يقوم النحل بقرص البشر لحماية نفسه والدفاع عن العش، وتكون معظم اللدغات مؤلمة، وقد يكون للبعض منهم ردود فعل تحسسية، كما أنه حينما تتخمر خلايا العسل داخل الشقوق في جدران المنزل تعمل على ضرر هياكل المنزل والجدران وتلويثها وجذب المزيد من الحشرات</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">أعراض قرصات النحل</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يلدغ النحل الشخص عشرات المرات خلال فترة زمنية قصيرة أو يقوم بهجوم جماعي عنيف يضم مئات النحل، حيث يتم غرس إبرة النحل في الجلد، ولذلك يجب عليك كشط مكان القرصة بدلا من الضغط عليها لنزع الإبرة، لأن الضغط قد يضخ المزيد من السم في الجسم، وتشمل أعراض القرصات:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\">شعور بالألم.</li>\r\n	<li style=\"text-align: justify;\">التهاب موضعي للجلد حول مكان اللدغة.</li>\r\n	<li style=\"text-align: justify;\">ظهور بقعة بيضاء مكان ثقب الإبرة,</li>\r\n	<li style=\"text-align: justify;\">حدوث رد فعل حساسية في بعض الأوقات.</li>\r\n	<li style=\"text-align: justify;\">&nbsp;غثيان وقيء وألم الصدر وتورم الوجه أو الفم.</li>\r\n	<li style=\"text-align: justify;\">&nbsp;صعوبة في البلع.</li>\r\n	<li style=\"text-align: justify;\">صعوبة التنفس مع تشنجات قوية.</li>\r\n	<li style=\"text-align: justify;\">الالتهابات البكتيرية الثانوية بعد حدوث عدوى الجلد، نتيجة عدم تنظيف مكان القرصة بشكل جيد.</li>\r\n	<li style=\"text-align: justify;\">ردود حساسية قوية تشمل تورم وانتفاخات حمراء على الجلد، صعوبة في التنفس بسبب تورم البلعوم وضيق القصبة الجوية، تهيج الجلد، وتكون هذه الأعراض أكثر شيوعاً لدى الذكور.</li>\r\n</ul>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">كيفية التخلص من النحل؟</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تجنب وجود ان النحل في محيط منزلك او داخل المنزل قد يمنع حدوث الكثير من المشاكل في المقام الأول على عكس الدبابير فان معظم أنواع النحل ليست عدوانية بشكل طبيعي ولا تصبح عدوانية الا في بعض الحالات مثل الاستفزاز او الهجوم علي اماكن الخلية او الاعشاش الخاصة به وقد تساعدك المعلومات والنصائح في الوقاية منها&nbsp;</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align: justify;\">تحديد نوع النحل الذي يوجد بمنزلك أو بالقرب منه.</li>\r\n	<li style=\"text-align: justify;\">التخلص الكامل من الخلية وذلك بمساعدة أخصائي مكافحة الحشرات بجدة.&nbsp;</li>\r\n</ul>', '<p dir=\"ltr\" style=\"text-align:justify\">Bees have many species that belongs to stinging pests, Order Hymenoptera, Super-family Apoidea, European honey bees are the most common species of bees, most of bee species is not a threat to humans, but people have to care from African honeybees, they bite human when feel threatened&nbsp;Bees bite people when threatened, causing sensitive reactions, they build nest over the ground in hidden locations, bee colonies contain thousands of members, bees are social insect, including: the queen, Workers and flying bees.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>&nbsp;Bees Definition&nbsp;</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Bees color varies according to type, their body are characterized with black and brown strips, size may reach half to three quarters of an inch. The beehive includes 10,000 to 50,000 bees, produces from 20 to 80 pounds of honey, bees differ in shape, and most of them belong to workers, they are about 3/8 inches long, they have four wings, colored black, brown and yellow, and the abdomen is larger than the chest and head.</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Other types of bees have more hair than honeybees, yellow hair on the chest and have a round belly, make their nest underground, and be much smaller than honeybee colonies, African bee looks similar to honeybees, but it is more aggressive and stings in large numbers, they build The colony is in the same places as honey bees, while bees appear in swarms during.</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Queen lays eggs throughout its life span, males mate with the queens and fertilize them, worker females protect the cell, bees are attracted to plants and flowers, spread pollen from one plant to another as it sticks to the hair that covers Her body.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>How Bees enter the house?</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Bees enter the house when searching for a nest location, they tend to live in safe and dark places, gardens and outer walls of the house, they fly in swarms, when they gather over a tree, they send some bees to define a new place to build the nest, when they find it, they begin to build a new nest, there are two types of bee swarms, primary and secondary, queen lead the first swarm which includes workers to protect the eggs and the queen, the secondary swarm is led by virgin bees who invade homes through cracks, gaps, or open windows.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Bees life cycle</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Bees life cycle consist of three main stages: the larvae, the pupa, the adult bee, queen lays the fertilized and unfertilized eggs, eggs hatch unfertilized eggs and turn to males, after three days&#39; eggs hatch.</p>\r\n\r\n<ul dir=\"ltr\">\r\n	<li style=\"text-align: justify;\">Eggs: eggs are laid in wax, egg cell is hexagonal, its size looks like rice and stands in middle of the cell, on the third day, egg hatch, queen lays between 2000 and 3000 eggs per day, the fertilized egg turns to be females, unfertilized eggs turns to be workers and queens, males cells are larger, the future queen bee is placed in a special cell called the royal cell.</li>\r\n	<li style=\"text-align: justify;\">The larva: After 3 days, egg developed into a white larva which has no wings or legs and it is blind, worker bees feed the larvae for three days from a honey mixture, larva, that will be a queen, feeds on royal jelly or bee milk, it is made salivary glands. It is produced by young bees who are between 5 and 14 days old, it contains vitamins, sugar and fat, the outer skin begins to fall several times. After 6 days, the egg cell that contains the larva is covered with a layer of wax by workers.</li>\r\n	<li style=\"text-align: justify;\">Pupa: The larva is wrapped inside the cocoon around itself, it develops into an adult bee by chewing the wax cell.</li>\r\n	<li style=\"text-align: justify;\">Adult bees: adult bees appear from the cocoon, it has wings, legs, head, chest and abdomen, and it takes 16 days for the queen to appear from the egg, the workers requires 18 or 22 days to fully develop, males require about 24 days.</li>\r\n</ul>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>What do bees eat</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Bees feed on the nectar of flowers and plants, using a tongue like organ, some bees feed on pollen, especially adult bees and larvae, which contain protein substances that full of energy, bees have an organ that turns the nectar into honey.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Are bees dangerous?</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">It is dangerous when you have bee nest near your home, bees bite humans to protect themselves and defend the nest, most bites are painful, some of them may have severe allergic reactions.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Symptoms of bee stings</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Bees sting a person many times during a short period of time, or attack human in groups, bee needle is implanted into the skin, so you should scrape the place of the sting instead of pressing over it to remove the needle, pressure may pump more poison into the body.</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">&nbsp;Symptoms of bee stings include:</p>\r\n\r\n<ul dir=\"ltr\">\r\n	<li style=\"text-align: justify;\">Moderate to severe pain.</li>\r\n	<li style=\"text-align: justify;\">Local inflammation of the skin around the bite.</li>\r\n	<li style=\"text-align: justify;\">appearance of a white spot in needle sting.</li>\r\n	<li style=\"text-align: justify;\">allergic reaction.</li>\r\n	<li style=\"text-align: justify;\">Nausea, vomiting, chest pain and swelling of the face or mouth.</li>\r\n	<li style=\"text-align: justify;\">&nbsp;Difficult swallowing.</li>\r\n	<li style=\"text-align: justify;\">short breathing with muscles cramps.</li>\r\n	<li style=\"text-align: justify;\">Bacterial infection due to skin infection.</li>\r\n	<li style=\"text-align: justify;\">severe allergic reaction that include inflammation, swelling, difficult in breathing due to pharynx swelling.</li>\r\n</ul>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>How to get rid of bees?</strong></h2>\r\n\r\n<ul dir=\"ltr\">\r\n	<li style=\"text-align: justify;\">Determine the type of bee in your home.</li>\r\n	<li style=\"text-align: justify;\">Contact pest control company in Jeddah to remove the nest completely.</li>\r\n</ul>', 18, 1, 0, '2020-05-08 02:33:30', '2020-05-12 13:24:02', 'النحل', 'Bees', 'حشرة النحل : معلومات وحقائق وكيفية التخلص من النحل', NULL, 'النحل', 'Bees', 'Bees | Facts & Identification, Control & Prevention', 'Bees color varies according to type, their body are characterized with black and brown strips, size may reach half to three quarters of an inch. The beehive includes 10,000 to 50,000 bees, produces from 20 to 80 pounds of honey, bees differ in shape, and most of them belong to workers, they are about 3/8 inches long, they have four wings, colored black, brown and yellow, and the abdomen is larger than the chest and head.');
INSERT INTO `pest_libraries` (`id`, `name_ar`, `name_en`, `image`, `meta_description_ar`, `image_alt`, `description_ar`, `description_en`, `sort`, `is_active`, `home_order`, `created_at`, `updated_at`, `slug_ar`, `slug_en`, `meta_title_ar`, `sub_pest`, `keywords_ar`, `keywords_en`, `meta_title_en`, `meta_description_en`) VALUES
(24, 'الذباب', 'Flies', 'uploads/pest_libraries/158894782611d97147bd1e91a4a80aef46f15ff81fimage.png', 'ينتمي الذباب إلى رٌتبة الحشرات Diptera ، وهناك أكثر من 110.000 نوع من الذباب، ويساعد الذباب في تلقيح النباتات والزهور، كما أنه يقضى على حشرة المن من خلال التغذية عليها، والذباب من الحشرات المتطفلة والتي تتغذى على يرقات الخنفساء أو العث وبعض الحشرات الأخرى، ويستخدم العلماء الذباب في بعض الدراسات الوراثية ومن أشهر أنواع الذباب الذباب المنزلي أو الMusca domestica، والتي تعيش في أماكن البشر، وهو من الأنواع الخطرة التي تنقل المرض للإنسان والحيوانات الأليفة، وٌيساعد فهم عادات الذباب ودورة حياتهم على التخلص من الذباب بشكل ناجح وسريع.', 'الذباب', '<p dir=\"rtl\">ينتمي الذباب إلى رٌتبة الحشرات Diptera ، وهناك أكثر من 110.000 نوع من الذباب، ويساعد الذباب في تلقيح النباتات والزهور، كما أنه يقضى على حشرة المن من خلال التغذية عليها، والذباب من الحشرات المتطفلة والتي تتغذى على يرقات الخنفساء أو العث وبعض الحشرات الأخرى، ويستخدم العلماء الذباب في بعض الدراسات الوراثية ومن أشهر أنواع الذباب الذباب المنزلي أو الMusca domestica، والتي تعيش في أماكن البشر، وهو من الأنواع الخطرة التي تنقل المرض للإنسان والحيوانات الأليفة، وٌيساعد فهم عادات الذباب ودورة حياتهم على التخلص من الذباب بشكل ناجح وسريع.</p>\r\n\r\n<h2 dir=\"rtl\"><strong>شكل الذباب</strong></h2>\r\n\r\n<p dir=\"rtl\">يصل طول الذباب لحوالي 1/4 بوصة، وتملك الذبابة الناضجة ذبابة جناحان وأربعة خطوط طولية سوداء في منطقة الظهر، وتملك يرقات الذباب ما يشبه الخياشيم للتنفس تحت الماء ويعيش الذباب حتى 3 أسابيع، وينشط في الفصول الحارة والدافئة، وتودع الأنثى من 50 إلى 100 بيضة خلال المرة الواحدة وبما يٌعادل 500 بيضة في حياتها، وتقوم بوضع البيض في المواد المتعفنة، ويفقس البيض خلال 12 ساعة وتبدأ اليرقات فى التغذية على المواد المحيطة بها لكي تنمو وهي تمر بثلاث مراحل، وهي اليرقة أو الخادرة أو الشرنقة ثم الذبابة البالغة، وتحدث أطوار النمو خلال 10 أيام ويٌسمى الذباب غير الناضج باليرقة والتي تعيش في الماء أو في النباتات الرطبة أو المتعفنة أو داخل الأنسجة الحيوانية، وهي تتغذى على النباتات أو الحشرات، وحينما تكون في الماء تتغذى من خلال أنبوب التنفس لتحصل على الهواء.</p>\r\n\r\n<h2 dir=\"rtl\"><strong>ماذا يأكل الذباب؟</strong></h2>\r\n\r\n<p dir=\"rtl\">يفضل الذباب مجموعة كبيرة من الأطعمة سواء التالفة أو المتخمرة وذات الرائحة القوية كما أنه يتغذى على الأطعمة السائلة، ولذلك يمكن رؤية الذباب حولنا في كل مكان، ومن أهم المواد الغذائية الجاذبة للذباب</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li>السوائل مثل الخل وعصير التفاح&nbsp;</li>\r\n	<li>الفواكه مثل الموز والعنب والخوخ والأناناس والطماطم والمخلل والبطاطس والبصل.</li>\r\n	<li>الأطعمة التي تحتوى على نسبة عالية من السكريات مثل الحلويات.</li>\r\n</ul>\r\n\r\n<h2 dir=\"rtl\"><strong>أضرار الذباب</strong></h2>\r\n\r\n<p dir=\"rtl\">ينقل الذباب الكثير من الأمراض الخطيرة مثل الملاريا والحمى الصفراء والجمرة الخبيثة والزحار وحمى التيفوئيد والداء العليقي، وبعض أنواع الذباب تفسد الفواكه والمحاصيل الزراعية، كما يمكن للذباب أن يتلف المنتجات الغذائية والخضروات، ويقوم بتلويث الطعام بمجرد الوقوف عليه وقد يتسبب في أمراض الجهاز الهضمي والقئ والإسهال أو بعض النزلات المعوية الحادة أو التسمم الغذائي في الحالات الأكثر خطورة مثل السالمونيلا و الإي-كولاي والتيفويد والكوليرا والزحار والفيروسات الأخرى ولذلك يجب التخلص من الأطعمة التي يقف فوقها الذباب أو حفظها بشكل جيد بعيداً عن مصادر التلوث.</p>\r\n\r\n<p dir=\"rtl\">كما يؤدي انتشار الذباب إلى الكثير من الخسائر الإقتصادية للشركات عن طريق تلوث الأطعمة الغذائية، كما أن وجود الذباب في أي مكان يمنح سمعة سيئة ولا تشجع أحد على التعامل مع من فيه لشعورهم بأن المكان غير صحي، وهناك بعض أنواع الذباب التي تقوم بقرص البشر وتتسبب في التهابات جلدية وحساسية والتورم والحكة، وإذا تُركت مشكلة الذباب دون حل ستزداد الإصابة وتتحول لمشكلة، لأن الكثير من الذباب يمكنه النضج الجنسي والتكاثر خلال سبعة أيام فقط وينتج البيض بعد التكاثر ويزيد من المشكلة مرة أخرى.</p>\r\n\r\n<h2 dir=\"rtl\"><strong>التخلص من الذباب</strong></h2>\r\n\r\n<ul dir=\"rtl\">\r\n	<li>تخلص من المواد الغذائية التالفة أولا بأول وكذلك المواد العضوية حتى لا تكون مصدر جذب للذباب، وذلك من خلال وعضها في أكياس القمامة وغلقها بشكل جيد.</li>\r\n	<li>&nbsp;تخلص من بقايا الطعام وقم بتنظيف سلة القمامة بشكل مستمر</li>\r\n	<li>إبقاء النوافذ والأبواب مغلقة بشكل مستمر حتى لا يدخل الذباب للمنزل.</li>\r\n	<li>وضع أنظمة طاردة للذباب على الأبواب لمنع الذباب من دخول المنزل.</li>\r\n	<li>تركيب أبواب أوتوماتيكية تٌفتح وتٌغلق عند الحاجة.</li>\r\n	<li>وضع المصائد اللاصقة أو التي تصدر الأشعة فوق البنفسجية حول المنزل والتي تقلل من أعداد الذباب.&nbsp;</li>\r\n	<li>وضع شرائط الراتنج المٌعلقة في أماكن نشاط الذباب، ويجب وضع شريط واحد مقاس 10 بوصات لكل 1000 قدم مكعب، ثم تثبيت الشرائط على مسافة 6 أقدام من الأرض وتكون هذه الطريقة ناجحة وتعمل حتى مدة تصل إلى 3 أشهر.</li>\r\n	<li>تركيب مصائد الضوء في الهواء الطلق لتجنب جذب المزيد من الذباب إلى المنزل، ثم قم بوضعها على بٌعد 15 قدم من الأبواب والمداخل وفي المناطق المظلمة البعيدة عن الشمس وذلك على بٌعد 5 أقدام على الأقل من مناطق تحضير الطعام لتقليل خطر تلوث الطعام، وقم بالحفاظ على المصيدة وبدلها في فصل الربيع، قبل موسم نشاط الذباب لأن المصابيح القديمة لا تصبح جذابة للذباب، كما يجب أن تقوم بتنظيفها بانتظام لأن الذباب الميت يعمل كغذاء للحشرات الأخرى.</li>\r\n	<li>يتم تدريب فنيين شركة مكافحة الحشرات بجدة على أحدث التقنيات والمهارات في مجال مكافحة الحشرات بأنواعها كما أن الفريق يتمتع بخبرة عالية في مجال التخلص من الذباب،&nbsp; علاج الإصابة بالذباب، ويتم معالجة مشكلة الذباب بعد التحديد الصحيح لنوع الحشرة والعثور على أماكن التكاثر في المنزل والتخلص من الذباب البالغ أو اليرقات.</li>\r\n</ul>', '<p dir=\"ltr\" style=\"text-align:justify\">Flies belong to the family Diptera, there are more than 110,000 species of flies, they cause damage to both plants and flowers, through feeding on them, flies also feed on beetle, larvae and other insects, scientists use flies in some genetic studies Domestic&nbsp; flies are One of the most common types flies, that called Musca domestica, they live humans locations, it is one of the dangerous types that transmits disease to humans and pets, when we understand flies habits and their life cycle, this will help us to get rid of flies successfully.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Flies identification</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">flies are approximately 1/4 inch in length, adult flies have two wings and four black longitudinal lines in the back, larvae have special organs to breathe underwater&nbsp;Flies live up to 3 weeks, they appear more in hot or warm weather, female lay from 50 to 100 eggs every time, and up to 500 eggs during its life cycle, it lays eggs in rotten places, eggs hatch within 12 hours, larvae begin feeding on the surrounding materials in order to grow up, it passes through three stages, larva, the pupa or cocoon, at last the adult fly, these phases may take up to 10 days&nbsp;Larva live in water, wet or rotten plants, on the surface of animal tissues. They feed on plants or insects, when they are in water they feed through the breathing tube to get air.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>What flies eat?</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Flies prefer a large group of foods, such as fermented food, liquid materials, flies can be seen around human houses, one of the most important food flies feed on are:</p>\r\n\r\n<ul dir=\"ltr\">\r\n	<li style=\"text-align: justify;\">Liquids such as vinegar and apple juice</li>\r\n	<li style=\"text-align: justify;\">Fruits such as bananas, grapes, prunes, pineapples, tomatoes, pickles, potatoes and onions.</li>\r\n	<li style=\"text-align: justify;\">Sugary food, such as sweets.</li>\r\n</ul>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Damage caused by flies</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Flies transmit many dangerous diseases, malaria, yellow fever, anthrax, dysentery, typhoid fever, some types of flies destroy fruits and crops. Flies can damage food products and vegetables, also contaminate food, and cause serious diseases on digestive system, vomiting, diarrhea, intestinal catarrh, food poisoning by salmonella, e-coli, typhoid, cholera, dysentery and other viruses.</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Economic and financial losses by flies&#39; contamination, also their presence in any company or restaurant gives customers a bad reputation and they feel that place is unhealthy, some types of flies can sting people and cause Skin infections, allergies, swelling and itching.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Flies elimination</strong></h2>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\">Get rid of damaged food, organic materials, put them in garbage bags and close them well.</li>\r\n	<li style=\"text-align: justify;\">Clean the trash periodically, Keep windows and doors closed so that flies do not enter the home.</li>\r\n	<li style=\"text-align: justify;\">Develop&nbsp; fly-repellant systems to prevent flies from entering home.</li>\r\n	<li style=\"text-align: justify;\">Develop automatic doors that open and close when needed.</li>\r\n	<li style=\"text-align: justify;\">Put sticky traps or ultraviolet radiation trap around the house, and use&nbsp; resin strips in active places of flies, place ne strip of 10 inches per 1000 cubic feet, put strips at a 6 feet&nbsp; distance from the ground. This method is successful and works for up to 3 months.</li>\r\n	<li style=\"text-align: justify;\">Install outdoor light traps to avoid flies, place them 15 feet from doors and entrances and in dark areas far from the sun, put them 5 feet from food areas to reduce the risk of food contamination, replace the trap it in the spring, old lamps do not become attractive to flies, you also must clean them regularly</li>\r\n	<li style=\"text-align: justify;\">Jeddah pest control company experts are well-trained, and have excellent skills in pest control, they also have a high experience in pest treatment, so you have to call them if you have flies infestation in your home.</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>', 10, 1, 0, '2020-05-08 06:23:46', '2020-05-12 13:22:30', 'الذباب', 'Flies', 'حشرة الذباب : معلومات وحقائق وكيفية التخلص من الذباب', NULL, 'الذباب', 'Flies', 'Flies | Facts & Identification, Control & Prevention', 'Flies belong to the family Diptera, there are more than 110,000 species of flies, they cause damage to both plants and flowers, through feeding on them, flies also feed on beetle, larvae and other insects, scientists use flies in some genetic studies Domestic  flies are One of the most common types flies, that called Musca domestica, they live humans locations, it is one of the dangerous types that transmits disease to humans and pets, when we understand flies habits and their life cycle, this will help us to get rid of flies successfully.'),
(25, 'السوس', 'Weevils', 'uploads/pest_libraries/1588948470c0d111d54ff60b0df8713dcfd644f190image.png', 'يٌطلق على السوس الخنافس صغيرة لها الخرطوم الواضح، وهي تنمتي لعائلة  Curculionidae، ويدخل السوس المنازل من خلال الفتحات والشقوق في الأبواب والنوافذ والأساس، ولا تسبب الضرر للحيوانات الأليفة والبشر أو المنشآت لكنها قد تضر الأطعمة، ويمكن أن تدمر كميات ضخمة من المواد الغذائية والمحاصيل وهناك أكثر من 60000 نوع من السوس', 'السوس', '<p dir=\"rtl\" style=\"text-align:justify\">يٌطلق على السوس الخنافس صغيرة لها الخرطوم الواضح، وهي تنمتي لعائلة &nbsp;Curculionidae، ويدخل السوس المنازل من خلال الفتحات والشقوق في الأبواب والنوافذ والأساس، ولا تسبب الضرر للحيوانات الأليفة والبشر أو المنشآت لكنها قد تضر الأطعمة، ويمكن أن تدمر كميات ضخمة من المواد الغذائية والمحاصيل وهناك أكثر من 60000 نوع من السوس، والغالبية العظمى تصيب سيقان النباتات وتتغذى على الثمار من الداخل، وهي حشرة صغيرة جداً أقل من 6 مم لكنها تٌسبب تلف سريع للمحاصيل والأطعمة مثل القمح والحبوب، وقد لا يكتشف المزارعون الضرر في المراحل الأولى ولذلك تتمكن من الإضرار بكميات كبيرة، ومن خلال هذا المقال سوف نتعرف على شكل السوس ودورة حياته وكيف يضر المواد الغذائية وعلامات الإصابة بالسوس في المنزل ولذلك تابع القراءة معنا</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>شكل السوس</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">للسوس أكثر من حجم وشكل ولون وفقاً للنوع، وهي تشترك في أنها حشرة نحيفة بيضاوية الشكل، طولها حوالي3 مم وقد يصل في بعض الأنواع ل 10 مم، وعادة ما يكون لها لون أسود أو بني داكن، والعلامة المميزة للسوس هي شكل الرأس، الذي يمتد منه ما يشبه الخرطوم الذي يٌعرف بالخطم والذي يوجد الفم في نهايته، ويوجد منها العديد من الأنواع مثل سوس الأرز، البذور، الذرة، الفول، البازلاء، البذور،&nbsp;</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>دورة حياة السوس</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يعيش السوس ويتغذى داخل الطعام، وتقوم الأنثى بعمل حفرة في منتصف الحبة، لكي تضع البيض داخلها وتٌغلق الفتحة، وتترك البيضة وراءها، وعندما تفقس تتغذى اليرقة على داخل الحبة حتى تنمو بشكل كامل، وتقوم الأنثى بإطلاق الفيرومونات التي تجذب الذكور للتكاثر.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>كيف يتغذى السوس؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يتمتع السوس باأجزاء فم طويلة تٌسمى الخطم، وتقوم بغرسه في الحبوب والنباتات، وتحفرها لتتناولها من الداخل إلى الخارج، وتتسبب في موت النباتات، ويٌمكن العثور على سوس الأرز وسوس الحبوب وسوس القمح داخل المواد الغذائية مثل القمح والحبوب الأرز والمكرونة، ويٌصيب سوس الحبوب والأرز الحبوب الكاملة والأرز والمكسرات والفاصوليا والحبوب والبذور والذرة وغيرها من الأطعمة.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>علامات الإصابة بالسوس</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">من الصعب أن يلاحظ أصحاب المنزل الإصابة بالسوس في البداية، ولكن يمكن رؤيتها بالمصادفة في الأطعمة والحبوب المخزنة، وفي حالة الإصابات الشديدة يزحف المئات منها على الجدران والنوافذ خاصة في المطبخ.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>أضرار حشرة السوس</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يعيش السوس في الهواء الطلق بالحدائق والحقول، ويمكن أن تتسبب في قتل نباتات حديقتك،&nbsp; ولذلك تعتبر من الحشرات الخطيرة كما أنها تلوث الطعام المٌصاب بالبراز والجلود التي تنشق عنها، وتتسبب في بعض الإضرار إذا تغذى البشر على الطعام الملوث، وتجعل الطعام غير صالح للأكل، وهي لا تقرص البشر ولا تتلف الخشب داخل وحينما يهاجم السوس المحاصيل أو مصانع الأغذية بأعداد كبيرة، تدمر المواد الغذائية بشدة، وحينما يدرك الناس وجود السوس يصعب التخلص منه السوس في المخازن أو المصانع أو المنازل، ومن أكثر الأنواع خطورة سوس اللوبيا Callosobruchus maculatus وسوس الحبوبSitophilus granarius وسوس الأرز Sitophilus oryzae</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>كيفية التخلص من السوس</strong></h2>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align: justify;\">البحث عن الأطعمة والمواد الغذائية التي يوجد بها السوس والتخلص منها.</li>\r\n	<li style=\"text-align: justify;\">الفحص الدقيق قبل شراء الحبوب والأرز والمكرونة وغيرها من العناصر الغذائية قبل الشراء لمنع إمكانية حدوث إصابة جديدة.</li>\r\n	<li style=\"text-align: justify;\">تجنب شراء المنتجات الغذائية التي يوجد بها ثقوب أو علامات تلف.</li>\r\n	<li style=\"text-align: justify;\">تخلص من السوس الذي تراه بالمكنسة الكهربائية سواء في الجدران والأثاث، وتخلص من كيس التنظيف وقم بتفريغه حتى لا تدخل السوسة للمنزل مرة أخرى.</li>\r\n	<li style=\"text-align: justify;\">قم بفحص المنزل من الخارج، وقم بسد أي فتحات مٌحتمّلة قد يستخدمها السوس لدخول منزلك سواء على الأبواب، وقم بإصلاح الشاشات التالفة.</li>\r\n	<li style=\"text-align: justify;\">باهظة الثمن للشركات في صناعة المواد الغذائية لأنه في كثير من الأحيان يجب التخلص من شحنة الطعام بالكامل ومخازن الحبوب.</li>\r\n	<li style=\"text-align: justify;\">أفضل طريقة للتخلص من السوس هو التأكد من عدم دخولهم لمكان الطعام، وذلك من خلال التخزين الجيد للمواد الغذائية والفحص الدقيق لها بشكل مستمر، حفظ الطعام في المناطق الباردة، تأكد من فحص الطعام قبل تخزينه.</li>\r\n	<li style=\"text-align: justify;\">لا يتمكن السوس من العيش في المناطق الباردة،&nbsp; ولذلك فإن خفض الحرارة في مناطق حفظ الطعام يوقف نشاط السوس، كما أن التأكد من تنظيف مناطق التخزين يضمن التخلص من السوس أول بأول.&nbsp;</li>\r\n	<li style=\"text-align: justify;\">قم بتخزين المواد الغذائية في منزلك بكميات صغيرة مثل الدقيق والحبوب.</li>\r\n	<li style=\"text-align: justify;\">التعاقد مع شركة مكافحة الحشرات بجدة للمساعدة في التخلص من الإصابة بالسوس ومنعه من دخول منزلك مرة أخرى.&nbsp;&nbsp;</li>\r\n</ul>', '<p dir=\"ltr\" style=\"text-align:justify\">Weevils are called small beetles with a curving snout, and they belong to a family Curculionidae, they enter homes from cracks and holes in doors, windows, and foundations, they are not known for harming pets or humans, they cause serious problems and damage to foods, they can destroy crops and plants There are more than 60,000 species of weevils, they infect the stems of plants and feed on them from the inner side, they are very small insects, less than 6 mm, they may cause rapid damage to crops and foods such as: wheat and grains, people may not notice crop damage at early stages, so they can damage large amounts of crops.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Weevils identification</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Weevils have several sizes, shapes, and color according to each species, they have oval-shaped body, length is about 3 mm, it may reach to 10 mm. they have&nbsp; black or dark brown colors, weevils have a characteristic appearance of head, it is extended and have a snout&nbsp; at the end of the mouth, there are many species of it such as rice weevils, seeds, corn, beans.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>weevil life cycle</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">weevils live and feeds inside food, female makes inside food, it put eggs inside it and close the hole, when eggs hatch, the larva feeds on the food and grow up, female releases pheromones that attract males for reproduction.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>How weevils eat?</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">weevil has a long mouth portions called snout, it put it inside grains and plants, then eat inner part of the seeds, so it kills plants, Whole, rice, nuts, beans, grains, seeds, corn and other foods.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Signs of weevil infestation</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">People don&rsquo;t notice weevils at early stages, they can be seen in stored food materials, in the case of severe injuries people find them crawl on walls and windows, especially in the kitchen area.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>weevil damage&nbsp;</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">weevil lives in the open area, such as gardens and fields, it kills plants in your garden. Therefore, it is considered a dangerous insect, it pollutes food by feces and the outer weevil skin, if humans feed on the contaminated food, it may cause diseases to them, it does not sting people or wood inside home, weevil attacks crops and food in large numbers, and then destroy them, when people discover weevils, it becomes difficult to get rid of them.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>How to get rid of weevils</strong></h2>\r\n\r\n<ul dir=\"ltr\">\r\n	<li style=\"text-align: justify;\">Get rid of food&nbsp; where weevils present.</li>\r\n	<li style=\"text-align: justify;\">accurate Examination before buying cereals, rice, pasta and other food to prevent new infection, also void buying food products with signs of infestation.</li>\r\n	<li style=\"text-align: justify;\">Get rid of weevils you see with the vacuum cleaner, walls, furniture, don&rsquo;t forget to get rid of cleaning bag and empty.</li>\r\n	<li style=\"text-align: justify;\">Examine the house, seal all possible openings that weevils may enter your house from, and repair damaged screens.</li>\r\n	<li style=\"text-align: justify;\">make sure that weevils do not enter food places, through good storage of food and constant examination of it&nbsp; food places, keep food in cold areas, be sure to check the food before storing it.</li>\r\n	<li style=\"text-align: justify;\">weevils are not able to live in cold areas, so reduce rooms temperature especially in food areas, making sure to clean the storage areas ensures that weevil gets rid of it first, Store food in your home in small quantities, such as flour and cereals.</li>\r\n	<li style=\"text-align: justify;\">call a pest control company in Jeddah to help get rid of weevils and prevent them from entering your home.</li>\r\n</ul>', 13, 1, 0, '2020-05-08 06:34:30', '2020-05-12 13:23:37', 'السوس', 'Weevils', 'حشرة السوس : معلومات وحقائق وكيفية التخلص من السوس', NULL, 'السوس', 'Weevils', 'Weevils | Facts & Identification, Control & Prevention', 'Weevils are called small beetles with a curving snout, and they belong to a family Curculionidae, they enter homes from cracks and holes in doors, windows, and foundations, they are not known for harming pets or humans, they cause serious problems and damage to foods, they can destroy crops and plants There are more than 60,000 species of weevils, they infect the stems of plants and feed on them from the inner side, they are very small insects, less than 6 mm, they may cause rapid damage to crops and foods such as: wheat and grains, people may not notice crop damage at early stages, so they can damage large amounts of crops.'),
(26, 'العناكب', 'Spiders', 'uploads/pest_libraries/1588952095c7146b9d401db88b2cb6705b8696ae00image.png', 'تنتمي العناكب إلى فئة ال Arachnida وهناك أكثر من 40000 نوع مختلف من العناكب وجميع العناكب حشرات سامة، وهي تملك ثمانية أرجل ومنطقة صدر وبطن وعضد ولها أنياب طويلة وغدد حريرة لإنتاج الحرير ولها 8 عيون وتتنفس الهواء من خلال الجانب السفلي من جسمها وتٌعد العناكب الكبيرة الأكثر خطورة بينها فهي تقوم بصيد الحشرات ولا تبني شباك، وتعيش في الهواء الطلق أو داخل المنزل حول الأضواء وفي المداخل وهي تنشط بالليل', 'Spiders', '<p dir=\"rtl\">تنتمي العناكب إلى فئة ال Arachnida وهناك أكثر من 40000 نوع مختلف من العناكب وجميع العناكب حشرات سامة، وهي تملك ثمانية أرجل ومنطقة صدر وبطن وعضد ولها أنياب طويلة وغدد حريرة لإنتاج الحرير ولها 8 عيون وتتنفس الهواء من خلال الجانب السفلي من جسمها وتٌعد العناكب الكبيرة الأكثر خطورة بينها فهي تقوم بصيد الحشرات ولا تبني شباك، وتعيش في الهواء الطلق أو داخل المنزل حول الأضواء وفي المداخل وهي تنشط بالليل، ومن المعروف عن عدد قليل جدا من العناكب لدغ البشر، وتعد جميع أنواع العناكب سامة، لكن القليل منها خطير، وفي الأغلب حينما تشعر العناكب بالتهديد تقوم بالهرب ونادراً ما تسبب اللدغات، وإذا تعرضت للقرص قم بطلب المساعدة من طبيب متخصص، ومن خلال هذا المقال سوف نتعرف على المظهر الخارجي للعناكب وعادات التغذية والتكاثر، وكيف تتمكن من دخول المنزل وكيفية التعامل معها بشكل ناجح.</p>\r\n\r\n<h2 dir=\"rtl\"><strong>المظهر الخارجي للعناكب</strong></h2>\r\n\r\n<p dir=\"rtl\">يكون جسم العناكب مقسم إلى منطقتين، وتملك الحشرة ثمانية أرجل ولا تملك أي أجنحة أو قرون استشعار ولها ثلاثة أو أربعة زوج من العيون، وتقوم العناكب بإنتاج الحرير المرن واللاصق، وتستخدمه في بناء الشبكات وأكياس البيض والمساكن وللشبكات أشكال وأحجام مختلفة العنكبوت حسب النوع فبعضها يكون شبكة منتظمة وبعضها يكون عل شكل قمع والبعض الآخر لا يوجد له شكل محدد،</p>\r\n\r\n<h2 dir=\"rtl\"><strong>دورة حياة العناكب</strong></h2>\r\n\r\n<p dir=\"rtl\">تنتج الإناث البيض الذي تضعه في كيس داخلها، وتخضع العناكب لعدة تطورات قبل البلوغ، ويقوم الذكر بجذب الأنثى له للتكاثر، حيث تؤدي الذكور من العناكب قفزات راقصة للفت نظر الأنثى، ويشكل التزاوج خطر على الذكور لأن الإناث تتناولهم في النهاية، وتصنع الأنثى حويصلات للبيض مستديرة وناعمة وتحتوي على ما بين 200 و 900 بيضة، ويفقس البيض بعد حوالي 30 يومًا، ويمكن أن تعيش الأنثى حتى ثلاث سنوات ولكن يعيش الذكور لمدة شهر أو شهرين، لأن الأنثى تقتله بعد عملية التزاوج، وتعيش بعض الأنواع في الجحور ولا تبني شبكات، وتوجد العناكب خارج المنازل بأعداد كبيرة وهي تنجذب للضوء، وتٌعد العناكب الذئبية الأكثر انتشارا.</p>\r\n\r\n<h2 dir=\"rtl\"><strong>كيف تدخل العناكب المنزل؟</strong></h2>\r\n\r\n<p dir=\"rtl\">تدخل العناكب المنزل من خلال الفتحات الموجودة تحت الأبواب والنوافذ والمداخل الأخرى للمنزل، وهي تدخل المنزل للبحث عن الطعام والرطوبة والدفء، كما أنها تدخل المنزل في حالة تواجد حشرات أخرى للتغذى عليها، وقد تدخل المنزل بالخطأ من خلال النباتات المزروعة أو العناصر المخزنة والملابس المستعملة والحطام الموجود في البدروم أو السندرة أو الطوابق السفلى.</p>\r\n\r\n<h2 dir=\"rtl\"><strong>ماذا تأكل العناكب؟</strong></h2>\r\n\r\n<p dir=\"rtl\">تتغذى العناكب على الحشرات والعناكب الأصغر منها والحشرات التي تعثر عليها وتتمكن من صيدها، ولا تملك العناكب فم ماضغ لكنها تستخدم إنزيمات خاصة للهضم يفرزها اللعاب لإخضاع الحشرة التي تتناولها، وتكون أمعاء العنكبوت ضيقة ولا تسمح لها بتناول الطعام كبير الحجم، وتكون أنواع قليلة منها التي تتناول النباتات.</p>\r\n\r\n<h2 dir=\"rtl\"><strong>هل تمثل العناكب خطورة على البشر؟</strong></h2>\r\n\r\n<p dir=\"rtl\">معظم العناكب تشكل خطرًا طفيفاً على البشر, وفي بعض الحالات تقوم بلدغهم وحالات قليلة هي التي تستدعي الرعاية الطبية، ويعد الناسك البٌني هو أكثر العناكب السامة شيوعًا ولها شكل جسم مٌميز يٌشبه الكمان في الجزء العلوي من الجسم، وتتصل منطقة الرأس مع الصدر في جسم هذا النوع، أما الأرملة السوداء فهي نوع خطير آخر ويشبه شكلها الساعة الرملية، وتتمتع بلون أحمر على الجانب السفلي من بطنها الذي يكون له لون أسود.</p>\r\n\r\n<h2 dir=\"rtl\"><strong>كيف يمكن التخلص من العناكب؟</strong></h2>\r\n\r\n<ul dir=\"rtl\">\r\n	<li>تهوية المنزل بشكل جيد خاصة الطوابق السفلية والغرف التي تحتوي على سندرات لأن العناكب تفضل المناطق المظلمة والرطبة.</li>\r\n	<li>رش المبيدات الكيميائية الخاصة بالعناكب في الزوايا والأركان صعبة الوصول وفي الحديقة للتخلص من الأعداد الكبيرة.</li>\r\n	<li>استخدام المكنسة الكهربائية للتخلص من شبكات العنكبوت وأكياس البيض والعناكب من منزلك.</li>\r\n	<li>قطع موارد الطعام عن العناكب وقطع مصادر الدخول من خلال سد النوافذ وفتحات الأبواب.</li>\r\n	<li>استخدام المصائد اللاصقة اللزجة للسيطرة على العناكب كما أنها تصلح في حالة القوارض والصراصير ويمكن وضعها في الخزائن والطوابق السفلية والسندرات والألواح الأساسية والزوايا ومناطق نشاط العنكبوت.</li>\r\n	<li>إزالة الفوضى بالخارج مثل الخشب والصخور والأكوام و سد جميع الشقوق في أساس منزلك وإزالة نقاط الدخول وإغلاق النوافذ بشاشات مجهزة والأبواب .</li>\r\n	<li>تزويد الأبواب بكنسات وشرائط وتنظيفها بانتظام وغسل أي خيوط من منزلك، والتخلص من الصناديق والفضلات من الورق المقوى في مناطق التخزين.</li>\r\n	<li>التخفيف من الإضاءة لأن العناكب تنجذب للإضاءة ولذلك يجب تقليل كمية الإضاءة الخارجية التي تستخدمها واستبدال الإضاءة بأضواء الصوديوم أو الأضواء الصفراء لأنها أقل جاذبية للحشرات.</li>\r\n	<li>التواصل مع متخصصي إدارة الحشرات بجدة بالتعامل مع العناكب والتعرف&nbsp; عليها وتحديدها بشكل ناجح.</li>\r\n</ul>', '<p dir=\"ltr\">Spiders belongs to class Arachnida, there are about 40,000 species of spiders over the world, spiders are poisonous insects, they have eight legs and have two body parts consists of abdomen and chest, they have long fangs, silk glands to produce silk, they have 8 eyes and breathe from body sides&nbsp;Large spiders are the most dangerous species, they hunt other insects, do not build nets, live in the open area, they are attracted to lights in the entrances, they are active at night, spiders are known for stinging human, all types of spiders are poisonous, but a few of them are dangerous, when spiders threatened, they escape and rarely cause stings, through this article, we will learn more about spider identification, feeding habits and reproduction,</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Spiders external appearance&nbsp;</strong></h2>\r\n\r\n<p dir=\"ltr\">Spider body is divided into two parts, it has eight legs, does not have wings, three or four pairs of eyes, they produce adhesive silk, building networks, egg sacs and nets, nets has different shapes and sizes, some nets look like mesh, some of them have no specific form,</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Spiders life cycle</strong></h2>\r\n\r\n<p dir=\"ltr\">Females lay eggs, and put them in a sac, spiders have may stages before reaching puberty, male attracts females for matting, females lay between 200 and 900 eggs, eggs hatch after about 30 days, female can live up to three years but male lives for a month or two, because the female kills it after mating, some species live in holes, and do not build nets, some spiders found Outdoors in large numbers and they attracted to light.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>How spiders enter the house?</strong></h2>\r\n\r\n<p dir=\"ltr\">Spiders enter the house through the openings under the doors, windows and other entrances, they search for food, moisture and warm places, they enter the house to eat insects or by mistake through plant cover, and they live in kitchen or basement.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>Spiders feeding habits</strong></h2>\r\n\r\n<p dir=\"ltr\">Spiders feed on insects, smaller spiders and insects they catch, spiders do not have a mouth part, they enzymes produced by saliva to digest food they eat, spider guts is narrow and does not allow them to eat large food.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>How serious are spiders?</strong></h2>\r\n\r\n<p dir=\"ltr\">Spiders don&rsquo;t cause a massive danger to humans, in some cases they bite them and they may seek medical care, The brown recluse or violin spider is the most poisonous type, its body shape resemble a violin in the upper part, head is connected to the chest, the black widow spider it is another dangerous type and looks like an hourglass, it has a red color and black abdomen.</p>\r\n\r\n<h2 dir=\"ltr\"><strong>How do you get rid of spiders?</strong></h2>\r\n\r\n<ul dir=\"ltr\">\r\n	<li>you have to ventilate your house especially basement and sandals, because spiders prefer dark and humid places.</li>\r\n	<li>Use chemical pesticides in the corners and hard reach places to get rid of large numbers in gardens.</li>\r\n	<li>Use a vacuum to get rid of spider webs, egg, and spiders from your home.</li>\r\n	<li>Cut off food resources and seal entry points by blocking windows and door openings.</li>\r\n	<li>Using adhesive traps to control spiders, put them in cupboards, basements, attics, baseboards, corners, and spider activity areas.</li>\r\n	<li>Eliminate clutter outside, wood, rocks and cracks, close windows with equipped screens and doors.</li>\r\n	<li>close doors openings, dispose boxes and cardboard waste in storage areas.</li>\r\n	<li>Reduce lighting because spiders are attracted to lights, so you should reduce the amount of external lighting, replace them with&nbsp;</li>\r\n	<li>sodium lights or yellow lights because they are less attractive to insects.</li>\r\n	<li>Contract pest control company in Jeddah to get rid of spiders.</li>\r\n</ul>', 24, 1, 0, '2020-05-08 07:34:55', '2020-05-12 13:23:54', 'العناكب', 'Spiders', 'حشرة العنكبوت : معلومات وحقائق وكيفية التخلص من العناكب', NULL, 'العناكب', 'Spiders', 'Spiders | Facts & Identification, Control & Prevention', 'Spiders belongs to class Arachnida, there are about 40,000 species of spiders over the world, spiders are poisonous insects, they have eight legs and have two body parts consists of abdomen and chest, they have long fangs, silk glands to produce silk, they have 8 eyes and breathe from body sides.'),
(27, 'أبو مقص', 'Earwig', 'uploads/pest_libraries/15889527705edc65ddb594ce49b662517c8df6dd2bimage.png', 'أبو مقص أو ال Dermaptera يضم حوالي 1800 نوعًا من الحشرات، ويُقسم الأسم العلمي الخاص بالحشرة لجزئين الأول بمعنى الجلد والثاني بمعنى الجناح، وهو من الحشرات الليلية التي تنشط في الليل، أما خلال النهار يختبئ في الشقوق وهو يعيش تحت الصخور وجذوع الأشجار وأحواض الزهور ويأكل النباتات والحشرات،كما أنه ينجذب إلى الأضواء ويصبح مصدر إزعاج للبشر خاصة مساء فصل الصيف.', 'Earwig', '<p dir=\"rtl\" style=\"text-align:justify\">أبو مقص أو ال Dermaptera يضم حوالي 1800 نوعًا من الحشرات، ويُقسم الأسم العلمي الخاص بالحشرة لجزئين الأول بمعنى الجلد والثاني بمعنى الجناح، وهو من الحشرات الليلية التي تنشط في الليل، أما خلال النهار يختبئ في الشقوق وهو يعيش تحت الصخور وجذوع الأشجار وأحواض الزهور ويأكل النباتات والحشرات،كما أنه ينجذب إلى الأضواء ويصبح مصدر إزعاج للبشر خاصة مساء فصل الصيف&nbsp;وهناك خٌرافة شهيرة عن الحشرة وهي أنها تدخل داخل طبلة أذن البشر لوضع البيض والتخفي والبحث عن الدفء أثناء نومهم، ولا يوجد دليل علمي على أن أبو مقص يفعل ذلك، لكن البشر يخشون من الحشرة بسبب الكماشة التي توجد في منطقة البطن وهي تستخدمها للدفاع عن نفسها وقت الخطر، ومن خلال هذا المقال سوف نتعرف على شكل أبو مقص، دورة حياته، عادات التغذية وأماكن الاختباء وكيفية التخلص منها ولذلك تابع القراءة معنا.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>شكل أبو مقص</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">أبو مقص حشرة مستطيلة لها جسم نحيل، وتحمل منطقة الصدر زوجين من الأجنحة، الأول صغير ومكسو بالجلد، والثاني كبير وله شكل غشائي، ويقوم أبو مقص بطي جناحه وقت الراحة إلى أسفل، ولبطن الحشرة شكل يشبه الملقاط أو الكماشة، ويتراوح طول الجسم من 4 إلى 80 مم، وللحشرة لون بني موحد أو أسود أو بني فاتح أو أصفر أو معدني أخضر.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>دورة حياة أبو مقص</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تضع الأنثى من 30 إلى 50 بيضة، وتخرج الحوريات من البيضة وتمر بأربع أو خمس مراحل حتى تصل لمرحلة البلوغ، وأهم مراحل أبو مقص البيض، الحوريات، الحشرة البالغة.</p>\r\n\r\n<ul>\r\n	<li dir=\"rtl\">\r\n	<h3 dir=\"rtl\" style=\"text-align:justify\"><strong>مرحلة البيض</strong></h3>\r\n	</li>\r\n</ul>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يحدث موسم التزاوج خلال فصل الخريف وأوائل الشتاء، حيث تحمل الأنثى بيضها حتى تجد مكانًا مناسبًا لوضعها في ما يٌسمى بالخلية، وتقوم بحفر الخلية في مكان مناسب سواء تحت الأوراق أو في التربة أو في الفجوات والشقوق، وتقوم الأنثى بوضع من 20-80 بيضة ويمكنها وضع البيض حتى مرتين في الموسم الواحد، وتقوم الأنثى بالعناية بالبيض حتى يحين موعد الفقس وعندما تضع الأنثى بيضها داخل الخلية تقوم الأم برعاية البيض وتنظيفه من الفطريات والطفيليات، وتقوم بوضعهم مرة أخرى في الخلية، وبمجرد فقس البيض، تقوم الأنثى بتوفير الطعام لصغارها حتى يكونوا مستعدين للاعتماد على أنفسهم، وتقوم الأنثى بحمل البيض داخلها لفترة طويلة من الزمن قد تصل لبضعة أشهر وبمجرد وضعه يفقس البيض خلال سبعة أيام.</p>\r\n\r\n<ul>\r\n	<li dir=\"rtl\">\r\n	<h3 dir=\"rtl\" style=\"text-align:justify\"><strong>مرحلة الحورية</strong></h3>\r\n	</li>\r\n</ul>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تعد مرحلة الحوريات هي مرحلة الطفولة بالنسبة لأبو مقص الذي يمر ب 4-6 مراحل قبل سن البلوغ، وفي كل مرة يقوم بالتخلص من الجلد القديم والقشرة الخارجية حتى تنمو طبقة جديدة من الجلد وهكذا، وخلال عملية الإنسلاخ تنمو الحورية بشكل مستمر وتنمو الشعيرات حول جسمها، ولا تبدو حورية أبو مقص شبيهة بشكل كبيرة بالحشرة البالغة إلا أنها تحمل بعض صفاتها، وتكون الحوريات أفتح لونًا، وتتحول إلى لون أكثر قتامة وأكثر صلابة خلال 40-60 يومًا.</p>\r\n\r\n<ul>\r\n	<li dir=\"rtl\">\r\n	<h3 dir=\"rtl\" style=\"text-align:justify\"><strong>مرحلة الحشرة البالغة</strong></h3>\r\n	</li>\r\n</ul>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">بمجرد أن تمر الحورية بآخر عملية انسلاخ الجلد، تتحول إلى الشكل البالغ للحشرة والذي يتكون من أجنحة وقرون استشعار وكماشة في منطقة البطن، وتبدأ في ممارسة الأنشطة المتعارف عليها للحشرة من الصيد والطيران والتزاوج وتكرر دورة الحياة مرة أخرى.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>طعام أبو مقص</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تتغذى حشرة أبو مقص على لحوم الحشرات الأخرى وهي من الحشرات المفترسة وهناك بعض الأنواع النباتية التي تتغذى على الأوراق والنباتات المتحللة والفطريات،أو تتغذى على بعض إفرازات الغدد الجلدية للخفافيش أو الحشرات الميتة.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>أين يعيش أبو مقص؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يعيش أبو مقص في الفجوات والشقوق الرطبة، ويمكن أن تجده بين الأوراق وتحت الحجارة واللحاء، والبعض منها قد يدخل طبلة الأذن للبشر للاختباء، ويغزو أبو مقص إلى المنزل للعثور على الملجأ أو الطعام أو بسبب تغيرات المناخ، ويدخل المنزل من خلال الأبواب المفتوحة ويفضل المناطق الباردة والرطبة وقد يدخل المنزل خلال فترات الجفاف ويتم العثور على أبو مقص في المناطق التي توجد بها المياه، مثل المطابخ والحمامات والمغاسل، وغرف النوم وحجرة المعيشة.&nbsp;</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>كيف يمكنني التخلص من أبو مقص</strong></h2>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align: justify;\">القضاء على أماكن اختباء أبو مقص.</li>\r\n	<li style=\"text-align: justify;\">&nbsp;استخدام المبيدات الحشرية للقضاء على أبو مقص.</li>\r\n	<li style=\"text-align: justify;\">تنظيف المنزل ونقل الخشب والأحجار وأكوام الحطب بعيدًا عن المنزل.</li>\r\n	<li style=\"text-align: justify;\">القضاء على مشكلة الرطوبة في المنزل.</li>\r\n	<li style=\"text-align: justify;\">&nbsp;إنشاء منطقة بجوار المنزل خالية من الأوراق النباتية المتساقطة والمواد العضوية بمقدار من 6 إلى 12 بوصة.</li>\r\n	<li style=\"text-align: justify;\">تقليم الأشجار والشجيرات والأفرع حول المنزل.</li>\r\n	<li style=\"text-align: justify;\">المحافظة على نظام صرف صحي سليم من خلال فحص أنابيب المياه والقنوات والتأكد من سلامتها.&nbsp;</li>\r\n	<li style=\"text-align: justify;\">عمل نظام ري يسقي النباتات في الصباح وتجف خلال النهار.</li>\r\n	<li style=\"text-align: justify;\">تخفيف الأضواء الخارجية للمنزل حتى تنجذب الحشرات بعيدًا، كما يمكن وضع المصابيح الصفراء لأن الأضواء البيضاء أكثر جاذبية للحشرات.</li>\r\n	<li style=\"text-align: justify;\">تأمين نقاط الدخول الممكنة من خلال إصلاح الشاشات والفتحات.</li>\r\n	<li style=\"text-align: justify;\">فحص الجزء الخارجي من المنزل وإزالة أكبر عدد ممكن من أماكن إختباء أبو مقص.</li>\r\n	<li style=\"text-align: justify;\">تهوية المنزل بشكل جيد والتأكد من تعرضه للشمس.</li>\r\n	<li style=\"text-align: justify;\">التعاقد مع شركة مكافحة الحشرات بجدة لفحص المنزل فحص شامل ووضع خطة علاجية مٌخصصة لمنزلك.</li>\r\n</ul>', '<p dir=\"ltr\" style=\"text-align:justify\">Earwigs (the scientific name Dermaptera) have about 1,800 species, the scientific name is divided into two parts, the first one means the skin and the second one means the wing, they are nocturnal insects that are active at night, during the day they hide in the holes, cracks, under the rocks, trunks, flowers, they eat plants and insects, they are attracted to the lights, cause a nuisance to humans especially in the summer evening.</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">There is a famous myth about earwigs, which is that they enter the human ear drum to lay eggs inside and hide seeking for warmth, but there is no scientific evidence that earwigs do so, people are afraid of earwigs, because they have tweezers in the abdominal region, they use it to defend themselves at Danger.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Earwigs identification</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Earwig is a rectangular in shape and has a slender body, there are a pair of wings on chest area, the first one is small, covered with skin, the second is large and has a membranous shape, earwigs folds its wing at rest, the abdomen resembles tweezers, body length ranges from 4 to 80 mm, it has a uniform color of brown, black, light brown, yellow, or green metallic.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Earwigs life cycle</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">female lays 30 to 50 eggs, nymphs leave the egg and go through four or five stages, the important stages are eggs, nymphs, the adult earwigs,</p>\r\n\r\n<ul dir=\"ltr\">\r\n	<li style=\"text-align: justify;\">\r\n	<h3><strong>Egg&nbsp;</strong></h3>\r\n	</li>\r\n</ul>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Fall and winter are mating season to earwigs, female holds eggs until it finds a suitable place to lay eggs, it digs the cell in a suitable place such as leaves, soil, gaps and cracks, female lays from 20-80 eggs, it can lay Eggs twice per season, female takes care of the eggs until the date of hatching&nbsp;When the female lays eggs inside the cell, it takes care of the eggs and cleans them from fungi and parasites, it returns them back in the cell, once the eggs hatch, the female provides food for nymphs until they are ready to depend on themselves, the female carries eggs inside for few Months, eggs will hatch within seven days.</p>\r\n\r\n<ul dir=\"ltr\">\r\n	<li>\r\n	<h3 dir=\"ltr\" style=\"text-align:justify\"><strong>nymphs stage</strong></h3>\r\n	</li>\r\n</ul>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">The nymph stage passes 4-6 stages before reach mature stage, every time itgets rid of the old skin until a new layer of skin grows, the nymph grows and it does not look very similar to the adult earwigs, as nymphs are lighter in color, turn into a darker and more solid color within 40-60 days.</p>\r\n\r\n<ul dir=\"ltr\">\r\n	<li>\r\n	<h3 dir=\"ltr\" style=\"text-align:justify\"><strong>Adult stage</strong></h3>\r\n	</li>\r\n</ul>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">when the nymph completes the last transformation, it turns into the adult earwigs, which consists of wings and antennae and forceps in the abdominal area, the earwigs begins hunting, flying, mating and repeating the life cycle.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Earwigs Food</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Earwigs is a predatory insect, they eat other insects, some eat plants, leaves, organic materials, fungi, skin glands secretions of bats or dead insects.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Where earwigs live?</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">earwigs live in humid cracks, you can find it under leaves, stones they enter home to search for a shelter and warmth&nbsp;Earwigs invade the house to find shelter, food, or because of climate change, they enter the house from open doors, they also prefer humid areas&nbsp;earwigs are found in areas where there is water, such as kitchens, bathrooms, washbasins, bedrooms and living room.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>How can I get rid of earwigs?</strong></h2>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\">get rid of earwigs hiding places.</li>\r\n	<li style=\"text-align: justify;\">use of pesticides to get rid of earwigs, clean the house and remove food, wood, stones away from home, and get rid of humidity problems.</li>\r\n	<li style=\"text-align: justify;\">Create a free area next to the house where there is no fallen leaves or organic materials from 6 to 12 inches from the house.</li>\r\n	<li style=\"text-align: justify;\">Maintaining a proper sanitation system by examining the water pipes and ensure safety.</li>\r\n	<li style=\"text-align: justify;\">Reduce the outer lights to get rid of earwigs ,you may use Yellow lights and place white lights which are more attractive to earwigs, also seal any entry points and repair openings.</li>\r\n	<li style=\"text-align: justify;\">Check the house and remove hiding places of earwigs.</li>\r\n	<li style=\"text-align: justify;\">Ventilate the house well to ensure sun exposure.</li>\r\n	<li style=\"text-align: justify;\">Contact a pest control company in Jeddah to examine the home and develop a treatment plan.</li>\r\n	<li style=\"text-align: justify;\">&nbsp;</li>\r\n</ul>', 16, 1, 0, '2020-05-08 07:46:10', '2020-05-12 13:23:54', 'أبو-مقص', 'Earwig', 'حشرة أبو مقص : معلومات وحقائق وكيفية التخلص من ابو مقص', NULL, 'أبو مقص', 'Earwig', 'Earwig | Facts & Identification, Control & Prevention', 'Earwigs (the scientific name Dermaptera) have about 1,800 species, the scientific name is divided into two parts, the first one means the skin and the second one means the wing, they are nocturnal insects that are active at night, during the day they hide in the holes, cracks, under the rocks, trunks, flowers, they eat plants and insects, they are attracted to the lights, cause a nuisance to humans especially in the summer evening.');
INSERT INTO `pest_libraries` (`id`, `name_ar`, `name_en`, `image`, `meta_description_ar`, `image_alt`, `description_ar`, `description_en`, `sort`, `is_active`, `home_order`, `created_at`, `updated_at`, `slug_ar`, `slug_en`, `meta_title_ar`, `sub_pest`, `keywords_ar`, `keywords_en`, `meta_title_en`, `meta_description_en`) VALUES
(28, 'البق النتن', 'Stink bugs', 'uploads/pest_libraries/1588953579bef6fbd84c471f8e869e55e2ff03d61aimage.png', 'يٌسمى البق النتن أو ال Pentatomidae بهذا الاسم بسبب الرائحة الكريهة التي تنبعث منه، حيث تقوم بعض الحشرات بفرز رائحة لطرد الأعداء والحيوانات المفترسة عند الشعور بالخطر، كما أنه يٌعد من الآفات الزراعية، ويغزو المنازل والمباني خلال فصل الشتاء للحصول على مكان آمن ودافئ والمعروف عن البق النتن أنه يقوم بحراسة بيضه الذي يوجد في مجموعات، وفي كثير من الأحيان لا يمكن رؤية البق النتن لأن يعيش في أماكن مظلمة ودافئة،', 'Stink bugs', '<p dir=\"rtl\" style=\"text-align:justify\">يٌسمى البق النتن أو ال Pentatomidae بهذا الاسم بسبب الرائحة الكريهة التي تنبعث منه، حيث تقوم بعض الحشرات بفرز رائحة لطرد الأعداء والحيوانات المفترسة عند الشعور بالخطر، كما أنه يٌعد من الآفات الزراعية، ويغزو المنازل والمباني خلال فصل الشتاء للحصول على مكان آمن ودافئ والمعروف عن البق النتن أنه يقوم بحراسة بيضه الذي يوجد في مجموعات، وفي كثير من الأحيان لا يمكن رؤية البق النتن لأن يعيش في أماكن مظلمة ودافئة، كما أنه هذه الحشرة تتميز بلون بني وأخضر يمكنها من التخفي داخل فضلات الأوراق أو التربة بحيث لا يمكن ملاحظتها بسهولة وينشط البق النتن من شهر مايو إلى أكتوبر، ويكون بعض&nbsp; أنواع الحشرة مفترس، والآخر يأكل النباتات، ولذلك يفضل المناطق الزراعية والحدائق والمتنزهات، ويٌعتبر بعض الأنواع من البق النتن مفيدة والآخر مصدر إزعاج لأصحاب المنازل، ومن خلال هذا المقال سنتعرف على المزيد عن حشرة البق النتن ولذلك تابع القراءة معنا.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>شكل البق النتن</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يتراوح طول البق النتن من 12 إلى 17 مم، ويتراوح لونها بين الأخضر إلى البني إلى الأسود، وتملك الحشرة فم ماص وقرون استشعار مزودة بشرائط بنية، ويكون للحشرة هيكل خارجي مميز يشبه الدرع، وللحشرة أجنحة مٌظللة بمجموعة من العٌصابات، وتتميز الحوريات بلون أصفر أو أحمر وعيون حمراء، وخلال النمو تتحول إلى اللون الأصفر ثم الأبيض الفاتح، وينبعث من البق النتنة رائحة نفاذة تشبه رائحة الكزبرة، وتفرز الحشرة هذه الرائحة من ثقوب في بطنها لتقوم بالدفاع عن نفسها ومنع الطيور والسحالي من الاقتراب منها.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>دورة حياة البق النتن</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تبحث الأنثى عن مضيف مناسب، ثم تقوم بوضع البيض على الجانب السفلي من أوراق النباتات على هيئة صفوف، في أوائل الربيع ، ويعثر البشر على بيض البق النتن على طول الأشجار والنباتات المزروعة بالقرب من مواقع تجمعهم، وتضع الإناث عادة من 20 إلى 30 بيضة على الجانب السفلي من النبات المضيف في الصيف، ويفقس البيض بعد 4-5 أيام وتبدأ الحوريات بالرضاعة، حتى تصل لمرحلة البلوغ بحلول الخريف، وتمر الحوريات بعدة مراحل لحين الوصول للبلوغ.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>طعام البق النتن</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يتغذى البق النتن البالغ والحوريات على النباتات والمحاصيل الزراعية من أوراق النباتات وهو يقوم بإتلافها من خلال اختراق أنسجة النبات، مما يسمح لمسببات المرض بالدخول للنبات وإتلافه، كما يتغذى على الفواكه والخضروات، والتفاح والخوخ والحمضيات والذرة والفاصوليا وفول الصويا، وهناك بعض الأنواع المفترسة منه والتي تتغذى على الحشرات الأخرى واليرقات الصغيرة.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>أين يعيش البق النتن؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">الموطن الأصلي للبق النتن قارة آسيا مثل اليابان وكوريا والصين وتايوان، وقد وصلت للولايات المتحدة في منتصف التسعينات، وسببت الكثير من المشاكل للمزارعين وأصحاب المنازل، وهناك عدة أنواع منها مثل حشرة البق النتن البنية، وحشرة البق النتن الأخضر، وحشرة البق النتن الخضراء الجنوبية، وهي تتغذى على أنواع متنوعة من المحاصيل والنباتات، وتنجذب للمنازل خلال الشتاء والخريف للحصول على مكان دافئ، أما في الصيف والربيع فهي تفضل العيش بعيداً عن المنازل.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>علامات الإصابة بالبق النتن</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">رؤية أعداد كبيرة من الحشرة هو الدليل على وجودها في منزلك، حيث المعروف عن البق النتن غزوه الجماعي في الخريف، ويمكن رؤية الحشرة في:</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align: justify;\">الجدران المشمسة: حيث يعيش البق النتن بالقرب من الشمس للحصول على الدفء.</li>\r\n	<li style=\"text-align: justify;\">المحاصيل الزراعية : حيث يكتشف المزارعون البق النتن من خلال الأضرار التي سببها للمحصول والنباتات.</li>\r\n</ul>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>أضرار البق النتن</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يتغذى البق النتن على المحاصيل النباتية ويقوم بإتلافها ويجعلها غير صالحة للبيع أو الأكل كمنتجات طازجة ولا يمكن حتى تصنيعها، ولذلك يٌعد البق النتن من الحشرات الزراعية الخطيرة، كما أن هناك بعض الأنواع التي تقوم بلدغ البشر وتكون اللدغة مؤلمة وتٌورم المنطقة المحيطة بها، وقد يعاني البعض من أعراض الحساسية بما في ذلك التورم الشديد وصعوبة التنفس ولذلك يجب استشارة طبيب متخصص، وكما ذكرنا من قبل يقوم البق يفرز رائحة كريهة عن الشعور بالتهديد سواء في المنازل أو الحدائق مما يجعل رائحة المكان الذي توجد فيه مٌنفّرة.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>كيفية التخلص من البق النتن؟</strong></h2>\r\n\r\n<ul>\r\n	<li dir=\"rtl\">\r\n	<p dir=\"rtl\" style=\"text-align:justify\">غلق الأبواب والنوافذ: حيث يمتلك البق النتن قدرة خارقة على التسلل إلى المنازل من خلال الشاشات الممزقة وشقوق الأبواب بسبب جسمها المسطح، وهو يدخل المنزل في الأجواء الباردة لذلك يجب غلق النوافذ والأبواب، وفحص أو إصلاح الشاشات الموجودة فوق فتحات السقف، وفتحات النوافذ والأبواب وتركيب مساحات الأبواب.</p>\r\n	</li>\r\n	<li dir=\"rtl\">\r\n	<p dir=\"rtl\" style=\"text-align:justify\">التخفيف من الإضاءة المنزلية بالليل: حيث ينجذب البق النتن نحو الضوء وتتجمع على الجدران الخارجية المشمسة، وفي الليل ينجذب نحو الأضواء الخارجية والحجرات المنيرة، ويدخل المنزل من خلال الشقوق والفجوات.</p>\r\n	</li>\r\n	<li dir=\"rtl\">\r\n	<p dir=\"rtl\" style=\"text-align:justify\">المبيدات الكيميائية: تطبيق المنتجات الكيميائية حول نقاط دخول البق النتن بعد فحص المنزل والعثور على أماكن الدخول للمنزل والأماكن التي تعيش بها.</p>\r\n	</li>\r\n	<li dir=\"rtl\">\r\n	<p dir=\"rtl\" style=\"text-align:justify\">التعاقد مع شركة مكافحة الحشرات بجدة :لوضع برنامج فريد لمنزلك للتخلص من البق النتن، ومنعها من الدخول للمنزل مرة أخرى.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\"><a href=\"https://www.terminix.com/pest-control/stink-bugs/\">https://www.terminix.com/pest-control/stink-bugs/</a></p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\"><a href=\"https://www.terminix.com/pest-control/stink-bugs/behavior/\">https://www.terminix.com/pest-control/stink-bugs/behavior/</a></p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\"><a href=\"https://www.terminix.com/pest-control/stink-bugs/facts/are-stink-bugs-poisonous/\">https://www.terminix.com/pest-control/stink-bugs/facts/are-stink-bugs-poisonous/</a></p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\"><a href=\"https://www.orkin.com/other/stink-bugs\">https://www.orkin.com/other/stink-bugs</a></p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\"><a href=\"https://cumberlink.com/news/smelly-invaders-bug-local-residents/article_b23c4d6c-b89d-5f8f-af58-33640e1af487.html\">https://cumberlink.com/news/smelly-invaders-bug-local-residents/article_b23c4d6c-b89d-5f8f-af58-33640e1af487.html</a></p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\"><a href=\"https://bugguide.net/node/view/182\">https://bugguide.net/node/view/182</a></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<br />\r\n&nbsp;</p>', '<p dir=\"ltr\" style=\"text-align:justify\">stink bugs or Pentatomidae, have their name because, they emit unpleasant smell, to protect themselves from the predators when they are in danger, they are considered an agricultural pest, they invade houses at winter to search for a safety and warm shelter&nbsp;stink bugs live in groups, they have brown or green color, these colors enable them to hide inside the leaves or soil so no one can distinguish them easily.</p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">stink bugs become active from May to October, and some species of stink bugs are predators, and the other eat plants, and prefer agricultural areas, gardens and parks, some species of stink bugs are useful and the other may cause troubles to home owners.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Stink Bugs identification</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">stink bugs length ranges from 12 to 17 mm, their color vary from green to brown to black, stink bug has a sucking mouth and antennae with brown stripes, it has a a shield-like structure, also has wings, nymphs have a yellow or red color red eyes, then they turn into yellow, then light white, they emit a bad smell which is similar to coriander. it produces it from abdomen to defend itself from birds and lizards&nbsp;</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Stink bugs life cycle&nbsp;</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">The female lays eggs on plants leaves in rows at spring, people may find the eggs along trees and plants, females lay 20 to 30 eggs on the plant, eggs hatch after 4-5 days, nymphs start to eat, until they become adult by autumn, nymphs go through several stages until reaching the adult stink bugs.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Stink Bugs food</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Adult stink bugs and nymphs eat plants, agricultural crops, plant leaves, they can destroy and penetrating plant tissues, let pathogens enter and destroy the plant, fruits, vegetables, apples, peaches, citrus fruits, corn, beans and soybeans, there are some predatory species that feed on other insects.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Where stink bugs live?</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Stink bugs main habitat is Asia such as Japan, Korea, China and Taiwan, it reached the United States in the mid-nineties, cause agricultural problems to farmers and homeowners, there are several species of stink bugs such as: stinky brown bugs, stinky green bugs, and southern stinky green bugs, they eat crops and plants, enter homes during winter and autumn, they prefer to live away from houses in summer and spring.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>Signs of stink bugs infestation</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Noticing a large numbers of stink bugs is an indication of infestation in your home, they invade houses in the fall, they can be seen in:&nbsp;&nbsp;</p>\r\n\r\n<ul dir=\"ltr\">\r\n	<li style=\"text-align: justify;\">stink bugs live near the sun to get warm.</li>\r\n	<li style=\"text-align: justify;\">stink bugs&nbsp; are found in agricultural crops where farmers discover them after causing</li>\r\n	<li style=\"text-align: justify;\">Damage to the plants.</li>\r\n</ul>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>stink bugs damage</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\">Stinky bugs eat plant and crops, they are able to destroy them cannot even be manufactured or eaten, that&rsquo;s why stink bugs are dangerous insects, there are some species that sting humans, they cause a painful and swelling skin, some may suffer allergies symptoms, including severe swelling and difficulty in breathing, so you have to consult a doctor, they emit unpleasant smell in homes or gardens, which makes the smell repulsive.</p>\r\n\r\n<h2 dir=\"ltr\" style=\"text-align:justify\"><strong>How to get rid of stink bugs?</strong></h2>\r\n\r\n<ul dir=\"ltr\">\r\n	<li style=\"text-align: justify;\">Close doors and windows where stink bugs can enter from torn screens and cracks of doors due to their flat body, they enter the house in cold climates so you have to open windows and doors, repair screens, windows, door openings.</li>\r\n	<li style=\"text-align: justify;\">Reduce home lighting at night because stink bugs attracted to light and on sunny walls, external lights and room.</li>\r\n	<li style=\"text-align: justify;\">Apply chemical pesticides around the stink bugs entry points after examining the home and finding the entry places.</li>\r\n	<li style=\"text-align: justify;\">Contact Jeddah pest control company to develop a suitable program to your home to get rid of stink bugs and prevent them from entering it again</li>\r\n</ul>', 25, 1, 0, '2020-05-08 07:59:39', '2020-05-12 13:23:24', 'البق-النتن', 'Stink-bugs', 'حشرة البق النتن : معلومات وحقائق وكيفية التخلص من البق النتن', NULL, 'البق النتن', 'Stink bugs', 'Stink bugs | Facts & Identification, Control & Prevention', 'stink bugs or Pentatomidae, have their name because, they emit unpleasant smell, to protect themselves from the predators when they are in danger, they are considered an agricultural pest, they invade houses at winter to search for a safety and warm shelter stink bugs live in groups, they have brown or green color, these colors enable them to hide inside the leaves or soil so no one can distinguish them easily.'),
(29, 'الوزغ', 'Gecko', 'uploads/pest_libraries/15890079429148d6db614059e7402331fedec03300image.png', 'الوزغ أو البرص أو أبو بٌريص( الاسم العلمي Gekkonidae من الكائنات الزاحفة ذات الدم البارد والسريعة التأقلم والتكيّف في الأماكن التي تقوم بغزوها، وهي بمثابة كابوس مٌخيف إذا اكتشفتها في منزلك، ويعيش الوزغ في كل مكان حول العالم، كما أنه من الكائنات البارعة في التخفي، ولذلك لا تراها بسرعة، وهي تقوم بالتسلل في المساء بحثا عن الحشرات الأخرى لتتغذى عليها، وهو يعيش في الشقوق والفجوات بالقرب من الجدران والحوائط، ولا يظهر على الأغلب إلا في المساء.', 'الوزغ', '<p dir=\"rtl\" style=\"text-align:justify\">الوزغ أو البرص أو أبو بٌريص( الاسم العلمي Gekkonidae من الكائنات الزاحفة ذات الدم البارد والسريعة التأقلم والتكيّف في الأماكن التي تقوم بغزوها، وهي بمثابة كابوس مٌخيف إذا اكتشفتها في منزلك، ويعيش الوزغ في كل مكان حول العالم، كما أنه من الكائنات البارعة في التخفي، ولذلك لا تراها بسرعة، وهي تقوم بالتسلل في المساء بحثا عن الحشرات الأخرى لتتغذى عليها، وهو يعيش في الشقوق والفجوات بالقرب من الجدران والحوائط، ولا يظهر على الأغلب إلا في المساء&nbsp;وقد لا تٌجدي بعض المبيدات في التخلص من الوزغ ولذلك ربما يكون عليك الإستعانة بشركة مكافحة حشرات متخصصة لكي تساعدك على القضاء عليه في المنزل،&nbsp; ويفيد فهم شكل الوزع ودورة حياته في التعرف على عاداته وسلوكه للقضاء عليه، ولذلك إذا كنت تعاني من مشكلة الوزغ في المنزل فربما يكون عليك قراءة هذا المقال.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>شكل الوزغ</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تشتهر الذكور بأنها أكبر من الإناث، ويتراوح طول الوزع من 7 ل 15 سم، ويوجد على الجسم حراشيف سميكة تمتد بطول منطقة الظهر والذيل، ويتراوح لون الوزغ من الرمادي إلى البني الفاتح إلى البيج وخطوط خضراء أو بيضاء أو شفاف، وتتميز بعض الأنواع بلمعة على الظهر.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>دورة حياة الوزغ</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تقوم ذكور الوزغ بمغازلة الأنثى من خلال التلامس بشكل متكرر من منطقة الأنف، وقد يقوم الذكر بعض الأنثى ويمسكها من عنقها، وبعد ثلاثة إلى أربعة أسابيع من نجاح عملية التكاثر، تقوم الإناث بوضع البيض والذي يكون مٌغطى بقشرة صلبة مٌثبتة جزئيًا على سطح صلب، ويحدث التكاثر على مدار العام في البيئات ذات درجات الحرارة المرتفعة ويكون موسميًا في الظروف الأكثر برودة، وتقوم للإناث بتخزين الحيوانات المنوية حتى عام، ويكون البيض مستدير وقوي عكس بيض الزواحف، ولذلك يقاوم الرطوبة ويمكنه من البقاء على قيد الحياة لفترة طويلة، ويصل الوزغ لمرحلة النضج الجنسي بعد ستة أشهر إلى عام ويعيش الوزغ البالغ لفترة قد تصل حتى 5 سنوات.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\">ماذا يأكل الوزغ؟</h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">الوزغ من الكائنات الشهيرة بقدرتها على الصيد الليلي، كما أنها انتهازية حيث تفترس عدد كبير من الحشرات والعناكب، كما أنه يتناول الكائنات الأصغر من الوزغ وبقايا الجلود والمواد الغذائية التي تحتوي على السكر والرحيق.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>مخاطر الوزغ&nbsp;</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">&nbsp;يٌشكل الوزغ تهديدًا قوياً في المنازل، علاوة على شكله الذي يخيف الكثير من أصحاب المنازل، وهو من الكائنات التي تتكيف بسرعة فائقة مع البيئة التي يتواجد بها، كما أنه يتنافس مع أقرانه على المنطقة التي يوحد بها، ويقوم بالتغذية على الحشرات التي توجد على الجدران وبالقرب من الإضاءة، وهو كائن عدواني يقوم بمهاجمة أي وزغ آخر ويقوم بإبعاده عن مصادر الغذاء الأخرى، ويقوم الوزغ في بعض الأحيان بقطع ذيله أو تناوله في حالة الظروف الغذائية الصعبة، كما أن عدوانيته ​​وقدرته على التعايش فترة طويلة في المنزل يجعله من الأنواع الخطيرة،&nbsp;</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>كيفية التخلص من الوزغ في المنزل؟&nbsp;</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">توجد الكثير من المبيدات الحشرية الناجحة في التخلص من الوزغ في المنزل، إلا أن المبيدات الحشرية تساعد في تلويث مصادر الغذاء مما يجبر الوزغ على ترك المنزل، وفي كثير من الأحيان لا تكون المبيدات كافية للتخلص من الوزغ ولذلك يمكنك تطبيق ما يلي للتخلص من هذه الكائنات المقززة:&nbsp;</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align: justify;\">منع الوزغ من دخول المنزل من البداية&nbsp;لأن الوقاية أفضل من العلاج، عليك تطبيق هذه المقولة على الوزغ أيضاً، حيث يجب عليك منعه من دخول منزلك بدلا من بذل الوقت في التخلص منه وإخراجه من المنزل، لاسيما إذا كان منزلك يحتوي على كمية كبيرة من الطعام أو درجات الحرارة المناسبة للوزغ للاستقرار والتكاثر، ولذلك يجب عليك أن تقوم بفحص منزلك وإغلاق جميع نقاط الدخول، بما في ذلك إغلاق الأبواب والنوافذ، والشقوق والفتحات المحيطة بمناطق السباكة أو الأسلاك الكهربائية وكذلك أي فتحات تجدها في منزلك أو في الأساس، ويمكن أن تقوم بسد هذه الفتحات بواسطة سدادات السيليكون السدادات الرغوية، ويجب التأكد بعد إتمام المهمة من إحكام الغلق بشكل تام لعدم دخول أي حشرات للمنزل.</li>\r\n	<li style=\"text-align: justify;\">طرد الوزغ من المنزل&nbsp;يمكنك جلب صندوق أو حاوية وتقوم بوضعها مباشرة فوق الوزغ، ثم تخرجه خارج المنزل وتقوم بتحريره والتخلص منه، ولكن إذا كنت تخاف من الوزغ أو هذه الكائنات فبالطبع لن تناسبك هذه الطريقة.&nbsp;</li>\r\n	<li style=\"text-align: justify;\">صيد الوزغ والتخلص منه&nbsp;يمكنك استخدام ألواح الغراء والورق المقوى اللاصق ثم تقوم بوضعها على مقربة من الأضواء وعلى النوافذ وأي أماكن أخرى يمر بها الوزغ أو تراهم به، ولكن يجب أن تعرف أن هذه الطريقة لن تقتل الوزغ حيث أنه سيلتص بلوح الغراء ويجب وقتها أن تقوم بالتخلص الفوري من هذا اللوح.</li>\r\n	<li style=\"text-align: justify;\">يمكنك أن تقوم بالتعاقد مع شركة مكافحة الحشرات بجدة في حالة إصابة منزلك بالوزغ وخوفك من التخلص منه بنفسك، حيث يمكن للشركة أن تقوم بفحص منزلك وتخلصك من مشكلة الوزغ في المنزل خاصة إذا قام بالتعشيش في منزلك والتكاثر.</li>\r\n</ul>', '<p style=\"text-align:justify\">Gecko which has a scientific name is Gekkonidae is a cold-blooded and very adaptive creature in which they invade, when house owners discover it in their houses, they become scared because of its creepy appearance, gecko can live everywhere around the world, people may not notice its presence in early stages, as it is a nocturnal creature, it sneaks in the evening to search for food, like small insects to feed on, it lives in cracks, holes and walls, it appears in the evening.</p>\r\n\r\n<p style=\"text-align:justify\">Many pesticides may not help you get rid of gecko, so you may have to contact experts in pest control company to help to get rid of it at home, gecko identification, understanding life cycle, habits, feeding behavior may help you to eliminate it.</p>\r\n\r\n<h2 style=\"text-align:justify\"><strong>Gecko identification</strong></h2>\r\n\r\n<p style=\"text-align:justify\">Males have bigger size than females, their length are varying from 7 to 15 cm, they have a thick scale which extend along tail and all around the body, gecko color is gray or light brown, beige, green with white or transparent stripes, some species have shinny bodies.</p>\r\n\r\n<h2 style=\"text-align:justify\"><strong>&nbsp;Gecko life cycle</strong></h2>\r\n\r\n<p style=\"text-align:justify\">Male starts to flirt with the female by touching female nose frequently, male may hold the neck of female too, female put eggs after three to four weeks, which are covered with a solid shell, female fix it to a solid surface, reproduction may happen all t the year in high temperature, it becomes seasonal in cold weather, females store sperms for a year, eggs are round and strong, it can resists moisture and able to survive for a long time, gecko reach sexual maturity soon and it may live up to 5 years.</p>\r\n\r\n<h2 style=\"text-align:justify\"><strong>What gecko eat?</strong></h2>\r\n\r\n<p style=\"text-align:justify\">Gecko is an opportunistic night hunter, it eats insects and spiders, It also eats smaller worms, exfoliated skin and food containing sugar and nectar.</p>\r\n\r\n<h2 style=\"text-align:justify\"><strong>Is gecko infestation a serious problem?</strong></h2>\r\n\r\n<p style=\"text-align:justify\">&nbsp;Gecko is a serious threat at homes, it scares homeowners, it can adapt very quickly with the environment, as we mentioned before it eats insects walking on the walls and near lights, gecko attacks any other gecko, it sometimes cut its tail or eat in hard food conditions, it is aggressive can live for a long time at home.</p>\r\n\r\n<h2 style=\"text-align:justify\"><strong>How to get rid of gecko at home?</strong></h2>\r\n\r\n<p style=\"text-align:justify\">You can use insecticides to eliminate gecko at home, but pesticides may contaminate food sources and make them leave the house, so pesticides are not enough to get rid of gecko, so you can use the steps to get rid of gecko:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\">Prevent gecko from entering the home &nbsp;prevention is better cure ever, you must prevent gecko from entering your home especially if your home contains a large amount of food or appropriate temperatures for gecko to live and multiply, so you have to check your house to seal any possible entry points, including doors, windows, cracks and openings surrounding the plumbing areas or electrical wires, block openings with silicone and foam plugs.</li>\r\n	<li style=\"text-align: justify;\">eliminate gecko from your house&nbsp;bring a container and put it directly on gecko, then take it out of the house and dispose it, if you are afraid of gecko so don&rsquo;t try this method.</li>\r\n	<li style=\"text-align: justify;\">Catch and dispose gecko&nbsp;use glue boards and sticky paperboard, put them close to gecko area, such as windows and any other places, when gecko stick to the glue board, you must immediately get rid of it outside the house.</li>\r\n	<li style=\"text-align: justify;\">Contact Jeddah Pest Control CompanyذCall Jeddah pest control company if you are afraid of getting rid gecko, our company can examine your home and eliminate the problem.</li>\r\n</ul>', 12, 1, 0, '2020-05-08 23:05:42', '2020-05-12 13:23:24', 'الوزغ', 'Gecko', 'الوزغ : معلومات وحقائق وكيفية التخلص من البرص', NULL, 'الوزغ', 'Gecko', 'Gecko | Facts & Identification, Control & Prevention', 'Gecko which has a scientific name is Gekkonidae is a cold-blooded and very adaptive creature in which they invade, when house owners discover it in their houses, they become scared because of its creepy appearance, gecko can live everywhere around the world, people may not notice its presence in early stages, as it is a nocturnal creature, it sneaks in the evening to search for food, like small insects to feed on, it lives in cracks, holes and walls, it appears in the evening.'),
(30, 'أم أربعة وأربعين', 'Centipedes', 'uploads/pest_libraries/1589009017957b3b37d8065af92729532eebb14d7cimage.png', 'أم أربعة وأربعين أو الحريشة أو كما يٌطلق على اسمها العلمي Chilopoda، حشرة طويلة ذات جسم مسطح وهي تشبه الدودة، كما أنها مٌزودة بالكثير من الزوائد الجلدية أو الأرجل، وهي تنتمي إلى فئة المفصليات، وهي من الحشرات مٌخيفة الشكل، وكل قطعة مقسمة من جسمها تحتوي على زوج من الأرجل وبعض الأنواع من أم أربعة وأربعين يكون لها عيون مركبة تحتوي على ما يصل إلى 200 وحدة بصرية، أما البعض الآخر لا يكون لديه عيون، وتشترك جميعها في أن ضعف البصر وتتبع الفريسة من خلال اللمس والرائحة، ويرى البعض أنها من الحشرات المفيدة للمنزل، لأنها تأكل الكثير من الحشرات وتخلص أصحاب المنازل من مشاكل الحشرات الأخرى.', 'أم أربعة وأربعين', '<p dir=\"rtl\" style=\"text-align:justify\">أم أربعة وأربعين أو الحريشة أو كما يٌطلق على اسمها العلمي Chilopoda، حشرة طويلة ذات جسم مسطح وهي تشبه الدودة، كما أنها مٌزودة بالكثير من الزوائد الجلدية أو الأرجل، وهي تنتمي إلى فئة المفصليات، وهي من الحشرات مٌخيفة الشكل، وكل قطعة مقسمة من جسمها تحتوي على زوج من الأرجل وبعض الأنواع من أم أربعة وأربعين يكون لها عيون مركبة تحتوي على ما يصل إلى 200 وحدة بصرية، أما البعض الآخر لا يكون لديه عيون، وتشترك جميعها في أن ضعف البصر وتتبع الفريسة من خلال اللمس والرائحة، ويرى البعض أنها من الحشرات المفيدة للمنزل، لأنها تأكل الكثير من الحشرات وتخلص أصحاب المنازل من مشاكل الحشرات الأخرى.</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">وأم أربعة وأربعين من الحشرات التي تحب الماء وغالباً ما تجدها في المصارف أو حولها أو بالقرب من مصدر للمياه، كما أنها من الحشرات السريعة للغاية ولا تعطلها أرجلها الكثيرة عن الحركة ويمكن أن تسير حتى 16 بوصة في الثانية، ويمكنها الركض على أرجلها الخلفية وتستند عليها لكي تستريح أثناء السير، ولأم أربعة وأربعين قدرة فائقة على استعادة أرجلها ففي حال فقدانها أو بترها لأي سبب، ستكون قادرة على إعادة نمو ساق جديدة.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>شكل أم أربعة وأربعين</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يتراوح حجم الحريشة أو أم أربعة وأربعين من 4 إلى 152 مم، وهي تتمتع بعدة ألوان مثل البني والبرتقالي المحمر، ويوجد لها زوج طويل من قرون الاستشعار، ولها جسم كبير مزود بما يُشبه المخالب، وتحتوي على السم، وهي من الحشرات التي تتغذى على الحشرات ولذلك تستخدم مخالبها لشل فرائسها من الديدان والعناكب والفقاريات الصغيرة، وتملك الحشرة من 15 إلى 177 زوجًا من الأرجل، ولذلك قد يكون اسم أم أربعة وأربعين مضلل للبعض.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>دورة حياة حشرة أم أربعة وأربعين؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تختفي أم أربعة وأربعين في المناطق الرطبة والمظلمة خلال فصل الشتاء، وهي تضع البيض في التربة الرطبة خلال الصيف أو الربيع، وعندما تصل الحشرة للبلوغ تنمو لها مجموعة كاملة من الأرجل والملحقات، وهي تعيش لأكثر من عام وبعضها يصل إلى ست سنوات.&nbsp;</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>كيف تدخل أم أربعة وأربعين للمنزل؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تبحث أم أربعة وأربعين عن مكان دافئ للاختباء والبحث عن الفريسة، وهي تدخل المنزل من تحت الأبواب و الشقوق والفتحات ويساعدها جسمها الصغير والمسطح على المرور بالسلام من المساحات الضيقة، وتٌفضل أم أربعة وأربعين المنازل المظلمة والتي يوجد بها مشاكل للرطوبة، ولهذا يمكن العثور عليها في الطوابق السفلية حيث تزداد مشاكل الرطوبة وكذلك الحجرات السفلية والحمامات والاحواض والخزائن وتنشط أم أربعة وأربعين في الليل للبحث عن الطعام ونادرًا ما يراها البشر خلال النهار، وهي حشرات غير خطيرة، على الرغم من أنها تملك غدد تضخ السم لحقنه وقتل الفريسة من الحشرات لكن الفك ليس قوي بما يكفي لثقب جلد الإنسان بسهولة، وقد تقرص للدفاع عن نفسها وقد تسبب الألم الموضعي والتورم.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>ما هو غذاء حشرة أم أربعة وأربعين؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تتغذى أم أربعة وأربعين على الحشرات المنزلية الصغيرة مثل العناكب والذباب والصراصير والأسماك الفضية، وهي من الحشرات ضعيفة البصر ولا ترى بشكل جيد لكنها تعثر على الفريسة بواسطة قرون الاستشعار الحساسة، ويمكن أن تعيش أم أربعة وأربعين لأكثر من عام، وبعض الأنواع منها قد تعيش من 5-6 سنوات.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>علامات الإصابة بأم أربعة وأربعين في المنزل؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">لا تترك أم أربعة وأربعين أي علامات مباشرة تدل على وجودها، ولكن رؤيتها بشكل مباشر أثناء حركتها النشطة في الليل دليل على وجودها بالطبع</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>ما مدى خطورة حشرة أربعة وأربعين؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">أم أربعة وأربعين نادراً ما تقرص البشر، ولكن سمها يسبب حساسية والتهاب خفيف، ويراها البعض من الحشرات المفيدة أو غير الضارة لأنها تخلصهم من حشرات المنزل، إلا أنها تصبح مصدر إزعاج بسبب شكلها و البقع التي تتركها في الأماكن التي تزحف فوقها.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>كيف يمكن التخلص من أم أربعة وأربعين؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">عند اكتشاف مشكلة أم أربعة وأربعين في المنزل عليك بالاتصال بشركة مكافحة الحشرات بجدة، لفحص منزلك، وبمجرد الانتهاء من الفحص، سيقوم المتخصصون بالشركة بعمل خطة متكاملة للتحكم في الحشرة سواء بطرق كيميائية أو غير كيميائية، وقد تشمل طرق التخلص من أم أربعة وأربعين ما يلي:</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align: justify;\">معالجة مشكلة الرطوبة عن طريق إصلاح تسرب المياه أو استخدام مزيل الرطوبة.</li>\r\n	<li style=\"text-align: justify;\">التقليل من الفوضى التي توفر المكان الآمن للحشرة وذلك من خلال التوقف عن تخزين العناصر المٌخزنة سواء على الحائط أو على الأرض.</li>\r\n	<li style=\"text-align: justify;\">التخلص من الحشرات والعناكب التي توفر مصدر غذاء لأم أربعة وأربعين.</li>\r\n	<li style=\"text-align: justify;\">سد الثقوب والشقوق والفجوات التي تسمح لأم أربعة وأربعين وغيرها من الحشرات بالدخول إلى المنزل.</li>\r\n	<li style=\"text-align: justify;\">استخدام المكنسة الكهربائية للتخلص من الحشرات.</li>\r\n	<li style=\"text-align: justify;\">رش المبيدات الحشرية الخاصة بالحشرات الزاحفة في الشقوق والمناطق التي تعيش بها سواء في الشقوق أو فجوات الأساس أو الأرضيات أو حول المنزل.</li>\r\n</ul>', '<p style=\"text-align:justify\">Centipede (the scientific name is Chilopod) is belonging to a category of arthropods, it has a flat body, many skin appendages that contains large numbers of legs, it is a scary insect, every skin appendage is divided to a pair of legs&nbsp;Some species of Centipede have complex eyes and contain up to 200 optical units, other species do not have eyes, so it follows its prey through touching and smelling, house owner may see Centipede, as useful insect, because it can eat house insects, therefore house owners can decrease number of insects&#39; problems in their homes.</p>\r\n\r\n<p style=\"text-align:justify\">Centipede prefer water and you may often find it in drains or around water source, it is a very fast insect and their too many legs do not disrupt its movement, it can crawl up to 16 inches per second, it also may rely on back legs, it also has an excellent ability to regain its legs when it lose it.</p>\r\n\r\n<h2 style=\"text-align:justify\"><strong>Centipede Identification</strong></h2>\r\n\r\n<p style=\"text-align:justify\">Centipede has 15 to 177 pairs of legs, its size ranges from 4 to 152 mm, it has a brown or reddish orange color, it also has a long pair of sensing pods, Centipede has claws that contain poison which immobilize their prey such as worms, spiders or small vertebrates.</p>\r\n\r\n<h2 style=\"text-align:justify\"><strong>Centipede life cycle</strong></h2>\r\n\r\n<p style=\"text-align:justify\">Centipede lives in wet and dark areas during the winter, females lay eggs in moist places during summer or spring, when Centipede reaches puberty it grows a full set of legs and appendages, Centipede can from one to six years.</p>\r\n\r\n<h2 style=\"text-align:justify\"><strong>How Centipede enters your house?</strong></h2>\r\n\r\n<p style=\"text-align:justify\">Centipede searches for a warm place to hide, it may enter your house through doors, cracks, openings, Centipede flat body helps it to pass through small areas, Centipede prefers dark places which has moisture problems, so you may find it on the lower floors and bathrooms&nbsp;Centipede becomes active at night and starts to search for food, Centipede is not a dangerous insect, it also has glands that pump poison to inject and kill its prey, the Centipede jaw is not strong enough to sting human skin, but it may sting human to defend itself causing skin swelling and painful area</p>\r\n\r\n<h2 style=\"text-align:justify\"><strong>Centipede feeding habits</strong></h2>\r\n\r\n<p style=\"text-align:justify\">Centipede feeds on small insects like spiders, flies, cockroaches and silver fish, Centipede is not able to see its prey, but it can find it with the help of a sensitive sensing pods, it can live for more than a year.</p>\r\n\r\n<h2 style=\"text-align:justify\"><strong>Centipede infestation in your home</strong></h2>\r\n\r\n<p style=\"text-align:justify\">Centipede does not leave signs of infestation, but seeing Centipede directly at night is a big evidence of infestation.</p>\r\n\r\n<h2 style=\"text-align:justify\"><strong>How serious is Centipede?</strong></h2>\r\n\r\n<p style=\"text-align:justify\">Centipede rarely sting humans, but when this happens it may cause mild inflammation, people may consider Centipede a harmless insect because it feeds on insects, it may become a source of nuisance because of its shape, it also leaves spots in crawling areas</p>\r\n\r\n<h2 style=\"text-align:justify\"><strong>How to get rid of the Centipede?</strong></h2>\r\n\r\n<p style=\"text-align:justify\">When you discover Centipede at home, you should contact Pest Control Company in Jeddah, to examine your house, experts will examine your house accurately, and put a suitable plan to control Centipede, using chemical or non-chemical treatment, you may also need to follow these steps:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\">Solve moisture problem by fixing leakage and using a dehumidifier.</li>\r\n	<li style=\"text-align: justify;\">Reduce insects in your house to eliminate Centipede and get rid of insects and spiders.</li>\r\n	<li style=\"text-align: justify;\">Seal holes, cracks and gaps that allow Centipede to enter your home.</li>\r\n	<li style=\"text-align: justify;\">Use a vacuum cleaner to eliminate a Centipede.</li>\r\n	<li style=\"text-align: justify;\">use insecticides in which Centipede live, like cracks, gaps, floors, or around your home.</li>\r\n</ul>', 26, 1, 0, '2020-05-08 23:23:37', '2020-05-08 23:23:37', 'أم-أربعة-وأربعين', 'Centipedes', 'أم أربعة وأربعين : معلومات وحقائق وكيفية التخلص من الحريشة', NULL, 'أم أربعة وأربعين', 'Centipedes', 'Centipedes | Facts & Identification, Control & Prevention', 'Centipede (the scientific name is Chilopod) is belonging to a category of arthropods, it has a flat body, many skin appendages that contains large numbers of legs, it is a scary insect, every skin appendage is divided to a pair of legs Some species of Centipede have complex eyes and contain up to 200 optical units, other species do not have eyes, so it follows its prey through touching and smelling, house owner may see Centipede, as useful insect, because it can eat house insects, therefore house owners can decrease number of insects\' problems in their homes.');
INSERT INTO `pest_libraries` (`id`, `name_ar`, `name_en`, `image`, `meta_description_ar`, `image_alt`, `description_ar`, `description_en`, `sort`, `is_active`, `home_order`, `created_at`, `updated_at`, `slug_ar`, `slug_en`, `meta_title_ar`, `sub_pest`, `keywords_ar`, `keywords_en`, `meta_title_en`, `meta_description_en`) VALUES
(31, 'النمل الناري', 'Fire Ant', 'uploads/pest_libraries/1589312508النمل-الناري.jpg', 'النمل الناري الاحمر ( Solenopsis ) هو نوع من النمل الذي يعيش في أمريكا الجنوبية والبرازيل وأوروغواي والأرجنتين، ويعتقد البعض أنه وصل إلى الولايات المتحدة الأمريكية من خلال السفن التي تحمل البضائع الزراعية من بلد موبوءة بهذا النوع، وهو يعيش في أستراليا وفي الكثير من المناطق الأخرى ويصل طول النمل الناري الأحمر من 1.6 إلى 5 ملم، وهو ذو لون أحمر ويتحول إلى اللون الأسود ناحية البطن ويكون هذا اللون من العلامات المميزة للتعرف على هذا النوع.', 'النمل الناري', '<p dir=\"rtl\" style=\"text-align:justify\">النمل الناري الاحمر ( Solenopsis ) هو نوع من النمل الذي يعيش في أمريكا الجنوبية والبرازيل وأوروغواي والأرجنتين، ويعتقد البعض أنه وصل إلى الولايات المتحدة الأمريكية من خلال السفن التي تحمل البضائع الزراعية من بلد موبوءة بهذا النوع، وهو يعيش في أستراليا وفي الكثير من المناطق الأخرى ويصل طول النمل الناري الأحمر من 1.6 إلى 5 ملم، وهو ذو لون أحمر ويتحول إلى اللون الأسود ناحية البطن ويكون هذا اللون من العلامات المميزة للتعرف على هذا النوع.</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">ويتمكن النمل الناري من بناء الأكوام الخاصة به في أى نوع من التربة ولكن في أغلب الحالات تفضل المناطق المشمسة المفتوحة كالمراعي والحدائق والحقول الزراعية وغيرها. تبني هذه الحشرة أكوام خاصة بها يتراوح طولها من 18 إلى 24 بوصة على الأقل وهذا في حالة توافر الظروف المناخية المناسبة لها، غالبًا ما تتواجد تلك الأكوام في جذوع الأشجار أو في المناطق المحيطة بها، كما أن المستعمرات الخاصة بالنمل الناري تتواجد تحت المباني أو بداخلها.</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\"><img alt=\"شكل النمل الناري\" src=\"https://orkidapest.com/orkidaFront/uploads/contents/1589312496-النمل-الناري.jpg\" style=\"height:455px; width:800px\" /></p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>&nbsp;أين يعيش النمل الناري الاحمر ؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">يقوم النمل الناري الاحمر ( Red Ants ) ببناء تربة مرئية في كثير من الأحيان في المناطق المشمسة، وتكون تلالهم كبيرة بما يصل إلى 18 سم وعرض 61 سم، وهي تكون على شكل قبة، ويكون النمل الناري أقل نشاطًا خلال ساعات النهار الأكثر سخونة، كما أنه يتجنب الظلام والظل، ومن المرجح أنه يظهر في الحقول المفتوحة والمروج الخضراء أكثر من الغابات، وحينما تزداد أعداد المستعمرة فقد يضطر النمل وقتها إلى إرسال ملكات إضافية لبدء بناء مستعمرات جديدة في مكان قريب.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>ما هو الطعام الذي يتناوله النمل الناري الأحمر ؟</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تعتبر حشرة النمل الناري الأحمر من الحشرات العدوانية والتي تتغذى في المقام الأول على الحشرات الحية والحيوانات الميتة، أما عندما تخترق المنازل فهي تنجذب إلى الدهون والمواد الغذائية الحلوة.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>ما هي الأضرار الناجمة عن النمل الناري الأحمر ؟</strong></h2>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align: justify;\">يقوم النمل الناري الأحمر ببناء مستعمرات تحت الأرض والتي قد تصل إلى عمق قدم أو أكثر في الطول وكذلك العرض ويقوم النمل بمهاجمة من يقوم بتهديد&nbsp; مستعمراته.</li>\r\n	<li style=\"text-align: justify;\">تكون لدغات النمل الناري الأحمر مؤلمة وتترك وراءها علامات حمراء وتتحول إلى بثور بيضاء فيما بعد.</li>\r\n	<li style=\"text-align: justify;\">لهذا النوع من النمل سلوك عدواني فهو يندفع بالمئات فجأة لمهاجمة من يدخل المستعمرة ويترك على جلده العديد من اللدغات الحمراء الظاهرة.</li>\r\n	<li style=\"text-align: justify;\">تكون لدغة هذا النوع سامة، وقد تتسبب في ردود فعل وأعراض طبية خطيرًة للغاية خاصةً إذا كان الشخص شديد الحساسية للسم.</li>\r\n	<li style=\"text-align: justify;\">يعد هذا النوع مشكلة خطيرة في المناطق الحضرية والبيئات ترفيهية حيث أن وجوده يمنع الناس من ممارسة الأنشطة في الهواء الطلق.</li>\r\n	<li style=\"text-align: justify;\">يبني هذ النوع أعشاش بالقرب من المباني ولذلك يكون له تأثير خطير على مصادر التغذية والمياه في المناطق التي يوجد بها.</li>\r\n	<li style=\"text-align: justify;\">يقلل من الإمدادات الغذائية المحلية في المنطقة التي يبني بها مستعمراته بسبب استهلاكه لها.</li>\r\n</ul>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>أين يمكنني العثور على النمل الأحمر الناري؟</strong></h2>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align: justify;\">يجب عليك البحث جيدا في الأماكن التالية إذا كنت تشك في وجود النمل بمنزلك</li>\r\n	<li style=\"text-align: justify;\">المناطق المفتوحة لا يحب النمل الناري الظل ولذلك يجب أن تبحث عنه في المناطق العشبية المفتوحة في الشمس.</li>\r\n	<li style=\"text-align: justify;\">ممرات المشاة يقوم النمل برسم مسارات خاصة به في المناطق المفتوحة والدافئة وخاصة في الأماكن التي يعبر بها المشاة وفي الممرات.</li>\r\n	<li style=\"text-align: justify;\">الحدائق المزروعة ينجذب هذا النوع إلى التربة حديثة الزراعة والتي تكون بعيدة عن الظل.</li>\r\n	<li style=\"text-align: justify;\">الصنابير غالباً ما يتم العثور على النمل الناري بالقرب من مصادر المياه مثل الصنابير والمرشات وبرك الماء.</li>\r\n	<li style=\"text-align: justify;\">الأنابيب يبني النمل الناري شبكة تحت الأرض من الأنفاق ويمكن أن تتداخل تلك التجاويف والفراغات في الأرض لتكون بجوار مرافق المياه أو الغاز أو الهاتف.</li>\r\n</ul>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>دورة حياة النمل الناري الأحمر</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تتكون مستعمرة النمل الناري الأحمر من ثلاثة أنواع من النمل وهم: العمال والنمل المجنح والملكات.</p>\r\n\r\n<ul>\r\n	<li dir=\"rtl\">\r\n	<h3 dir=\"rtl\" style=\"text-align:justify\"><strong>العمال</strong></h3>\r\n	</li>\r\n</ul>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تكون السمة المميزة للعاملين هي أحجامهم المتعددة حيث يتراوح حجم العمال من 2-6 مم في نفس المستعمرة ويكون جميع النمل العامل من الإناث العقيمة وهم يعيشون لمدة 2-3 أشهر، أما&nbsp; العمال الأكبر في الحجم فقد يعيشون لمدة تصل إلى 6 أشهر ويتم تحديد وظائف العمال حسب عمرهم وحجمهم واحتياجات المستعمرة، حيث أن هناك عمال لإطعام النمل الصغير والملكة، ومع تقدم عمر النمل العامل يتغير دوره ليتحول إلى حراسة وصيانة المستعمرة والدفاع عنها، وهناك فئة من العمال التي تترك المستعمرة للبحث عن الطعام بالخارج.</p>\r\n\r\n<ul>\r\n	<li dir=\"rtl\">\r\n	<h3 dir=\"rtl\" style=\"text-align:justify\"><strong>النمل المجنح</strong></h3>\r\n	</li>\r\n</ul>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تنتج المستعمرات الناضجة عددًا من الذكور والإناث والذين يٌطلق عليهم النمل المجنح، ويتم الاعتناء بهم من قبل النمل العامل حتى يغادروا المستعمرة لبدء رحلات التزاوج الخاصة بهم، وعادة ما تحدث هذه الرحلات خلال أشهر الصيف الدافئة عندما تكون الظروف البيئية مناسبة لهم من حيث درجة الحرارة والرطوبة ويمكن لهذا النوع من النمل أن يتكاثر على مدار العام.</p>\r\n\r\n<ul>\r\n	<li dir=\"rtl\">\r\n	<h3 dir=\"rtl\" style=\"text-align:justify\"><strong>الملكة</strong></h3>\r\n	</li>\r\n</ul>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تتزاوج الملكة مع الذكر أثناء رحلة التكاثر وبعد التزاوج يموت الذكر ويترك الأنثى التي تبدأ في تكوين مستعمرة جديدة إذا عثرت على موقع مناسب، ويمكن أن يطير النمل الإناث إلى 2- 5 كيلو متر لتبحث عن الموقع الأكثر ملاءمة للمستعمرة، وتكون الملكات الجديدة ضعيفة بشكل خاص في هذه المرحلة ولا يبقى سوى نسبة ضئيلة للغاية منها على قيد الحياة ربما أقل من 1٪ وهذه النسبة هي التي تبدأ المستعمرة الجديدة.</p>\r\n\r\n<ul>\r\n	<li dir=\"rtl\">\r\n	<h3 dir=\"rtl\" style=\"text-align:justify\"><strong>مستعمرات النمل جديدة</strong></h3>\r\n	</li>\r\n</ul>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">تحفر الملكة الجديدة غرفة تحت الأرض ثم تضع فيها بيضها الأول وفي غضون ال 24 ساعة الأولى تضع من 10-20 بيضة والتي تفقس بعد 6-10 أيام وهي تتطور لتكون يرقات حديثة الفقس وهي تمر ب 4 مراحل على مدى 12-14 يوما وذلك قبل أن تصبح شرانق، وبعد من 9 إلى 16 يومًا يظهر النمل المتطور حديثًا والذي يستغرق قرابة الشهر ليتحول إلى النمل الناضج جنسيا.</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">وخلال هذه الفترة تواصل الملكة وضع البيض والذي يعتني به العمال، ويمكن للملكة وضع ما يصل إلى 800 بيضة في اليوم، حيث تتراكم أعداد النمل بسرعة لتتكون مستعمرة عمرها أكثر من 100 ألف عامل، ويمكن للملكة العيش لأكثر من 7 سنوات وهي تكون قادرة على إنتاج أكثر من 2 مليون بيضة في حياتها، كما تنتج الملكات مواد كيميائية تسمى الفيرومونات والتي تؤثر على سلوك العمال في المستعمرة.</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">قد يشكل النمل الناري الأحمر خطرًا كبيرا على البشر والحيوانات الأليفة في بعض الأحيان، ومن الجيد التشاور مع المحترفين فيما يتعلق بطرق السيطرة عليهم، ولذلك يجب عليك التواصل مع <a href=\"https://orkidapest.com/ar/services/%D8%B4%D8%B1%D9%83%D8%A9-%D9%85%D9%83%D8%A7%D9%81%D8%AD%D8%A9-%D8%AD%D8%B4%D8%B1%D8%A7%D8%AA-%D8%A8%D8%AC%D8%AF%D8%A9\">شركة مكافحة الحشرات بجدة </a>لفحص منزلك وتفتيش ممتلكاتك، حيث سيصل لك مجموعة من الأخصائيين ذوي الخبرة العالية في مكافحة الآفات لإجراء تفتيش شامل في الداخل والخارج، ثم يقومون بوضع&nbsp; خطة علاج مخصصة لتلبية احتياجاتك على أفضل وجه.</p>\r\n\r\n<h2 dir=\"rtl\" style=\"text-align:justify\"><strong>نصائح هامة لحماية منزلك من النمل الناري الأحمر&nbsp;</strong></h2>\r\n\r\n<p dir=\"rtl\" style=\"text-align:justify\">هناك العديد من الطرق لعلاج إصابة النمل الناري الأحمر ويوجد العديد من الخيارات المتاحة لمالكي المنازل حيث تعتبر معلاجة إصابة هذا النوع بشكل فردي من المهام الصعبة، إلا أن هناك بعض النصائح التي سوف نتحدث عنها&nbsp; لتطبيقها إذا أخذت قرار مكافحة هذه الآفة بنفسك ومنها:</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align: justify;\">استخدام المبيدات الحشرية السائلة والقيام بسكب كميات كبيرة منها على تلال النمل والمستعمرات.</li>\r\n	<li style=\"text-align: justify;\">استخدام الماء الساخن وصبه فوق أعشاش النمل إلا أن هذه الطريقة قد لا تصل إلى الملكة التي تعيش في عمق العش وبالتالي لن يتم القضاء على المستعمرة.</li>\r\n	<li style=\"text-align: justify;\">استخدام الغبار السطحي حيث يتم تطبيق مبيد حشري للأتربة أو الحبيبات فوق قمة المستعمرة.</li>\r\n	<li style=\"text-align: justify;\">حقن المستعمرة بكمية كبيرة من المبيدات للتوغل إلى العمق والقضاء على الملكة ويتم تطبيق تلك الطريقة عدة مرات وهي تكون أكثر تكلفة ولكنها أكثر فعالية.</li>\r\n	<li style=\"text-align: justify;\">استخدام الطعوم للقضاء على النمل حيث يتم رش كمية صغيرة من الطعوم حول التلة حيث يتغذى النمل عليها ويقوم بأخذها للمستعمرة لبقية النمل وهذه الطريقة تعتبر بطيئة ولكنها أكثر فاعلية من الكثير من الطرق.</li>\r\n	<li style=\"text-align: justify;\">التحكم الميكانيكي في المستعمرة حيث توجد بعض الأجهزة الميكانيكية والكهربائية في السوق للتحكم في النمل.</li>\r\n	<li style=\"text-align: justify;\">استخدام العلاجات المنزلية مثل السوائل قابلة للاشتعال فوق المستعمرة إلا أنها طريقة غير مستحسنة لأنها خطيرة للغاية ليس فقط بالنسبة للبشر ولكن أيضًا للبيئة.</li>\r\n</ul>', '<p dir=\"ltr\">Red fire ants (Solenopsis) is a type of ants that lives in South America, Brazil, Uruguay and Argentina, and some believe that it arrived in the United States of America through ships carrying agricultural goods from a country endemic to this species, and it lives in Australia and in many other regions and arrives The length of the fiery red ants from 1.6 to 5 mm, which is red and turns black towards the abdomen, and this color is a distinctive sign to identify this type.</p>\r\n\r\n<p dir=\"ltr\">Fire ants can build their own piles on any type of soil, but in most cases they prefer sunny open areas such as pastures, gardens, agricultural fields, and others. This insect builds its own piles ranging in length from at least 18 to 24 inches. This is when suitable climatic conditions are available for them. These piles are often found in tree trunks or in the surrounding areas. Also, colonies of fiery ants are found under or inside buildings.</p>\r\n\r\n<p dir=\"ltr\">&nbsp;Where do red fiery ants live?</p>\r\n\r\n<p dir=\"ltr\">Red fire ants often build visible soils in sunny areas, their hills are large up to 18 cm wide and 61 cm wide, and they are dome shaped, and fiery ants are less active during the hottest daylight hours, and He avoids darkness and shade, and is more likely to appear in open fields and green meadows than forests, and as colony numbers increase, ants may then have to send additional queens to start building new colonies nearby.</p>\r\n\r\n<p dir=\"ltr\">What food does red fiery ants eat?</p>\r\n\r\n<p dir=\"ltr\">Red fiery ants are aggressive insects that feed primarily on live insects and dead animals, but when they penetrate into homes they are attracted to fats and sweet food.</p>\r\n\r\n<p dir=\"ltr\">What are the damages caused by red fiery ants?</p>\r\n\r\n<ul>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">Red fiery ants build underground colonies that may reach a foot depth or more in length as well as width and ants attack those who threaten their colonies.</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">The bites of red fiery ants are painful, leave behind red marks and turn into white blisters later.</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">This type of ants has an aggressive behavior, as it suddenly rushes in hundreds to attack those who enter the colony and leave many visible red bites on their skin.</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">The bite of this type is toxic, and it may cause very serious medical reactions and symptoms, especially if the person is hypersensitive to poison.</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">This type is a serious problem in urban areas and recreational environments as its presence prevents people from outdoor activities.</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">This type builds nests near the buildings and therefore has a serious impact on the sources of nutrition and water in the areas where it is located.</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">It reduces the local food supply in the area where he builds his colonies because of his consumption of them.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p dir=\"ltr\">Where can I find fiery red ants?</p>\r\n\r\n<ul>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">You should search well in the following places if you suspect the presence of ants in your home</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">Open areas fiery ants do not like shade, so you should look for them in grassy areas open in the sun.</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">Pedestrian paths Ants draw their own paths in open and warm areas, especially in places where pedestrians cross and in the corridors.</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">Cultivated gardens This type is attracted to newly planted soils that are far from shade.</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">Taps Fire ants are often found near water sources such as taps, sprinklers and ponds.</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">Pipes Fire ants build an underground network of tunnels, and these cavities and voids can interfere in the ground to be near water, gas, or telephone facilities.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p dir=\"ltr\">Life cycle of red fiery ants</p>\r\n\r\n<p dir=\"ltr\">The fiery red ant colony consists of three types of ants: workers, winged ants, and queens.</p>\r\n\r\n<p dir=\"ltr\">Laborers</p>\r\n\r\n<p dir=\"ltr\">The distinguishing feature of the workers is their multiple sizes, where the size of the workers ranges from 2-6 mm in the same colony and all the working ants are sterile females and they live for 2-3 months, while the larger workers may live for up to 6 months and the jobs of the workers are determined Depending on their age, size and the needs of the colony, as there are workers to feed the small ants and the queen, and as the age of the working ants progresses, its role changes to shift to guarding, maintaining, and defending the colony, and there is a group of workers who leave the colony to search for food abroad.</p>\r\n\r\n<p dir=\"ltr\">Winged ants</p>\r\n\r\n<p dir=\"ltr\">Mature colonies produce a number of male and female who are called winged ants, and are taken care of by working ants until they leave the colony to start their mating trips, and these trips usually occur during the warm summer months when the environmental conditions are appropriate for them in terms of temperature and humidity and can This type of ants breeds throughout the year.</p>\r\n\r\n<p dir=\"ltr\">the Queen</p>\r\n\r\n<p dir=\"ltr\">The queen intermarries with the male during the breeding journey and after mating the male dies and leaves the female who begins to form a new colony if she finds a suitable location, and female ants can fly up to 2- 5 km to search for the most suitable location for the colony, and the new queens are especially weak in This stage is left with only a very small percentage of them surviving perhaps less than 1% and this percentage is what starts the new colony.</p>\r\n\r\n<p dir=\"ltr\">New ant colonies</p>\r\n\r\n<p dir=\"ltr\">The new queen digs an underground room and then lays her first eggs and within the first 24 hours she lays 10-20 eggs which hatch after 6-10 days as she develops to be newly hatched larvae as she passes 4 stages over a period of 12-14 days before that They become cocoons, and after 9 to 16 days, newly developed ants, which take about a month, develop into sexually mature ants.</p>\r\n\r\n<p dir=\"ltr\">During this period the queen continues to lay eggs that the workers take care of, and the queen can lay up to 800 eggs a day, as the numbers of ants quickly accumulate to form a colony that is more than 100,000 workers, and the queen can live for more than 7 years and is able to produce more Of the 2 million eggs in her life, the queens also produce chemicals called pheromones that affect the behavior of workers in the colony.</p>\r\n\r\n<p dir=\"ltr\">Red fiery ants can pose a great danger to humans and pets sometimes, and it is good to consult with professionals regarding ways to control them, so you should contact with a Jeddah pest control company to check your house and search your property, as a group of highly experienced specialists will come to you pest Control to conduct a comprehensive inspection at home and abroad, then they develop a plan customized to meet your needs treatment best.</p>\r\n\r\n<p dir=\"ltr\">Important Tips to protect your home from a fiery red ants</p>\r\n\r\n<p dir=\"ltr\">There are many ways to treat a red fiery ant injury and there are many options available for homeowners. Treating this type of injury individually is considered a difficult task, but there are some tips that we will talk about to apply if you take the decision to combat this pest yourself, including:</p>\r\n\r\n<ul>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">Using liquid pesticides and pouring large quantities of them on ant hills and colonies.</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">Use hot water and poured over ant nests, but this method may not reach the queen living in the depth of the nest and thus will not be eliminated colony.</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">Surface dust is used where dust or granular insecticide is applied over the top of the colony.</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">Injecting the colony with a large amount of pesticides to penetrate deep and eliminate the queen. This method is applied several times and it is more expensive but more effective.</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">The use of baits to eliminate the ants where a small amount of baits are sprayed around the hill where the ants feed on them and take them to the colony for the rest of the ants. This method is considered slow but more effective than many methods.</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">Mechanical control of the colony where there are some mechanical and electrical devices on the market for controlling ants.</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\">Using home remedies like liquids is flammable on top of the colony but it is not a recommended method because it is very dangerous not only for humans but also for the environment.</p>\r\n	</li>\r\n</ul>\r\n<br />\r\n&nbsp;', 27, 1, 0, '2020-05-12 11:41:48', '2020-05-12 11:41:48', 'النمل-الناري', 'fire-ant', 'النمل الناري - معلومات وحقائق عن النمل الاحمر', 10, 'النمل الناري', 'fire ants', 'Scientific facts about the fire ants and the fastest ways to get rid of it permanently', 'Red fire ants (Solenopsis) is a type of ants that lives in South America, Brazil, Uruguay and Argentina, and some believe that it arrived in the United States of America through ships carrying agricultural goods from a country endemic to this species, and it lives in Australia and in many other regions and arrives The length of the fiery red ants from 1.6 to 5 mm, which is red and turns black towards the abdomen, and this color is a distinctive sign to identify this type.');

